css:
@-moz-document domain("docs.rainmeter.net") {

/*=========*/
/*Variables*/
/*=========*/

:root{
	--bg-color-1: #121212; /*     Main BG     */
	--bg-color-2: #161616; /*     Nav  BG     */
	--bg-color-3: #000000; /*  Code Block BG  */
	--fg-color-1: #FFFFFF; /* Main Text Color */
	--fg-color-2: #007BFF; /*Main Accent Color*/
	--fg-color-3: #80C0DD; /*   Link  Color   */
	--fg-color-4: #5A5DAB; /*  Header  Color  */

	--cd-color-1: #CECECE; /* Code Main Color */
	--cd-color-2: #6496C8; /*Code Prefix Color*/
	--cd-color-3: #E1E100; /*  Section Color  */
	--cd-color-4: #FF8800; /*  Number  Color  */
	--cd-color-5: #FFFFFF; /*  String  Color  */
}

/*=========*/
/*Main Body*/
/*=========*/

body {
    font-size: 17px;
    color: var(--fg-color-1);
    background-color: var(--bg-color-1);
}

a{
	color: var(--fg-color-3);
}

dt{
	background-color: var(--fg-color-4);
}

.docs-content dt code{
	color: var(--fg-color-1) !important;
	background-color: #00000000 !important;
}

.docs-content dd{
	border: 1px solid #555555;
	border-top: 0;
	box-shadow: 0 0px 0px 0;
}

.docs-nav {
    background-color: var(--bg-color-2);
}

.docs-nav h4, .docs-nav h4 a {
    color: var(--fg-color2);
}

.docs-nav-block a {
    color: var(--fg-color-1);
}

.h3, h3 {
    color: var(--fg-color-2);
}

.h2, h2 {
    color: var(--fg-color-2);
}

.h1, h1 {
    color: var(--fg-color-2);
}

/*====*/
/*Code*/
/*====*/

code{
	color: var(--cd-color-2) !important;
	background-color: var(--bg-color-3) !important;
}

/*==========*/
/*Code Block*/
/*==========*/

pre { /*Code Block*/
    background-color: var(--bg-color-3);
    color: var(--cd-color-1);
	border: 0px;
}

pre .title, pre .id, pre .coffeescript .params, pre .scss .preprocessor, pre .section{ /*Header*/
	color: var(--cd-color-3) !important;
}

pre .attr, pre .keyword, pre .css .rule .keyword, pre .winutils, pre .javascript .title, pre .nginx .title, pre .subst, pre .request, pre .status { /*Options*/
    color: var(--cd-color-2) !important;
}

pre .number{ /*Number*/
	color: var(--cd-color-4);
}

pre .string{ /*String*/
	color: var(--cd-color-5)
}

.highlight a{ /*Select All Box*/
	background-color: var(--bg-color-1);
	border-color: var(--bg-color-3);
	top: 2px !important;
	right: 2px !important;
}

/*==========*/
/*Search Bar*/
/*==========*/

#search{
	color: var(--fg-color-1);
	background-color: var(--bg-color-3);
}

/*===============*/
/*Main Page Cards*/
/*===============*/

.card {
    background-color: var(--bg-color-3);
}
.docs-index .card {
    color: var(--fg-color-1);
}

/*=========*/
/*Changelog*/
/*=========*/

.release-notes b{
	color: var(--fg-color-1);
}

.release-notes li{
	color: var(--cd-color-1);
}

/*================*/
/*Setting Up Cards*/
/*================*/

.exampleprev{
	color: var(--fg-color-1);
	background-color: var(--fg-color-2);
	border-color: var(--bg-color-3);
}


}
