css:
@-moz-document url-prefix("https://www.findagrave.com") {
.top-msg {
    background: #E9A93E;
}
div.fg-prad:nth-child(3) .bio-more {
    display: none !important;
}
.section-first {
    background: #2e255c;
}
section#content {
    background: #2e255c;
}
#container_footer {
    background: #2e255c;
}
footer {
    background: #2e255c;
}
.bio-min {
    color: white;
}
div {
    color: white;
}
div.fg-prad:nth-child(3) {
    color: #2e255c;
}
.well {
    background: #7c797e;
    color: #2e255c;
}

/* unvisited link */
a:link {
    color: white;
}

/* visited link */
a:visited {
    color: white;
}
div.overview-panel:nth-child(2) > h4:nth-child(1) {
    color: white;
}
div.col-sm-5:nth-child(2) {
    color: white;
}
.col-md-8 > h1:nth-child(1) {
    color: #E9A93E;
}

/* memorial page */
.memorial-search.section-form {
    background: #2e255c;
}
.grave-search-bg h1 {
    color: white;
}
.memorial-search .grave-search .collapse-link {
    color: white;
}
.fg-links .item .icon {
    color: white;
}
.fg-links .item:hover .icon,
.fg-links .item:focus .icon,
.fg-links .item:hover strong,
.fg-links .item:focus strong {
    color: #f1f606;
}
.fg-links .item strong {
    color: white;
}
#find-FAG-member .header-prad,
#memorial-index .header-prad {
    background: #2e255c;
}
.nav-memorial {
    background: #6f1e38;
}

/* cemetary page */
.cemetery-search.section-form {
    background: #2e255c;
}
.cemetery-search-form {
    background: #2e255c;
}
.brand-color.fg-links .item strong {
    color: white;
}
.brand-color.fg-links .item:hover .icon,
.brand-color.fg-links .item:focus .icon,
.brand-color.fg-links .item:hover strong,
.brand-color.fg-links .item:focus strong {
    color: #f1f606
}

/*topbar*/
#cemetery-index .header-prad,
#cemeteries-detail .header-prad,
#cemetery-browse .header-prad {
    background: #2e255c;
}

/*bot of map*/
div {
    color: white;
}
.page-top-heading .page-title {
    color: white;
}
.text-muted {
    color: white;
}

/*famous memorial page */
.section-famous-landing {
    background: #2e255c;
}
.memorial-item .memorial-item---grave p.text-muted {
    color: white;
}
.list-group-item {
    background: #2e255c;
}
#user-profile .header-prad {
    background: #651425
}
a.memorial-item:hover {
    background: #2e255c;
    color: gray;
}
div {
    color: black;
}
.nav-pills > li > a {
    color: #d4af37;
}
div {
    color: #efe8e8;
}
header {
    background: #6f1e38;
}
#memorial-summary.bg-dk .memorial-overview {
    background: #2e255c;
}
#memorial-summary.bg-dk .header-prad {
    background: #2e255c;
}
#famous-memorial-landing .header-prad {
    background: #2e255c;
}
#contribute-index .header-prad {
    background: #2e255c;
}
p {
    background: #2e255c;
    color: gray;
}
.fg-prad {
    background: #2e255c;
}
.label-relation {
    color: white;
}
.memorial-item .memorial-item---grave b {
    color: white;
}
.memorial-item .memorial-item---grave .name-grave {
    color: #E9A93E;
}
.btn-link {
    color: white;
}
.memorial-item .memorial-item---cemet p {
    color: white;
}
.badge {
    background: #5C60A3;
}
.btn-dark {
    background: #5C60A3
}
.text-danger {
    color: #6F1E38
}
.honor-widget {
    background: #2E255C;
    border: #2E255C;
}
.honor-widget .honor-info i {
    border: #2E255C;
    color: white;
}
.honor-widget .honor-info b {
    color: white;
}
.honor-widget .honor-caption strong {
    color: white;
}
.honor-widget .honor-info {
    color: white;
}
.transfer-details {
    color: white;
}
.panel-body {
    background: #2E255C;
}
.btn-dark:hover,
.btn-dark:focus {
    background: #E9A93E
}
.news-panel h3 {
    color: white;
}
.breadcrumb > .active {
    color: white;
}
.board-item .img-credits {
    color: white;
}
}

@-moz-document url-prefix("https://www.findagrave.com") {
.tab-content .container {
    width: 750px;
}
.tab-content .col-md-5.col-lg-6.border-line {
    display: none;
}
.tab-content .col-sm-6,
.tab-content .col-lg-6 {
    width: 100%;
}
.tab-content .member-family {
    padding-left: 7px;
}
.tab-content .member-item {
    margin-top: 7px !important;
}
.tab-content .media-body p {
    display: inline-block;
}
.tab-content .media-body a {
    display: inline-block;
}
#memorial-summary.bg-dk .memorial-overview,
#memorial-summary.bg-dk .header-prad {
    background-color: #2e255c;
}
.section-bio-cover {
    margin-left: auto;
    margin-right: auto;
    max-width: 1100px;
}
.section-bio-cover .col-md-offset-1 {
    margin-left: 0;
}
.section-bio-cover .col-sm-3 {
    width: 30%;
}
.section-bio-cover .bio-dp img {
    object-position: 100% 100%;
}
.section-bio-cover .bio-dp,
.section-bio-cover .bio-dp img {
    height: auto !important;
}
.header.condensed ~ .header-prad {
    margin-top: 0 !important;
}
.header-prad > div,
.footer-prad > div {
    height: 0 !important;
}
.overview-panel .overview-panel--body .bio-min:first-child::first-line {
    font-size: inherit;
}
.overview-panel .overview-panel--body .bio-min.dropcap:first-child::first-letter {
    float: none;
    font-size: inherit;
    line-height: inherit;
    padding-top: 0;
    padding-right: 0;
    padding-left: 0;
}
#bio-overflow-text {
    display: inline !important;
}
.bio-more {
    display: none !important;
}
}
