/* NONE */

.ghx-backlog .ghx-issues .ghx-extra-field[data-tooltip="L2B: None"],
.ghx-backlog .ghx-issues .ghx-extra-field[data-tooltip="Traffic: None"],
.ghx-backlog .ghx-issues .ghx-extra-field[data-tooltip="Customer Happiness: None"]
{
    display: none;
}

.ghx-backlog .ghx-issues .ghx-extra-field-seperator {
    display: none!important;    
}

.ghx-backlog .ghx-issues .ghx-extra-field {
    margin-left: 12px;
}


/* flex up */

.ghx-backlog .ghx-issues .ghx-issue-content {
    display: flex;    
}

.ghx-backlog .ghx-issues .ghx-row {
    flex-grow: 1; 
    order: 1;
    position: relative!important;
}
    
.ghx-backlog .ghx-issues .ghx-end {
    position: relative;
    order: 3;
    flex-grow: 0;
    flex-shrink: 1;
}

.ghx-backlog .ghx-issues .ghx-plan-extra-fields {
	flex: 0 1 auto;
    line-height: 25px;
    order: 2;
}

.ghx-backlog .ghx-issues .ghx-end.ghx-extra-field-estimate {
    width: 64px;
    margin-top: -3px;
    padding-top: 0;
}

.ghx-backlog .ghx-issues .ghx-key, .ghx-backlog .ghx-issues .ghx-summary {
    line-height: 25px;
}

.ghx-backlog .ghx-issue-compact .ghx-flags,
.ghx-backlog .ghx-issue-compact .ghx-flags img,
.ghx-backlog .ghx-issue-compact .ghx-type,
.ghx-backlog .ghx-issue-compact .ghx-type img {
    line-height: 0px;
    margin-top: -3px;
}

.ghx-backlog .ghx-issues .ghx-priority img {
    margin-top: 0;
}

.ghx-backlog .ghx-issues .ghx-row-version-epic-subtasks {
    margin-right: 5px!important;
    top: 1px!important;
}

.ghx-backlog .ghx-issues .ghx-row-version-epic-subtasks .ghx-estimate {
    top: -1px;
}

.ghx-backlog .ghx-issues .aui-label.ghx-label.ghx-label-single {
	line-height: 18px;
    margin-top: 0px;
    padding: 2px 8px 0;
}
}
