css:
@-moz-document domain("nextinpact.com") {
    body {
            font-family: Arial;
    }

    p {
            font-family: Arial;
    }

    .actu_chapeau {
            font-family: Arial;
            font-weight: bold;
    }

    h1, h2, h3, h4, h5, h6 {
            font-family: Arial;
    }

    #main_content_section .unit-article div>h1 {
            font-size: 32px;
    }

    #global_comment h2 {
            font-family: Arial;
    }

    .text_comment {
     font-family: Arial;
    }

    ul#menu li a, nav ul#responsive_menu li a {
            font-size: 12px;
    }
    .brief-item .brief-content p {
            font-family: Arial;
    }
    
    header {
        display: none;
    }
    
    #body {
        margin-top: 50px;
        width: auto;
    }
    
    #sideBarArticle, #sideBarIndex {
        display: none;
    }
    
    .main-content {
        width: auto;
        text-align: center;
    }
    
    #main_content_section {
        float: none;
        text-align: left;
    }
    
    #extra-services {
        display: none;
    }
    .msg_add_citation {
        left: -150px;
    }
    
    #header-brief-edition {
        display: none;
    }
    
    #child-briefs-container {
        display: none;
    }
    
    .brief-item {
        width: auto;
        margin-right: 2em;
        margin-left: 2em;
    }
    
    .image-brief-container {
        margin-top: 0px;
        width: 100%;
        height: auto;
        position: initial;
    }
    
    .image-brief-container .dedicated {
        max-width: 100%;
    }
    
    .image-brief-container .mask_grey {
        display: none;
    }
    
    .brief-item .brief-content {
        width: 100%;
        padding: 0px;
    }
    
    .brief-item .brief-foot {
        padding: 0;
    }
}
