css:
@-moz-document domain("edna.cz") {
:root {
    --black: #1a1a1a;
    --orange: #fa8e10;
    --semi-orange: #fabd76;
}
body,
#mother,
.webinfo .info {
    background: var(--black) 50% 0 no-repeat !important;
    background-attachment: fixed;
    color: white;
}
.stars::before{
    color: #b4b0aa;
}
.web .labels div {
    border-top-color: #333333;
    border-left-color: #333333;
    border-bottom-color: #333333;
    border-right-color: #333333;
}
.labels .label-gray {
    background-color: #393939;
}
div.content-fanweb-top-articles.articles div.episodeText,
.fanweb .content .episodes h3 a,
.col-side .box .translations .completed,
div.inner p a,
.schedule .episodes h3 a,
.schedule.news a,
.seriesbar > a,
.web .episodes h3 a,
a.btn-big:hover,
.ranks .stars::before,
.stars span:before,
#series-menu-favorite-wrapper #series-favorite-box .serbox-left-inside span,
.fanweb .content .labels a.colored,
.serieslist .series_list_new h3 a,
.fanweb .content .seriesmenu li a,
.fanweb .content a,
a.box_name {
    color: var(--orange) !important;
}
div.adsense,
div.clearfix,
.fanweb .content .seriesadvert,
div#mother > a{
    display: none;
}
.icon-eye-open {
    margin-left: 3px;
}
.fanweb .content,
.fanweb .content h1,
.seriesheader h1,
.fanweb .col-content .content-inner h2.main,
.fanweb .content h2.main,
.fanweb .list li .text-box h2,
.fanweb .list li .text-box h3,
.fanweb .list li .show-box a,
.fanweb .comments h3,
.fanweb form label,
h2.main a,
.col-side .box .translations .percents,
.comments .preview legend,
.col-side .box .head h2,
.fanweb .content .labels a.btn-hover,
.fanweb .content .labels .transparent,
.episodes-bg.articles .head,
div.content-fanweb-top-articles.articles div.episodes .episodeInside div.episodeRow div.episodeText a,
.webinfo .info .text-box,
.web .episodes td,
.list li a.series,
form label,
.ac_results[data-apriori="true"] ul li span,
.ac_results li .muted,
.ac_results li small,
.list li .text-box h3,
#comments textarea {
    color: white !important;
}
.fanweb .content h1 span,
.fanweb .content h2.main span {
    border-bottom-color: white;
}
.header .search input[type="text"],
.content form input[type="text"],
.content form textarea {
    background: var(--black);
    caret-color: white;
    color: white;
    outline-color: var(--orange);
    border: 3px solid var(--orange);
}
form input[type="text"][required],
form input[type="password"][required],
form input:active {
    outline-color: var(--orange);
    border: 3px solid var(--orange);
}
.fanweb .content .seriesmenu li a,
.fanweb .content a.seriesmenu-favorite,
.web .seriesmenu li a {
    color: white;
    text-shadow: 0px 1px 1px var(--black);
}
.seriesmenu li.active a,
.seriesmenu li a:hover {
    color: white;
    border-bottom: 3px solid white;
}
.seriesheader {
    color: white;
    height: 100px;
}
#series-menu-favorite-wrapper #series-favorite-box .serbox-left-inside,
.web label {
    color: white !important;
}
.subtitles-to-redirect-download-box .inner-redirect {
    border-color: white;
    color: white;
}
.subtitles-to-redirect-download-box .redirect-svg svg path {
    fill: white !important;
}
.web .col-content {
    background: var(--black);
    color: inherit;
}
.paginator a.btn,
.center a.btn,
#fanwebinfoBox > a,
a.btn-big {
    color: var(--black) !important;
}
.seriesheader-0,
.seriesheader-1,
.seriesheader-2 {
    height: 365px;
    text-indent: -3000px;
}
a.btn,
a.btn-hover:hover,
input[type='submit'],
.ranks a.btn-big,
a.btn-big {
    background: var(--orange);
    border: none;
}
.header .search {
    box-shadow: none;
}
.usermenu .img-box img {
    border: 1px solid var(--orange) !important;
    box-shadow: 0px 0px 2px 1px var(--orange) !important;
    -webkit-box-shadow: 0px 0px 2px 1px var(--orange) !important;
}
.header .mainmenu li a:hover,
.header .mainmenu li.active a {
    color: var(--orange);
    border-color: var(--orange);
}
a,
h3,
h1,
h3 > a,
.webinfo h1 a,
.webinfo a,
.labels a.btn-hover,
.labels a.colored,
.web .episodes a,
.current_series .cs_title a,
.seriesmenu li.active a,
div.content-fanweb-top-articles.articles div.head-box,
a.box_name {
    color: var(--orange);
}
.webinfo .info {
    color: inherit;
}
div.content-fanweb-top-articles.articles div.head-box,
.ac_results ul,
.webinfo .info .img-box,
.web .seriesmenu,
.row,
a.btn-big:hover,
.fancybox-inner {
    background: var(--black);
}
.web .episodes h3 a {
    border-bottom: none;
}
div.ranks,
.fanweb .content .col-content {
    background: inherit;
}
.footer,
.webinfo::before,
.fanweb .content .content-inner,
.fanweb .content .seriesmenu,
.fanweb .content .col-side,
.fanweb .content-fanweb-top-articles,
.web .footer {
    background: none;
}
div.content-fanweb-top-articles.articles div.head,
.col-side .box .translation .bar,
.ac_results li.ac_over,
.poll .answer .bar,
.list li a.series,
.btn-top,
a.series,
.fancybox-skin,
.subtitles-to-redirect-download-box {
    background: var(--orange);
}
.list-more:hover,
.web .list li:hover .img-box,
form input[type="text"] {
    border-left: 3px solid var(--orange);
}
.serieslist-menu-item.active::before {
    border-top: 10px solid var(--orange);
}
h2.main {
    color: inherit;
}
h2.main > span {
    border-bottom: 3px solid var(--orange);
}
.list li .img-box.img-box,
.list-more {
    border-left: 1px solid var(--orange);
}
.fanweb .content .seriesadvert {
    width: 970px;
    height: 280px;
    padding: 10px 15px;
    overflow: hidden;
}
.fanweb .content.has-adverts .content-inner,
.fanweb .content-fanweb-top-articles {
    background-position: 0 -300px;
}
.fanweb .content.has-adverts .col-side {
    background-position: 320px -300px;
}
.fanweb .content .seriesmenu li a,
.fanweb .content a.seriesmenu-favorite,
.fanweb .content .seriesmenu li.menuLinkAboutSerial a {
    color: var(--orange);
    text-shadow: 0px 1px 1px var(--black);
}
.seriesmenu li.active a,
.seriesmenu li a:hover,
.seriesmenu li.active.menuLinkAboutSerial a,
.seriesmenu li.menuLinkAboutSerial a:hover,
.header .mainmenu li a:hover {
    color: var(--orange);
    border-bottom: 3px solid var(--orange);
}
.content a.btn,
.content input[type="submit"],
.fanweb .content .fanwebinfo a.btn,
.content a.btn-hover:hover {
    color: white;
    background: var(--orange);
}
.content form input[type="text"],
.content form input[type="password"],
.content form textarea,
.content form select,
.content form input[type="text"][required],
.content form input[type="password"][required],
.content form textarea[required],
.comments .preview,
#fancybox-thumbs ul li.active,
#series-menu-favorite-wrapper #series-favorite-box,
.ac_input,
.ac_results,
.fanweb .header .news a,
.ac_results li.ac_over {
    border-color: var(--orange) !important;
}
.fanweb .episodes h3 a {
    border-bottom: 2px solid var(--orange);
}
.comments .comment,
.comments .nocomments {
    border-top-color: var(--orange);
}
hr {
    color: var(--orange);
    background-color: var(--orange);
}
em {
    color: var(--orange);
}
#series-menu-favorite-wrapper #series-favorite-box .serbox-right-inside {
    background: var(--orange) !important;
}
#series-menu-favorite-wrapper #series-favorite-box::after {
    border-top-color: var(--orange) !important;
}
.content a.btn,
.content input[type="submit"],
.fanweb .content .fanwebinfo a.btn,
.btn-top,
.content a.btn-hover:hover,
.fanweb .content .labels a.btn-hover:hover,
.ranks a.btn-big,
.header .search input[type="submit"],
select {
    color: var(--black);
}
#series-menu-favorite-wrapper #series-favorite-box .serbox-right-inside .svg_holder svg path {
    fill: var(--black) !important;
}
p.spoiler-alert {
    color: var(--orange);
    border-color: var(--orange);
}
.fanwebinfo {
    background-color: var(--black) !important;
}
#series-menu-favorite-wrapper #series-favorite-box {
    background: var(--black) !important;
}
.fanwebinfo h2,
#fanwebinfoBox .text-box h2,
#fanwebinfoBox .text-box p {
    color: white;
}
.header,
.fanweb .episodes-bg,
.col-side .box .head {
    background-color: var(--black);
    background-image: none;
}
div.center,
.fanwebinfo dt,
#fanwebinfoBox .text-box dl {
    color: white !important;
}
dd > a {
    color: var(--orange) !important;
}
.episodes-bg.articles .episodes .episodeInside .episodeRow .episodeText a,
.episodes .leftEpisode h2 {
    color: var(--semi-orange);
}
.episodes .leftEpisode h2 {
    color: var(--semi-orange);
    border-bottom: 2px solid var(--semi-orange);
}
div.episodes-bg.articles .episodes .episodeInside .episodeRow:after,
h1 span {
    border-bottom: 2px solid var(--semi-orange);
}
div.episodes-bg.articles div.episodes .episodeInside div.episodeRow div.episodeText a {
    border-bottom: 2px solid var(--black);
    color: var(--orange);
}
.fanweb .content .content-inner.intro {
    background-position: 0 -170px;
}
.fanweb .content.has-adverts .content-inner.intro {
    background-position: 0 -470px;
}
a.btn > i {
    color: var(--black);
}
table.center{
    background-color: inherit !important;
}
td, th{
    border-color: var(--orange);
}
select{
    background-color: var(--black);
    color: inherit !important;
}
/* Instagram iframe */
.instagram-media, .instagram-media-rendered {
	background: none !important;
	border: none !important;
}
.Embed, .Header, html.js{
    background-color: purple !important;
}
/* Twitter iframe */
.EmbeddedTweet,
.EmbeddedTweet--mediaForward .EmbeddedTweet-tweet{
    background-color: var(--black);
    color: inherit !important;
    border: none;
}
.MediaCard-borderOverlay{
    border:none;
}::-webkit-scrollbar
{
  max-width: 10px !important;
  max-height: 10px !important;
  background: #1d1d1d !important;
}

::-webkit-scrollbar-track, ::-webkit-scrollbar-corner
{
  background: var(--black) !important;
}

::-webkit-scrollbar-thumb
{
  background: rgba(175, 175, 175, .5) !important;
}

::-webkit-scrollbar-thumb:hover
{
  background: var(--orange) !important;
}
}
