css:
@-moz-document url-prefix("http://rneventteknik.se/stage") {
body
{
    color: #eee;
    background-color: #222;
}

#container
{
    background-color:#333;
}

h1, h1[style], h2, h3, h4, h4[style], h5, h6
{
    color: #eee !important;
	background-color: transparent !important;
}

.c-nav-buttons
{
    background-color: #333 !important;
    box-shadow:none !important;
}
.col-frontpage.col-bookings
{
    background: #444;
}
a, ul.navigation_regular li.nav_item_main > a, ul.navigation_mobile li.nav_item_main > a, ul.navigation_regular li a
{
    color: #7cf;
}
.subnav-bar, .subnav-bar-a
{
    background: #7cf;
}
.subnav-bar-a .a_line {
	color: black;
}
#cal
{
	display:none;
}
.grey
{
    color: #eee;
}
.task_sign
{
    color: #eee;
}
.color-icon-soft, .color-icon-dark
{
    color: #eee;
}
.ui-progressbar
{
    background-color: #555;
}
.table_list tr.tbl-mid1 td
{
	background-color: #444;
    box-shadow: none;
}
.splitrow, .cat_headline
{
    background-color: #333;
}
.splitrow h4
{
    color: #eee;
}
.c-nav-menu, .c-nav-menu__close
{
    background-color: #222;
}
.table_list tr td
{
    background-color: #222;
}
input[type="text"], input[type="password"], input[type="datetime-local"], input[type="date"], input[type="email"], select, textarea, input[type="text"].input_long, input[type="password"].input_long, input[type="number"].input_long, input[type="text"].input_medium
{
	background-color: #444;
    color: #eee !important;
    border: none;
    
}
button, input[type="button"], input[type="reset"], input[type="file"], input[type="submit"], .a_button
{
    background-color: #7cf !important;
}

.a_label {
	color: #eee;
}
.a_button {
	color: black;
}

ul.navigation_regular li a:hover {
	background-color: #333;
}
.head_list_box_h {
	color:#eee;
}
.subnav-bar-a .a_line:hover {
	color :black;
}
#logo {
	width: 250px;
}

.btn-icon-remove {
	filter: invert(1);
}

.btn-submit {
	background: #7cf;
    border: none;
}
.event-status-sent-to-admin {
	background-color: #7cf;
    color: black;
}
}
