css:
@-moz-document domain("twistapp.com") {

.topbar {
    /* background-color: #4674c2; */
    
    background-color: #3b3b3b;
}
div.workspace {
    background-color: #000000;
}
.left_col {    
    /* background-color: #4D394B; */ /* Slack left column color */
    background-color: #2d2d2d;
    border-right: 1px solid #333;
}
.left_col a.item p.name {
    color: #cecece;
}
.left_col a.item.active {
    background-color: #3f4e72 !important;
}
.left_col a.item:hover,
.center_col ul a.item:hover {
    background-color: #3f485c !important;
}
.center_col__wrapper .center_col {
    background-color: #333;
    border-right: 1px solid #333;
}
.center_col ul a.item section header h1 {
    color: #cecece;
}
.center_col ul a.item .border_wrapper {
    border-bottom: 1px solid #474747;
}
.center_col ul a.item.active {
    background-color: #454f65;
}
.center_col__wrapper .center_col__wrapper__body .center_col {
    background-color: #333;
}
.center_col__wrapper .center_col__wrapper__body .drag_to_upload,
.center_col__wrapper .center_col__wrapper__body .right_col {
    background-color: #333;
}
.empty_screen__container {
    background-color: #333;
    color: #fff;
}
.empty_screen--title {
    color: #FFF;
}
.empty_screen__illustration {
    display: none;
}
.empty_screen__content {
    background-image: url('https://lh3.ggpht.com/doFtR_H-dXJH0Mnm0zVchxrv0cWBy3O46_lWVYOnhY1M-cid2Tb7fX1FXPTyxx1BT24=s180');
    width: 200px;
    height: 200px;
    background-repeat: no-repeat;
    background-position: 50% 0px;
    padding: 200px;
}
h1 {
    color: #bdbdbd;
}
header.list_header section h1,
header.list_header section input {
    color: #bdbdbd;
}
.comments article.item section header p.user_name,
.search_results article.item section header p.user_name {
    color: #FFF;
}
.mkdown p,
.mkdown ol li,
.mkdown ul li {
    color: #bdbdbd;
}
.mkdown a,
.external-actions a {
    color: #7fa9ef;
}
.mkdown a:hover {
    color: #538ef0;
}
.mkdown pre {
    background-color: #484848;
}
.mkdown code {
    background-color: #484848;
}
.mkdown p code,
.mkdown pre {
    border: 1px solid #474747;
}
.mkdown code {
    color: #bdbdbd;
}
.under_header_button {
    background-color: #333;
}
.under_header_button div {
    background-color: #333;
    border-bottom: 1px solid #474747;
}
.under_header_button:hover {
    color: #7fa9ef;
}
header.list_header {
    border-bottom: 1px solid #474747;
}
.center_col__wrapper .drag_to_upload,
.center_col__wrapper .right_col {
    background-color: #333;
}
.empty_conversation,
.empty_invite_people_screen {
    color: #bdbdbd;
}
.comment_composer main {
    border: 1px solid #474747;
}
.comment_composer .recipients {
    border-bottom: 1px solid #474747;
}
.recipients .selector .input_label,
.recipients .selector .item {
    background-color: #333;
}
.recipients .selector input {
    background-color: #5a5a5a;
    color: #bdbdbd;
}
.reactions .reaction_item.active,
.reactions .reaction_item {
    background-color: #5a5a5a;
    border: 1px solid #474747;
}
.reactions .reaction_item.active .reaction_item__counter {
    color: #bdbdbd;
}
/* thread composer */

.thread_composer input.content_editable,
.thread_editor .composer_wrapper > .recipients_wrapper {
    border-bottom: 1px solid #474747;
}
.thread_composer__actions {
    border-top: 1px solid #474747;
}
.thread_composer__actions .channel_name p {
    color: #bdbdbd;
}
/*  Table (seen in the "Move Thread" dialog)  */

.table tbody tr {
    border-top: 1px solid #474747;
}
.table__wrapper {
    border: 1px solid #474747;
}
/* editors for comments, messages, search */

.comment_composer .comment_composer__content textarea.content_editable,
.message_composer .message_composer__content textarea.content_editable,
.search-filter-form__trigger,
input.content_editable,
textarea.content_editable,
.create_conversation .new_selector .selected,
.create_conversation .new_selector .selected input {
    background-color: #5a5a5a;
    color: #bdbdbd;
}
.upload_button a.display-text,
.mkdown_helper_button {
    color: #bdbdbd;
}
.mkdown_helper_button.active,
.mkdown_helper_button:hover {
    background: #454f65 url(https://twistapp.com/a/img/icons/mkdown_helper/mkdown_helper.svg) no-repeat 10px;
}
.upload_button a.display-text:hover {
    background-color: #454f65;
}
.mkdown_helper_compact {
    background-color: #5a5a5a;
}
.mkdown_helper_compact > p {
    color: #bdbdbd;
}
.mkdown_helper_expanded table th,
.mkdown_helper_expanded table td {
    border-bottom: 1px solid #474747;
}
.mkdown_helper_expanded table {
    color: #bdbdbd;
}
.mkdown_helper_expanded table code,
.mkdown_helper_expanded table pre {
    color: #333;
}
a {
    color: #7fa9ef;
}
/*    */

.reactist_modal_box__header {
    background-color: #5a5a5a;
    border-bottom: 1px solid #474747;
    color: #bdbdbd;
}
.reactist_modal_box__header p {
    color: #bdbdbd;
}
.reactist_modal_box__body {
    background-color: #333;
    color: #bdbdbd;
}
.reactist_modal_box__actions {
    border-top: 1px solid #474747;
    background-color: #333;
}
.tabs .tabs__header {
    border-bottom: 1px solid #474747;
}
.tabs .tabs__header a.active {
    color: #7fa9ef;
}
.menu_button .menu_button_trigger {
    border: 1px solid #474747;
}
.reactist.dropdown .body {
    background-color: #5a5a5a;
}
.menu_button .menu_button_items .menu_button_item:nth-child(2) {
    border-top: 1px solid #474747;
    border-bottom: 1px solid #474747;
}
div.entry input[type="password"],
div.entry input[type="text"] {
    background-color: #5a5a5a;
    color: #bdbdbd;
    border: 1px solid #474747;
}
.reactist.button.white {
    background-color: #5a5a5a;
    color: #7fa9ef;
    border: 1px solid #7fa9ef;
}
.notification_settings_container .notification_settings .settings_container .setting__header {
    color: #a5a5a5;
}
.user_settings .reactist_modal_box__body p {
    color: #bdbdbd;
}
/* Messages */

.messages .item .message_wrapper {
    border: 1px solid #474747;
}
.messages .item section header .user_name {
    color: #FFF;
}
.action_list {
    background-color: #5a5a5a;
}
.action_list a {
    color: #a5a5a5;
}
.reactist.dropdown hr {
    background-color: #474747;
}
.message_composer .message_composer__content {
    border: 1px solid #474747;
}
.messages .item.self section .message_wrapper {
    background-color: #474747;
}
.edit_text {
    background-color: #474747;
}
.create_conversation .new_selector .selected {
    border: 1px solid #474747;
}
.create_conversation .conversation.item,
.create_conversation .user.item {
    border-bottom: 1px solid #474747;
}
.create_conversation .conversation.item.active,
.create_conversation .user.item.active {
    background-color: #515151;
    /*  #454f65    */
}
.create_conversation .conversation.item .profession,
.create_conversation .user.item .profession,
.create_conversation .conversation.item .name,
.create_conversation .user.item .name {
    color: #bdbdbd;
}
/* Team */

.reactist.button.secondary,
.reactist.button.secondary:disabled {
    background-color: #bfbfbf !important;
    color: #5a5a5a;
}
.user_profile article > div p,
.user_profile .profile_completion > header > p {
    color: #bdbdbd;
}
.role_select .role-select-trigger {
    border: 1px solid #474747;
    color: #bdbdbd;
}
.role_select .role-select-options div:nth-child(2) {
    border-top: 1px solid #474747;
    border-bottom: 1px solid #474747;
}
.role_select .role-select-options div h1,
.role_select .role-select-options div p {
    color: #bdbdbd;
}
.card {
    border: 1px solid #474747;
}
.workspace_settings .entry.setting label {
    color: #bdbdbd;
}
/* "Invite people to your team" modal */

.add_user .entry label,
.add_user .entry .controls .mails input {
    color: #bdbdbd;
}
.selector .selected_wrapper,
.add_user .entry .controls .mails-container {
    border: 1px solid #474747;
    background-color: #333;
}
/* Search */

.search {
    border-left: 1px solid #474747;
    border-right: 1px solid #474747;
    background-color: #2d2d2d;
}
.search_box {
    border-bottom: 1px solid #474747;
}
.search .empty_right_column {
    background-color: #2d2d2d;
}
.search_box .input-container {
    background-color: #5a5a5a;
    border: 1px solid #474747;
}
.search-filter-form__trigger {
    border-right: 1px solid #474747;
}
.search-filter-form {
    background-color: #474747;
    border: 1px solid #474747;
}
.search-filter-form header {
    background-color: #474747;
    color: #bdbdbd;
}
.form-inline-selected {
    border: 1px solid #5a5a5a;
}
.comments article.item.collapsed,
.search_results article.item.collapsed {
    background-color: #474747;
    border-bottom: 1px solid #474747;
}
.search_item_details article.expand .load_more a {
    background-color: #bababa;
}
.search_item_details__header {
    border-bottom: 1px solid #474747;
}
.goto_item {
    border-top: 1px solid #474747;
}
/* Header Menus */

.dropdown .tip p,
.dropdown .tip p.title {
    color: #bdbdbd;
}
.topbar .dropdown_invite_people .invite_actions .invite_email {
    border-top: 1px solid #474747;
    color: #bdbdbd;
}
.topbar .toggle_workspaces a {
    color: #bdbdbd;
}
.topbar .user_box p.user_name,
.topbar .apps a {
    color: #bdbdbd;
}
.topbar .version
/* v1.00.36 */

{
    background-color: #474747;
}
.recipients .selector .item p {
    color: #fff;
}
}
