css:
@-moz-document domain(karachan.org) {
body:not(#menu) {
	background-image: url(https://i.imgur.com/KDOhJl5.png);
	background-repeat: repeat-x, no-repeat;
    background-size: cover; 
	background-attachment: fixed, fixed;
	background-position: top, right bottom;
	background-color: #12000d;
	color: #CCC;
}


.haze {
	color: hotpink;
	background-color: #2c1a4f;
	text-shadow: 0 0 5px #ff00d4!important;
}

h2.boardHeader {
	background: rgba(50, 9, 57, 0.7);
}

.ladda-button {
	border: 1px solid #AAA;
	color: #aa4678;
	
}

.ladda-button:hover {
	border: 1px solid hotpink;
	text-shadow: 0 0 5px hotpink;
	color: hotpink;
}

.ladda-button[data-loading] {
	background-color: rgba(0, 0, 0, 0.5)!important;
	border: 1px solid #CCC;
}

.ladda-progress {
	background: #560c58!important;
}

.ladda-button[data-color="mint"] {
	background-color: rgba(36, 12, 88, 0.506);
}

.ladda-button[data-color="mint"]:hover {
	background-color: #552881;
}

.btn {
    border: 1px solid #AAA;
    background-color: rgba(36, 12, 88, 0.506);
    color: #aa4678;
}

.btn:hover {
	color: hotpink;
	border: 1px solid hotpink;
	background-color: #552881;
	text-shadow: 0 0 5px hotpink
}

.BBButton {
    border: 1px solid #AAA;
    background-color: rgba(36, 12, 88, 0.506);
    color: #aa4678;;
}

.BBButton:hover {
	background-color: #552881;
	color: hotpink;
	text-shadow: 0 0 5px hotpink;
	border: 1px solid hotpink
}

.tmoved {
	color: orange;
}

a,a:visited {
	color: #93b5bf!important;
	text-decoration: none!important;
	text-shadow: none!important;
}

a.replylink:not(:hover) {
	color: #aa4678!important;
}

a:hover {
	color: #00d8ff;
	text-decoration: none;
	text-shadow: 0 0 5px #00d8ff!important
}

hr {
	border-top-width: 1px;
	border-top-style: solid;
	border-right-style: none;
	border-bottom-style: none;
	border-left-style: none;
	border-top-color: #666666;
}

input,textarea,select {
	background-color: #311077;
	border: 1px solid #ff00dc;
	border-shadow: #ff00dc;
	color: #ff00dc;
}





.next > form:nth-child(1) > input:nth-child(1) {
	background: none;
	border-radius: 4px;
	color: #aa4678;
}

.prev > form:nth-child(1) > input:nth-child(1) {
	background: none;
	border-radius: 4px;
	color:#aa4678;
}

::-webkit-input-placeholder {
    color: hotpink;
}

::-moz-placeholder {
    color: hotpink;
}

::-ms-placeholder {
    color:hotpink;
}

::placeholder {
    color: hotpink;
}

div.boardBanner>div.boardTitle {
	font-family: Tahoma,sans-serif;
	color: #ee00ff;
	text-shadow: 0 0 15px #ee00ff
}

div.sideArrows {
	color: #e0b7dd;
}

div.reply {
	background: rgba(77, 18, 99,0.8);
	padding: 4px!important;
	color: #cacaca;
	filter: alpha(opacity=50);
	-moz-opacity: 0.5;
	-moz-border-radius: 4px;
	-webkit-border-radius: 4px;
	border-radius: 4px;
	color: #CCC;
}
	
div.post div.postInfo span.postNum a {
	text-decoration: none;
	color: #aa4678;
	
}

div.post div.postInfo span.postNum a:hover,.posteruid .hand:hover {
	color: #ee00ff!important;
	text-shadow: 0 0 5px #ee00ff;
}

div.op div.postInfo {
	padding: 5px;
	background-color: rgba(36, 9, 57, 0.3);
}

div.post div.postInfo span.nameBlock span.name {
    color: #ee00ff;
    font-weight: bold;
	text-shadow: 0 0 5px #ff00d4
	
}

div.post div.postInfo span.nameBlock span.postertrip {
	color: #831aa7;
}

.quoteLink,.quotelink,.deadlink {
	color: #64abba!important;
	

}


.quoteLink,.quotelink:hover,.deadlink:hover {
	color: #00f2ff!important;
	text-shadow: 0 0 5px #00f2ff

}
div.postingMode {
	background-color: #150939
}

div.pagelist {
	background: #300635;
	border-right: 2px solid #d9b7d5;
	border-bottom: 2px solid #d9b7d5;
	color: #999;
	border-radius: 10px;
}

div.pagelist div.pages a {
	text-decoration: none!important;
	color: #870099;
}

div.pagelist strong {
	color: #FFFFFF;
}

div.pagelist div.cataloglink {
	border-left: 1px solid #d9b7d5;
}

.extended-small .thread {
	border: 2px solid rgba(175, 128, 173, 0.63);
	background: rgba(255, 255, 255, 0.05);
}

table.postForm>tbody>tr>td:first-child {
	background-color: #240c58;
	color: hotpink;
	border: 1px solid hotpink;
	border-radius: 6px;
	text-shadow: 0 0 5px #ee00ff;
}

table.logForm>tbody>tr>td:first-child {
	background-color: #552881;
	color: hotpink;
	border: 1px solid hotpink;
	border-radius: 6px;
	text-shadow: 0 0 5px #ee00ff;
}

#postForm input[type=text],input[type=password],textarea {
	border: 1px solid #aa4678;
	background-color: #311077!important;
	color: #ff00dc
	
}


#postForm input[type=text]:focus,input[type=password]:focus,input:not([type]):focus,textarea:focus {
	border: 1px solid hotpink!important;
	background-color: #240c58!important;
	text-shadow: 0 0 50px #ff00dc;
}

.postblock {
	background-color: #b988ee;
	color: #880085;
	border: 1px solid #800;
}

.password {
	color: #00d0ff;
	text-shadow: 0 0 5px #00d0ff;
}

.highlightPost {
	background: #580c3c;
	color: #FFF;
	border: 2px dashed #707070;
}

.reply:target,.reply.highlight {
	background:#540c58;
	color:#FFF;
	border: 2px dashed #707070;
}

span.spoiler {
	color: #000!important;
	background: #000!important;
}

span.spoiler:hover,span.spoiler:focus {
	color: #fff!important;
}

s,s a:not(:hover) {
	color:#9039b5!important;
	background: #9039b5!important;
	text-decoration: none;
}

s:hover,s:focus,s:hover a {
	color: #fff!important;
	box-shadow: 0px 0px 5px hotpink
}

.useremail:not(:hover) .name,.useremail:not(:hover) .postertrip {
    color: #0080a0!important;
	text-decoration: none!important;
	text-shadow: 0px 0px 5px #2c003f!important;
 
}

.useremail:hover * {
    color: #00cbff!important;
	text-shadow: 0px 0px 5px #00cbff!important;
}

#postForm input[type="text"], textarea {
	background-color: #999;
}

input[type="password"] {
	background-color: hotpink;
}

#quote-preview {
    border: outset;
    border-width: 1px;
    border-color: #c891d9 !important;
	background-color: #2d0635;
	margin: 8px !important;
	padding: 4px!important;
	color: #cacaca;
	filter: alpha(opacity=50);
	-moz-opacity: 0.5;
	-moz-border-radius: 6px;
	-webkit-border-radius: 6px;
	box-shadow: 0 0 5px 0.5 hotpink!important
}

#captcha > td:nth-child(2) > div:nth-child(2) {
	background-color: #240c58!important;
	border: 1px solid hotpink!important;
	color: #240c58!important
}

.blockquoteReply > div:nth-child(2) {
	background-color: rgba(57, 9, 54, 0.7)!important;
	border: 1px solid rgb(170, 170, 170)!important;
}

.captchaClickHere {
	background-image: url('data:image/png;base64,');
}

.reloadCaptcha {
	border: 1px solid rgb(170, 170, 170)!important;
}

.fastform > .blockquoteReply > .captchaBlock {
	border: 1px solid hotpink;
}

#menu {
	background-color: #2a0030;
	background-image: url(data:image/png;base64,);
	background-repeat: repeat-x;
	background-attachment: fixed;
	color: #ff00bb;
}

#menu .coll {
	background: #730d73;
	border: 1px solid hotpink;
}

#menu li a {
	color: hotpink!important;
	text-decoration: none!important;
}

#menu li a:hover {
	text-decoration: none!important;
	background: #290939!important;
}

#menu h2 {
	background-color: #290939;
	color: hotpink;
	border: 1px solid hotpink;
	background-color: #290939;
}

.skandalBox {
	background: none repeat scroll 0% 0% #580c4c;
	border: 1px solid #000;
}

.skandalBox h2 {
	background-color: #580c4c;
	border: 1px solid #000;
}

.skandalBox button {
	border: 1px solid #AAA;
	background: #580c4c;
	color: #aa4678;
}

.skandalBox button:hover {
	background: none repeat scroll 0% 0% #812872;
}

table:not(#postForm)>thead>tr>td {
	background-color: #290033;
	color: #999;
	border: 1px solid hotpink;
}

table:not(#postForm)>tbody>tr>th {
	background-color: #290033;
	color: #999;
	border: 1px solid hotpink;
}

table:not(#postForm):not(#fastReplyButtons) tbody tr:nth-of-type(odd) {
	background-color: rgba(219, 48, 183, 0.24);
}

.box-outer {
	border: 1px solid hotpink;
}

.boxbar {
	background: hotpink;
	color: #999;
}

.top-box {
	background: rgba(89, 14, 80, 0.5);
}

a:visited {
	color: #86638e;
}

.content {
	color: rgb(153, 153, 153);
	background: rgba(255, 45, 195,0.3);
}

.content h3 {
	background: rgba(255, 45, 195, 0.7);
	border: 1px solid hotpink;
	color: rgb(136, 109, 145);
}

img {
    border: hotpink;
	text-shadow: 0 0 5px #803582
}

.lnkSmall {
	color: #000000;
}

.lnkSmall:hover {
	color: #803582 !important;
	text-decoration: underline;
}

.lnkSmall:visited {
	color: #000000;
}

.postnew {
	background-color: rgba(114, 14, 91, 0.3)!important;
	border: 1px solid #803582;
	text-shadow: 0 0 5px #803582
}

#settingsDiv {
	background: #39092d;
	border: 1px solid #000;
}

#settingsTitle {
	background: #ba1691;
}

#watcher_box {
	border: solid 1px cyan;
	background-color: rgba(0, 123, 130, 0.2);
	box-shadow: 0 0 5px cyan
}

#watcher_title {
	background: #f200ff;
}

.meny h2 {
	background: #880078!important;
}

.meny li:hover {
	background: #280939;
}

.meny a {
	color: #c120b9!important;
	font-family: sans-serif!important;
	font-size: 86%;
}

.meny-left .meny-arrow {
	border-left: 16px solid #300939!important;
}

.meny {
	background-color: #100012!important;
	background-image: url(data:image/png;base64,)!important;
	background-repeat: repeat-x!important;
	background-attachment: fixed!important;
	background-position: top!important;
	color: #aa4678!important;
}

div.sub-menu {
	box-shadow: 0px 3px 3px rgba(42, 5, 45, 0.3);
	background: #2a052d;
}

.modal-cont {
	background: #530c58!important;
	border: 1px solid hotpink;
}

.modal-cont label {
	color: hotpink!important;
}

.modal-cont input[type="button"] {
	background: #330634;
	color: #aa4678;
	border: 1px solid #AAA;
	transition: all 0.3s cubic-bezier(0.175, 0.885, 0.32, 1.275) 0s;
}

.modal-cont input[type="button"]:hover {
	background: #730e75;
}

#menu .modal-nav li {
	border-right: 1px solid #000;
	border-bottom: 1px solid #000;
	background-color: #732881;
}

#menu .modal-nav li:hover {
	background-color: #580c54;
}

.group-options a:hover, .group a:hover {
	background: #93118d!important;
}

@media only screen and (max-width: 1024px) {

	.group a {
		background: #580c56!important;
	}
	#menu {
	background-color: rgba(29, 4, 26, 1)!important;
	background-image: none!important;
	}
	a.tab:hover, a.tab-opened {
	background: rgba(58, 7, 52, 1)!important;
	}
	#just-a-menu-bg {
	background: #330022!important;
	}
}

#FLupfile {
	color: hotpink;
}

.fastform {

}

.ui-corner-all {
	background: rgba(45, 5, 44, 0.8);
	border: 1px solid #AAA;
}

.ui-widget-header {
	background: #550c58;
	color: #ff00f6;
	border: 1px solid #1e091b;
}

#charsCounterNormal {
	color: hotpink;
}

#charsCounterFastReply {
	color: hotpink;
}

.postOptions {
	border: 1px solid #8c13a3;
}

.postOptions a:hover {
	background: #4c0c58;
}


.rules > li  {
	color: #00d0ff!important ;
	text-shadow: 0 0 5px cyan!important
}

.rules > li > b {
color: #ffdd00 !important;
text-shadow: 0 0 5px #ffdd00!important

} 


#counter {
color: #ffdd00 !important;
font-weight: bold;
text-shadow: 0 0 5px #ffdd00!important;
}



.captchaClickHere { 
    background-image: url("https://i.imgur.com/HSI31Xr.jpg"); 
}










.stylechanger {
color: #ff00dc!important;
font-weight: bold;
text-shadow: 0 0 20px #ff00dc

} 

input[name="reason"] {
color: #ff00dc !important;
} 



.opsign {
color: #f6ff00!important;
text-shadow: 0 0 20px #f6ff00;
}

.quote {
color: #00ddff !important;
}

.navLinks {
	color: #00ddff!important;
	text-shadow: 0 0 5px #ff00e5!important
}


.opsign {
color: #f6ff00!important;
text-shadow: 0 0 20px #f6ff00;
}

.deleteform {
color: #ff00dc!important;
text-shadow: 0 0 2px  #311077
}

.autocount {
color: #00ddff!important;
text-shadow: 0 0 5px #ff00e5!important
}

.uinfo {
color: #00ddff!important;
text-shadow: 0 0 5px #ff00e5!important
}
}
