css:
@-moz-document domain("www.creationkit.com") {
/*WIKI UI - Logo*/

a.mw-wiki-logo {
	background-color: transparent !important;
	background-image: url("http://i.imgur.com/7fWOYlE.png") !important;
}
#p-logo {
	background: transparent !important;
}
div#p-navigation,
div#p-tb {
	background-color: transparent !important;
}
div#mw-panel div.portal div.body ul li a,
div#mw-panel div.portal div.body ul li a:visited {
	color: lightblue !important;
}
div#mw-panel div.portal h3 {
	color: #efefef !important;
}
/*WIKI UI - Splashcreen Background*/

body,
#mw-head,
.mediawiki,
.noresize,
#bodyContent,
#content {
	background: #161616 !important;
	color: #efefef !important;
	border: none !important;
}
#firstHeading {
	background: #161616 !important;
	height: auto !important;
}
/*WIKI UI - Account*/

a,
a:visited,
.mw-body a.external,
.mw-body a.external:visited {
	color: lightblue !important;
}
a.new,
a.new:visited {
	color: lightcoral !important;
}
/*WIKI UI - General Text Color*/

h1,
h2,
h3,
h4,
h5,
h6,
h7,
h8,
h9,
#firstheading {
	color: #fcb532 !important;
	border-bottom: 1px solid #333 !important;
}
p {
	color: #efefef !important;
}
#toc,
.toc {
	background-color: #232323 !important;
	color: lightblue !important;
	border: none !important;
}
pre,
code,
textarea,
div.mw-geshi.mw-code.mw-content-ltr {
	background-color: #232323 !important;
	border: none !important;
}
.catlinks,
div#simpleSearch input,
div#simpleSearch,
.mw-ui-input,
.mw-search-profile-tabs {
	background-color: #232323 !important;
	border: none !important;
	color: #efefef !important;
}
.mw-search-profile-tabs div.search-types ul li.current a,
.mw-search-profile-tabs div.search-types ul li.current a:visited {
	color: #efefef !important;
}
#mw-page-base,
#mw-head-base {
	visibility: hidden !important;
}
.mw-plusminus-pos,
.mw-search-result-data {
	color: lightgreen !important;
}
.mw-plusminus-neg {
	color: lightcoral !important;
}
#pagehistory li {
	border: none !important;
}
/*Papyrus Syntax Color*/

pre,
code,
textarea,
.mw-code,
.papyrus.source-papyrus .sy0,
.papyrus.source-papyrus .br0,
.papyrus.source-papyrus .kw1 {
	color: #d4d4d4 !important;
}
.papyrus.source-papyrus .kw3,
.papyrus.source-papyrus .nu0 {
	color: #D33682 !important;
}
.papyrus.source-papyrus .st0 {
	color: #8D983E !important;
}
.papyrus.source-papyrus .kw1 {
	color: #B58900 !important;
}
.papyrus.source-papyrus .kw2 {
	color: #268BD2 !important;
}
.papyrus.source-papyrus .kw5 {
	color: #CB4B16 !important;
}
.papyrus.source-papyrus .me0 {
	color: #7e7eba !important;
}
/* XML */

.xml.source-xml .sc3,
.xml.source-xml .re1,
.xml.source-xml .re2 {
	color: #7e7eba !important;
}
.xml.source-xml .re0 {
	color: #268BD2 !important;
}
.xml.source-xml .st0 {
	color: #8D983E !important;
}
/*WIKI UI - TABS*/

div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul li,
div.vectorTabs li.selected,
div.vectorTabs span,
div.vectorTabs a {
	background-color: transparent !important;
	background-image: none !important;
}
div.vectorTabs li a,
div.vectorTabs li a:visited,
div.vectorTabs li.selected a,
div.vectorTabs li.selected a:visited {
	color: lightblue !important;
}
div.vectorTabs li.new a,
div.vectorTabs li.new a:visited {
	color: lightcoral !important;
}
/* TABLES */

table.wikitable,
table.nmbox {
	border-color: #161616 !important;
	background-color: #161616 !important;
}
table.wikitable *,
table.nmbox * {
	color: #efefef !important;
	background-color: #232323 !important;
	border-color: #161616 !important;
}
table.wikitable a,
table.wikitable a:visited,
table.nmbox a,
table.nmbox a:visited {
	color: lightblue !important;
}
/* DIFF */

td.diff-context {
	color: #efefef !important;
	background-color: #232323 !important;
	border-color: #161616 !important;
}
td.diff-deletedline {
	border-color: lightcoral !important;
}
td.diff-addedline {
	border-color: lightgreen !important;
}
td.diff-deletedline .diffchange {
	background: darkred !important;
}
td.diff-addedline .diffchange {
	background: darkgreen !important;
}
/* THUMBNAILS */

div.thumbinner,
li.gallerybox div.thumb {
	border: none !important;
	background-color: #232323 !important;
	color: #efefef;
}
div.thumbinner a,
div.thumbinner a:visited {
	color: lightblue !important;
}
html .thumbimage {
	border: 1px solid lightblue !important;
}
/*WIKI UI - Footer*/

#footer-info-lastmod,
#footer-poweredbyico {
	visibility: hidden !important;
}
}
