css:
@-moz-document domain("grcade.co.uk") {
.username-coloured {
    text-shadow: -1px -1px 0 #40444C,
    -1px 1px 0 #40444C,
    1px -1px 0 #40444C,
    1px 1px 0 #40444C;
}

html {
    background: #202730;
}

body {
    background: #202730;
    color: #727C86 !important;
    font-family: "SF Pro Text", Helvetica, Arial, sans-serif;
}

a {
    color: #9C9FA4;
}

a.topictitle {
    color: #848890 !important;
}

.row, .bg1, .bg2, .bg3 {
    background: #32353A !important;
    border-color: #282C30 !important;
}

.header, .nav-tabs {
    background: #40444C !important;
}

.headerbar {
    color: #727C86;
}

hr {
    border-color: #12171C;
}


.navbar, .selected {
    background: #32353A !important;
    border-color: #282C30 !important;
}

.pagination li a {
    background: #40444C !important;
    border-color: transparent !important;
    color: #848890;
}

label {
    color: #848890;
}

fieldset dl:hover dt label {
    color: #A6AFB9;
}

h1, p.sitename {
    color: #727C86;
}

.panel {
    background: #40444C !important;
    border-color: #40444C !important;
    color: #727C86;
}

.panel dd:hover span {
    color: #A6AFB9;
}

.panel h2, .content h2 {
    color: #A6AFB9;
    border-color: #727C86;
}

h3 {
    color: #A6AFB9 !important;
}

ul.topiclist li {
    color: #727C86;
}

.forabg ul.topiclist li.header, .forumbg ul.topiclist li.header {
    color: #727C86;
}

.header, .panel > .inner > h3:first-child, .panel > h3:first-child {
    background: #383C44 !important;
    border-color: #727C86 !important;
}

.content {
    color: #848890;
}

.postbody {
    color: #727C86;
}

.inputbox, textarea, select {
    background: #32353A !important;
    border-color: #32353A;
    color: #727C86
}

.inputbox:hover, select:hover {
    color: #848890 !important;
    border-color: #D03C3C !important;
    border-right: 1px solid #D03C3C;
}

.inputbox:focus, select:focus {
    color: #848890 !important;
    border-color: #727C86 !important;
    border-right: 1px solid #727C86;
}

.inputbox:hover::placeholder {
    color: #848890;
}

dd label {
    color: #727C86;
}

#message-box textarea {
    color: #848890;
}

.postprofile, .signature, .post {
    border-color: #24282F;
    color: #727C86;
}

.postprofile strong {
    color: #A6AFB9;
}

blockquote, .dropdown, .dropdown-contents, .codebox {
    background: #40444C !important;
    border-color: #848890;
    color: #A6AFB9
}

blockquote blockquote, blockquote blockquote blockquote blockquote {
    border-color: #24282F;
    background: #32353A !important;
}

blockquote blockquote blockquote, blockquote blockquote blockquote blockquote blockquote {
    background: #40444C !important;
    border-color: #848890;
}

cite {
    color: #727C86;
}

.codebox p {
    color: #727C86;
    border-color: #848890;
}

.codebox code {
    color: #00FF00;
}

.dropdown li {
    border-color: #848890;
}

.navbar .nav-tabs .tab:hover, .navbar .nav-tabs .tab.dropdown-visible {
    background: #24282F;
}

.notification_list li.dropdown-notification {
    background: #24282F;
    border-color: #32353A;
}

.notification_list li.dropdown-notification:hover {
    background: #40444C;
    border-color: #32353A;
}

.notification_list li.dropdown-notification strong {
    color: #727C86;
}

.dropdown-extended .header, .dropdown-extended .footer {
    border-color: #32353A;
}

table.table1, tbody, td, tr {
    border-color: #24282F !important;
    color: #727C86 !important;
}

tr.bg3 td {
    background: #40444C !important;
}

.post-buttons-seperator {
    color: #24282F !important;
}

#tabs .activetab > a, #tabs .activetab > a:hover {
    background: #40444C !important;
    border-color: #40444C;
    color: #A6AFB9;
}

#tabs .tab > a {
    background: #24282F;
    border-color: #24282F;
    color: #727C86;
}

#tabs .tab > a:hover {
    background: #40444C;
    border-color: #40444C;
}

.forum-statistics .value {
    color: #727C86;
}

#navigation a {
    background: #40444C;
}

#navigation a:hover {
    background: #32353A;
}

#navigation #active-subsection a {
    background: #24282F;
}

.cp-mini {
    background: #32353A;
}

#cp-main .pm {
    background: #40444C !important;
}

.panel-container .panel li.header dd, .panel-container .panel li.header dt {
    color: #727C86;
}

fieldset.polls dl {
    border-color: #727C86;
    color: #848890;
}

fieldset.polls dl.voted {
    color: #A6AFB9;
}

fieldset.polls dl.most-votes {
    color: #FFFFFF;
}

div.with-mark a.forumtitle {
    text-shadow: -1px -1px 0 #40444C,
    -1px 1px 0 #40444C,
    1px -1px 0 #40444C,
    1px 1px 0 #40444C;
} 

.search-box .button, .search-box .post-buttons > #expand > a, .search-box .button:hover, .search-box .post-buttons > #expand > a:hover {
    background: #40444C !important;
    border-color: #40444C !important;
}

.search-box .inputbox, .search-box .inputbox:hover, .search-box .inputbox:focus, .search-box .button:hover, .search-box .post-buttons > #expand > a:hover {
    border-color: #40444C;
}

.post:target .content {
    color: #727C86;
}

.phpbb_alert {
    background: #40444C;
    border-color: #40444C;
    color: #727C86;
}

dl.details dt {
    color: #727C86;
}

dl.details dd {
    color: #848890;
}
}
