css:
@-moz-document url-prefix("https://jumpin.chat/") {
body {
    margin: 0;
    font-size: 1.3rem;
    font-family: "Roboto", "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: /*[[a-color]]*/;
}
.cams__Header {
    display: flex;
    flex-shrink: 0;
    justify-content: space-between;
    height: 40px;
    background: #3c434c;
    color: #ffffff;
}
.button:hover {
    background-color: /*[[a-hover]]*/;
    /* Green */
    
    color: white;
}
.roomHeader {
    display: flex;
    flex-shrink: 0;
    height: 40px;
    padding-left: .5em;
    padding-right: .5em;
    background: /*[[a-color]]*/;
    color: /*[[a-color]]*/;
    justify-content: space-between;
}
.chat__Feed {
    flex-grow: 1;
    padding: .25em;
    overflow-y: scroll;
    background: #111111;
    border-right: 1px solid #3c434c;
    color: #ffffff;
}
#style-1::-webkit-scrollbar {
    width: 6px;
    background-color: #F5F5F5;
}
.chat__Header {
    display: flex;
    flex-shrink: 0;
    justify-content: space-between;
    height: 40px;
    background: rgba(0, 0, 0, .8);
}
.input {
    padding: 0 .25em;
    background: #111111;
    color: #e7ecf2;
    outline: 0;
}
.chat__Input {
    flex-grow: 1;
    border: 0;
    border-top: 1px solid /*[[a-color]]*/;
}
.button-blue {
    background: /*[[a-color]]*/;
    color: #fff;
}
.chat__UserList {
    background: #111111;
    color: #ffffff;
    position: absolute;
    border-left: 3px solid /*[[a-color]]*/;
    right: -17px;
    /* Increase/Decrease this value for cross-browser compatibility */
    overflow-y: scroll;
}
.chat__UserListItem+.chat__UserListItem {
    border: 1px solid #5a6370;
    background: /*[[a-color]]*/;
    padding-right: 5px;
    padding-left: 5px;
}
.roomHeader__Logo {
    background-image: url(http://bcweedco.com/wp-content/uploads/2017/01/weed-logo.png);
    height: 500px;
}
.cams__ContainerInternal {
    display: flex;
    flex-grow: 1;
    align-items: center;
    background: /*[[a-color]]*/;
    background-image: url('/*[[a-picture]]*/');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
}
.cams__RoomName {
    position: absolute;
    left: 60px;
    top: 0px;
}

.cams__Cam {
    background: #3c434c;
    border-top: 1px solid;
    border-right: 2px solid;
    border-bottom: 1px solid;
    border-left: 2px solid;
    border-color: /*[[a-cam-border]]*/;
    box-sizing: border-box;
    transition: box-shadow .25s ease-out,border-color .25s ease-out;
}









.button--blue {
    background-color: /*[[a-color]]*/;
}
.full-width--white {
    background-color: #111111;
}
.hero {
    position: relative;
    height: 200px;
    padding-top: 0;
    padding-bottom: 3.2rem;
    background-image: url('/*[[a-picture]]*/');
    background-size: cover;
}
.hero__Message {
    padding-top: 10rem;
}
.full-width--grey {
    background-color: #3c434c;
    display: none;
}
.full-width {
    float: left;
    width: 100%;
}
.info {
    padding: 2.4rem 0;
    display: none;
}
.wrapper {
    margin: 0 auto;
    padding: 0 1rem;
}
.room__Item {
    flex-basis: 320px;
    margin: 1em .5em 0;
    background: #333333;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .25);
    text-decoration: none;
    color: #ffffff;
}
.room__Name {
    flex-grow: 1;
    margin: 0;
    border-radius: 3px;
    color: #FFFFFF;
    font-family: inherit;
    font-size: 2em;
    font-weight: inherit;
    text-transform: uppercase;
    text-align: center;
}
.room__Broadcasters,
.room__Viewers {
    padding: 0 .35em;
    border-radius: 3px;
    text-align: center;
    color: /*[[a-color]]*/;
    font-weight: 600;
}

.privateMessages__ListWrapper {

    color: #ffffff;
    position: absolute;
    border-left: 3px solid /*[[a-color]]*/;
    left: 0px;
    /* Increase/Decrease this value for cross-browser compatibility */
    overflow-y: scroll;
    overflow:hidden;
            background: #111111;
}
.header__Title  {
    background-image: url(http://bcweedco.com/wp-content/uploads/2017/01/weed-logo.png);

}
.privateMessages__Empty {
    display: flex;
    flex-grow: 1;
    align-items: center;
    justify-content: center;
    background: /*[[a-color]]*/;
    border-right: 1px solid #c7cfd9;
    color: #000000;
    font-size: 1.25em;
    font-weight: 400;
}
.dropdown__Options {
    width: 100%;
    min-width: 15rem;
    background: #333333;
    border-radius: 3px;
    overflow: hidden;
    box-shadow: 0 1px 2px rgba(0,0,0,.5);
    border:1px solid /*[[a-color]]*/;
	border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(125, 167, 58);

}
.dropdown__Option {
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 35px;
    padding: .5rem 1em;
    box-sizing: border-box;
    border-bottom: 1px solid /*[[a-color]]*/;
    cursor: pointer;
    font-weight: 500;
    text-align: center;
    text-decoration: none;
    color: /*[[a-color]]*/;
}
.dropdown__Option-button:hover, .dropdown__Option:hover {
    background: /*[[a-color]]*/;
    color: #ffffff;
}
.dropdown__Option-header, .dropdown__Option-header:hover {
    color: #ffffff;
    font-weight: bold;
        display: flex;
    justify-content: space-between;
    align-items: center;
    height: 35px;
    padding: .5rem 1em;
    box-sizing: border-box;
    border-bottom: 1px solid /*[[a-color]]*/;
    cursor: pointer;
    text-align: center;
    text-transform: uppercase;
}
}

