    padding: 1em;
    background-color: #333333;
    border: 1px solid /*[[a-color]]*/;
}
.header__Title {
    margin: 0 0 1.6rem;
    padding: 50px 0 0;
    background-image: url(http://bcweedco.com/wp-content/uploads/2017/01/weed-logo.png);
    background-repeat: no-repeat;
    background-size: 50px;
    background-position: center top;
    font-size: 4.2rem;
    font-weight: 300;
    color: #fff;
    text-align: center;
}
.header__ProfileDropdownItem {
    display: block;
    padding: .45em 1em;
    color: /*[[a-color]]*/;
    background-color:#333333;
    border:1px solid /*[[a-color]]*/;
    text-decoration: none;
    text-align: center;
    line-height: 1;
}
.header__ProfileDropdownItem+.header__ProfileDropdownItem {
    border-top: 1px solid /*[[a-color]]*/;
}
}

