.form__Input {
    padding: .4em .75em;
    border: 1px solid #ffffff;
    border-radius: 2px;
    background-color: /*[[a-color]]*/;
    color: #ffffff;
    outline: 0;
    box-sizing: border-box;
}

.settings__Menu {
    list-style: none;
    margin: 0;
    padding: 0;
    border: 1px solid #ffffff;
    background-color:/*[[a-color]]*/;
    border-radius: 3px;
}
.settings__MenuItemLink {
    display: block;
    padding: .25em;
    background-color: /*[[a-color]]*/;
