css:
@-moz-document url("https://www.coinbase.com/dashboard") {
/* header */ 
.eIziKc {
    background-color: #333333;
}
/* nav bg */ 
.UMJSF {
    background-color: #202020;
}
/* main bg */ 
.dbBnxF {
    border-bottom: 1px solid #757575;
    background-color: #202020;
}
/* cont header bg */ 
.ldrMGn {
    border-bottom: 1px solid #757575;
    background-color: #333333;
}
/* cont price bg */ 
.gqcCWD {
    flex-shrink: 0;
    height: 140px;
    padding: 0px 56px;
    background-color: #333333;
}
/* cont graph bg */ 
.gcuQks {
    position: relative;
    margin: -1px 20px 0px;
    border-top: 1px solid #757575;
    border-bottom: 1px solid #757575;
    background-color: #333333;
}
/* dividers */
.jvgDjo {
    border-color: #757575;
}
/* cont price bg */
.jmZGzC {
    margin: 10px 40px;
    background-color: #333333;
}
.gXpXdG {
    display: flex;
    flex-direction: column;
    box-shadow: rgba(0, 0, 0, 0.05) 0px 1px 2px 0px;
    background-color: #333333;
    border-radius: 4px;
    border-width: 1px;
    border-style: solid;
    border-color: #757575;
    border-image: initial;
}
/* cont price bg */ 
.cIAmxo {
    flex-direction: column;
    flex: 1 1 0%;
    background-color: #333333;
}
/* in between bg */
.dLmVNN {
    background-color: #333333;
}
/* footer bg */ 
.faRDVy {
    border-top: 1px solid #757575;
    background-color: #333333;
}
/* footer input bg */
.hcFbgK {
    color: #202020;
    border-color: #202020;
    background-color: #757575;
}
/* fotter nav links bg */
.hhsGxO {
    width: 1180px;
    max-width: 1180px;
    font-size: 14px;
    color: #757575;
}
.dUEqc {
    color:  #757575;
}
/* coin dividers bg */
.gGoMFO:not(:first-child) {
    border-top: 1px solid #757575;
}
.jpJBDV {
    flex-shrink: 0;
    height: 54px;
    font-weight: 500;
    color: rgb(125, 149, 182);
    border-top: 1px solid #757575;
    transition: all 0.25s ease;
}
.inbMSi {
    color: rgb(155, 166, 178);
    border-bottom: 1px solid #757575;
}
.hBJAEm {
    color: rgb(125, 149, 182);
    border-top: 1px solid #757575;
}
/* coin link active */
.FMlLB  {
    font-size: 18px;
    font-weight: 500;
    color: #3C9C41;
}.iYzyqx {
    margin-top: 1px;
    color: #3C9C41;
    font-size: 16px;
    font-weight: 500;
}.hoyLpr  {
    color: #FFB200;
}.lbsPRY   {
    border-bottom: 1px solid #757575;
}
}
