css:
@-moz-document domain(wex.nz)
{

/*
    Just a copy-paste from this link, thank you airflame.
    https://userstyles.org/styles/98320/btc-e-dark-theme
*/
    
  body
  {
    background: url("http://subtlepatterns.com/patterns/slash_it.png");
    color: #CBCBCB;
  }

  #header
  {
    background: url("http://subtlepatterns.com/patterns/slash_it.png");
  }

  a
  {
    color: #8DA0B9;
  }

  .orderStats
  {
    color: #8DA0B9;
  }

  #footer
  {
    background: url("http://subtlepatterns.com/patterns/navy_blue.png");
  }

  .block
  {
    background: none;
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  #twitter-widget-0
  {
    display: none !important;
  }

  h3
  {
    color: #8DA0B9;
  }

  h1
  {
    color: #8DA0B9;
  }

  .profile
  {
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  #nChatInput
  {
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
    color: #8DA0B9;
  }

  .pairs li
  {
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  .inblock
  {
    background: none;
  }

  .inblock
  {
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  input, textarea, select
  {
    background: none;
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
    color: #8DA0B9;
  }

  .table tr:nth-child(2n+1)
  {
    background: url("http://subtlepatterns.com/patterns/dark_fish_skin.png") repeat scroll 0 0 rgba(0, 0, 0, 0);
  }

  .table th
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #5F5F5F;
  }

  .pairs li
  {
    background: none repeat scroll 0 0 #CBCBCB;
  }

  .pairs a
  {
    color: #000000;
  }

  .pairs a:hover, .pairs-selected
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #404040 !important;
  }

  .main-s-navhover_bg
  {
    background: none repeat scroll 0 0 #EFEFEF;
    border-radius: 4px;
  }

  .menu li
  {
    background: none repeat scroll 0 0 #CBCBCB;
    border-radius: 4px;
  }

  .menu a
  {
    line-height: 30px;
  }

  .inblock.mpanel_hin
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #5F5F5F;
  }

  .inblock.mpanel_h
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #5F5F5F;
  }

  .tcenter tr
  {
    background: none !important;
  }

  .profileBtn
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #303030;
    border-top: 0px;
    border-radius: 4px;
  }

  .profileBtn:hover
  {
    background: url("https://wex.nz/themes/standart/images/table_h.png") repeat scroll 0 0 #202020;
    border-radius: 4px;
  }

  .button, button
  {
    background: none repeat scroll 0 0 #CBCBCB;
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  .button:hover
  {
    background: none repeat scroll 0 0 #8F8F8F;
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
  }

  .pagenav a, .pagenav b
  {
    background: none repeat scroll 0 0 #CBCBCB;
    border: 1px solid #373737;
    box-shadow: 0 0 6px #4F4F4F;
    color: #000000;
  }

  g rect
  {
    fill: #1E1E2E;
  }

  rect
  {
    fill: none;
  }

  g path
  {
    fill: #262626;
  }

  g text
  {
    fill: #CBCBCB;
  }

  #nChat b
  {
    color: #BB000D;
  }

  #nChatRefreshBtn
  {
    background: #262626 !important;
  }

  #nPopupCon
  {
    background: #262626 !important;
  }

  .order_self
  {
    background: url("http://subtlepatterns.com/patterns/images/table_h.png") repeat scroll 0 0 #707884 !important;
    color: #80FF80;
  }

  #nChatErrors
  {
    width: 280px;
    background: none repeat scroll 0 0 #7F0000;
  }

  #cMenu
  {
    background: none repeat scroll 0 0 #242424;
  }

  .msg-text
  {
    color: #8DA0B9;
  }

  .msg-con
  {
    background: none;
  }

  .msg-con:hover
  {
    background: none;
  }
}
