css:
@-moz-document domain("query.wikidata.org") {
html, body, #query-box .query-helper .panel-info, .wikibase-queryservice {
    background-color: #222222;
    color: #fff;
}

.table-hover > tbody > tr:hover {

    background-color: #444444;

}

#query-box .query-helper .panel-heading {
    background-color: #444444;
}

.navbar {
    background: #444444;
    border-color: #222222;
}


/* Based on the theme at http://bonsaiden.github.com/JavaScript-Garden */

/*<!--match-->*/
.cm-s-default span.CodeMirror-matchhighlight { background: #494949; }
.cm-s-default.CodeMirror-focused span.CodeMirror-matchhighlight { background: #314D67 !important; }

/*<!--activeline-->*/
.cm-s-default .CodeMirror-activeline-background { background: #253540; }

.cm-s-default.CodeMirror {
    background: #0F192A;
    color: #D1EDFF;
}

.cm-s-default div.CodeMirror-selected { background: #314D67; }
.cm-s-default .CodeMirror-line::selection, .cm-s-default .CodeMirror-line > span::selection, .cm-s-default .CodeMirror-line > span > span::selection { background: rgba(49, 77, 103, .99); }
.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-default .CodeMirror-line > span::-moz-selection, .cm-s-default .CodeMirror-line > span > span::-moz-selection { background: rgba(49, 77, 103, .99); }
.cm-s-default .CodeMirror-gutters { background: #0F192A; border-right: 1px solid; }
.cm-s-default .CodeMirror-guttermarker { color: white; }
.cm-s-default .CodeMirror-guttermarker-subtle { color: #d0d0d0; }
.cm-s-default .CodeMirror-linenumber { color: #D0D0D0; }
.cm-s-default .CodeMirror-cursor { border-left: 1px solid #F8F8F0; }

.cm-s-default span.cm-comment { color: #428BDD; }
.cm-s-default span.cm-atom { color: #AE81FF; }
.cm-s-default span.cm-number { color: #D1EDFF; }

.cm-s-default span.cm-property, .cm-s-default span.cm-attribute { color: #A6E22E; }
.cm-s-default span.cm-keyword { color: #E83737; }
.cm-s-default span.cm-string { color: #1DC116; }

.cm-s-default span.cm-variable { color: #FFAA3E; }
.cm-s-default span.cm-variable-2 { color: #FFAA3E; }
.cm-s-default span.cm-def { color: #4DD; }
.cm-s-default span.cm-bracket { color: #D1EDFF; }
.cm-s-default span.cm-tag { color: #449; }
.cm-s-default span.cm-link { color: #AE81FF; }
.cm-s-default span.cm-error { background: #F92672; color: #F8F8F0; }

.cm-s-default .CodeMirror-matchingbracket {
  text-decoration: underline;
  color: white !important;
}

}
