@-moz-document url-prefix("https://nitrogensports.eu/sport"), regexp("https://nitrogensports.eu/sport") {
/* 
		the code in here only applies to:
			-pages on the domain images.example.com
			-pages whose URLs start with http://example.com/images
	*/

/* body bg */
.page-content {
    background: #222!important;
}
/* title font */
.page-title-dark, .page-title-dark small {
    color: #BD9948!important;
    font-weight: 400;
}
/* main bg */
.container-fluid {
    background-color: #222;
}
/* tab */
.nav-tabs>.active>a {
    background-color: #555!important;
    font-weight: 600;
    color: #ffffff;
}
.nav-tabs>.active>a, .nav-tabs>.active>a:focus, .nav-tabs>.active>a:hover {
    color: #ffffff;
    cursor: default;
    background-color: #fff;
    border: 1px solid #BD9948;
    border-bottom-color: transparent;
}
.nav-tabs {
    border-bottom: 1px solid #BD9948;
}
a {
    text-shadow: none!important;
    color: #777;
}
/* search */
#event-search {
    display: none;
    height: 52px;
    clear: both;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
    margin-bottom: 20px;
}
#event-search-left {
    float: left;
    width: 53px;
    height: 100%;
    border-right: 1px solid #64584c;
    background: #555;
    -webkit-border-top-left-radius: 3px!important;
    -moz-border-radius-topleft: 3px!important;
    border-top-left-radius: 3px!important;
    -webkit-border-bottom-left-radius: 3px!important;
    -moz-border-radius-bottomleft: 3px!important;
    border-bottom-left-radius: 3px!important;
    font-size: 24px;
    color: #fff;
    text-shadow: 2px 2px 2px #38322b;
    padding: 14px;
    box-sizing: border-box;
}
#event-search-right {
    float: left;
    height: 100%;
    width: -moz-calc(100% - 53px);
    width: -webkit-calc(100% - 53px);
    width: calc(100% - 53px);
    border-left: 1px solid #333;
    background: #444;
    -webkit-border-top-right-radius: 3px!important;
    -moz-border-radius-topright: 3px!important;
    border-top-right-radius: 3px!important;
    -webkit-border-bottom-right-radius: 3px!important;
    -moz-border-radius-bottomright: 3px!important;
    border-bottom-right-radius: 3px!important;
    padding: 5px;
    box-sizing: border-box;
}
#event-search-textbox {
    width: 100%;
    height: 100%;
    background: #555;
    border: none;
    border-top: 1px solid #463e36;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
    outline-style: none;
    outline-color: transparent;
    -moz-appearance: none;
    outline-width: 0;
    -webkit-box-shadow: none;
    font-family: 'Open Sans',sans-serif;
    font-size: 16px;
    color: #fff;
    text-shadow: 2px 2px 2px #38322b;
    box-sizing: border-box;
}
/* bet info */
.event {
    margin-bottom: 20px;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
    -moz-box-shadow: none;
    -webkit-box-shadow: none;
    box-shadow: none;
}
.event-header {
    margin-top: 10px;
    background: #444;
    font-size: 15px;
    font-family: 'Open Sans',sans-serif;
    color: #fff;
    font-weight: 600;
    -webkit-font-smoothing: antialiased;
    padding-top: 10px;
    padding-bottom: 0!important;
    line-height: none;
    border-bottom: 1px solid #1e1f24;
    -webkit-border-top-left-radius: 3px!important;
    -moz-border-radius-topleft: 3px!important;
    border-top-left-radius: 3px!important;
    -webkit-border-top-right-radius: 3px!important;
    -moz-border-radius-topright: 3px!important;
    border-top-right-radius: 3px!important;
}
.event-content {
    border-top: 1px solid #5a5e6d;
    background: #333;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
    padding: 10px;
    -webkit-border-top-left-radius: 0!important;
    -moz-border-radius-topleft: 0!important;
    border-top-left-radius: 0!important;
    -webkit-border-top-right-radius: 0!important;
    -moz-border-radius-topright: 0!important;
    border-top-right-radius: 0!important;
}
.event-participant {
    position: relative;
    background: #cdcdcd;
    font-size: 15px;
    padding: 10px 10px;
    font-family: 'Open Sans',sans-serif;
    color: #2c2f3b;
    font-weight: 600;
    border-top: 1px solid #13151a;
    -webkit-border-radius: 3px!important;
    -moz-border-radius: 3px!important;
    border-radius: 3px!important;
}
.event-odds {
    background: #cdcdcd;
}
.selectboxit-container {
    position: relative;
    display: inline-block;
    vertical-align: top;
    margin-top: 0;
    background: #cdcdcd;
}
.selectboxit-container .selectboxit {
    background: #cdcdcd;
}
/* bet slip */
#betslip-header {
    color: #352b2a;
    background: #aaa;
    padding: 10px;
    height: 28px;
    font-size: 18px;
    -webkit-border-top-right-radius: 3px!important;
    -moz-border-radius-topright: 3px!important;
    border-top-right-radius: 3px!important;
    -webkit-border-top-left-radius: 3px!important;
    -moz-border-radius-topleft: 3px!important;
    border-top-left-radius: 3px!important;
}
#betslip-outer {
    margin-top: 10px;
    background: #cdcdcd;
    -webkit-border-radius: 6px!important;
    -moz-border-radius: 6px!important;
    border-radius: 6px!important;
    border: 1px solid #e3e4df;
    padding: 5px;
    -moz-box-shadow: 2px 2px 2px #888;
    -webkit-box-shadow: 2px 2px 2px #888;
    box-shadow: 2px 2px 2px #888;
}
/* internal wager # who cares */
.event-rotation {
    background: #666;
    border: 1px solid #555;
}
