css:
@-moz-document domain("youtube.com"), domain("support.google.com") {
.ytd-subscribe-button-renderer:not(.style-grey-text)[subscribed],
yt-formatted-string.style-scope.ytd-subscribe-button-renderer {
    background: #EEEEEE !important;
    color: #696969 !important;
}
.ytd-subscribe-button-renderer[subscribed] span {
    color: #696969 !important;
}
.ytd-subscribe-button-renderer:not(.style-grey-text) {
    background: #0094FF !important;
    color: #FFFFFF !important;
}
.ytd-subscribe-button-renderer span {
    color: #FFFFFF !important;
}
.badge-style-type-live-now.ytd-badge-supported-renderer,
.guide-entry-badge.ytd-guide-entry-renderer,
.ytp-swatch-color,
yt-formatted-string#text.style-scope.yt-button-renderer.style-brand,
.home-page .site-menu__link--home-page,
.experiences-page .site-menu__link--experiences-page,
.policies-page .site-menu__link--policies-page,
.copyright-page .site-menu__link--copyright-page,
.brand-resources-page .site-menu__link--brand-resources-page,
.press-page .site-menu__link--press-page,
.site-menu__link:hover,
.site-menu__link:focus,
.site-menu__link:active,
.social-links__link:hover,
.social-links__link:focus,
path.style-scope.yt-icon,
ytd-guide-entry-renderer[active] .guide-icon.ytd-guide-entry-renderer,
#creator-sidebar .creator-sidebar-item.selected,
#creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover,
#creator-sidebar .creator-sidebar-submenu a.creator-sidebar-section-link.selected,
#creator-sidebar .creator-sidebar-single-section.selected a,
#creator-sidebar .creator-sidebar-single-section.selected a:hover,
#creator-sidebar > .creator-sidebar-section.selected > h3 a,
.exp-invert-logo li.guide-section h3,
.exp-invert-logo li.guide-section h3 a {
    color: #0094FF !important;
}
.badge-style-type-live-now.ytd-badge-supported-renderer {
    color: #0094FF !important;
    border: 1px solid #0094FF !important;
}
button.ytp-live-badge.ytp-button::before,
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox,
.freedoms__ribbon::after,
.card .underline__inner,
.news__link-text::after,
.tabs__nav-button .underline__inner,
.guide-badge-live .yt-valign-container {
    background: #0094FF !important;
}
.ytp-swatch-background-color,
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-chrome-controls .ytp-button[aria-pressed]::after,
#notification-count.ytd-notification-topbar-button-renderer,
#creator-sidebar .creator-sidebar-item.selected,
#creator-sidebar .creator-sidebar-submenu .creator-sidebar-item.selected a:hover,
#creator-sidebar .creator-sidebar-submenu a.creator-sidebar-section-link.selected,
#creator-sidebar .creator-sidebar-single-section.selected a,
#creator-sidebar .creator-sidebar-single-section.selected a:hover {
    background-color: #0094FF !important;
}
img[src="https://www.gstatic.com/youtube/img/branding/youtubelogo/svg/youtubelogo.svg"],
img[src="https://www.gstatic.com/images/branding/product/2x/youtube_96in128dp.png"],
.exp-invert-logo #header::before,
.exp-invert-logo .ypc-join-family-header .logo,
.exp-invert-logo #footer-logo .footer-logo-icon,
.exp-invert-logo #yt-masthead #logo-container .logo,
.exp-invert-logo #masthead #logo-container,
.exp-invert-logo .admin-masthead-logo a,
.exp-invert-logo #yt-sidebar-styleguide-logo #logo,
img.gb_1a[src="https://services.google.com/fh/files/helpcenter/yt_helpcenter_wordmark_176x41.png"] {
    filter: hue-rotate(240deg);
}
.hero__video:hover .hero__play-svg-fill,
.hero__video:focus .hero__play-svg-fill,
.page-header__logo-bg,
path.style-scope.ytd-topbar-logo-renderer[fill="#FF0000"],
path[fill="#f00"],
path[fill="#F00"],
circle[fill="#F00"],
.page-hero__video:hover .page-hero__play-svg-fill,
.page-hero__video:focus .page-hero__play-svg-fill,
.st0.ytcp-app-icon {
    fill: #0094FF !important;
}
#creator-sidebar .studio-icon.creator-sidebar-community.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-community.selected,
#creator-sidebar .studio-icon.creator-sidebar-live-streaming.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-live-streaming.selected,
#creator-sidebar .studio-icon.creator-sidebar-video-manager.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-video-manager.selected,
#creator-sidebar .studio-icon.creator-sidebar-dashboard.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-dashboard.selected,
#creator-sidebar .creator-sidebar-analytics.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-analytics.selected,
#creator-sidebar .studio-icon.creator-sidebar-translations-and-transcriptions.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-translations-and-transcriptions.selected,
#creator-sidebar .studio-icon.creator-sidebar-create.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-create.selected,
#creator-sidebar .studio-icon.creator-sidebar-channel.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-channel.selected,
#creator-sidebar .studio-icon.creator-sidebar-your-contributions.selected,
#creator-sidebar a:hover .studio-icon.creator-sidebar-your-contributions.selected {
    background-image: url("https://i.imgur.com/JOCiqSf.png")
}
.audiolibrary-column-tag .new-tag,
#creator-sidebar .yt-badge {
    color: #0094FF !important;
    border: #0094FF !important;
}
.live-welcome .live-welcome-hero {
    background-image: url("https://i.imgur.com/T7h4Y3Q.png")
}
.tabbed-page .tab.active {
    border-top: #0094FF
}
.account-status-copyright-status-v2-scale .status-scale-great,
.account-status-community-guidelines-status-v2-scale .status-scale-great {
    background-image: url("https://i.imgur.com/oFCYOy6.png")
}
}
