css:
@-moz-document domain("4chan.org") {
body, html
{
  background: rgb(22, 22, 22);
  color: rgb(160, 160, 160);
  margin: 0px;
  padding: 0px;
}
#boardNavDesktop
{
  background: rgb(22, 22, 22);
  padding-bottom: 5px;
  height: 25px;
  margin-bottom: -10px;
}
.boardList
{
  max-width: calc(100% - 120px);
  overflow-x: scroll;
}
.navLinks
{
  text-align: center;
}
.navLinks .thread-stats
{
  float: none;
}
.board .navLinks
{
  text-align: left;
}
.board .navLinks .thread-stats
{
  float: right;
}

#ctrl-top
{
  margin-top: 10px;
  text-align: center;
}
#ctrl-top hr
{
  display: none;
}
.boardBanner
{
  background: linear-gradient(to bottom, rgb(22, 22, 22) 0%,rgb(22, 22, 22) 100%);
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgb(22, 22, 22)), color-stop(100%,rgb(22, 22, 22)));
  margin-top: -5px;
}

hr.aboveMidAd,
hr.aboveMidAd + div,
hr.belowLeaderboard,
hr.abovePostForm + div
{
  display: none;
}
hr.abovePostForm
{
  width: 100%;
}

#blotter
{
  display: none;
}

.post,
.post.reply
{
  background-color: rgb(22, 22, 22) !important;
  border-color: rgb(22, 22, 22) !important;
  width: 100% !important;
  margin-top: 10px !important;
}
.post.highlight .name,
.post.highlight .dateTime
{
  color: rgb(22, 22, 22) !important;
}
.post.highlight .postMessage
{
  color: rgb(22, 22, 22);
}

#quote-preview
{
  width: auto !important;
  color: #eee !important;
}
.sideArrows
{
  display: none;
}
.expanded-thumb
{
  max-width: 80vw !important;
  max-height: 80vh !important;
}
.board
{
  width: 80%;
  max-width: 1000px;
  margin: 0px auto;
}
.pagelist
{
  background-color: rgb(22, 22, 22) !important;
  border-color: rgb(22, 22, 22) !important;
}
hr
{
  border-color: #333;
  margin-top: 30px;
  margin-bottom: 30px;
}
a,
a.quotelink,
.thread a.replylink
{
  color: #aae !important;
}

.boxbar
{
  background-color: rgb(22, 22, 22);
}
.boxcontent,
#announce
{
  background-color: rgb(22, 22, 22);
  color: #eee;
}
.boxcontent li
{
  background: none !important;
}
.box-outer
{
  background-color: rgb(22, 22, 22);
  border-color: rgb(22, 22, 22);
}
#ft
{
  margin-top: 20px;
}
#ft ul
{
  width: auto;
  text-align: center;
  border: none;
}
#ft li
{
  background: rgb(22, 22, 22) !important;
  border-color: rgb(22, 22, 22) !important;
}
#ft li.fill
{
  display: none;
}
#logo
{
  display: none;
}
#doc
{
  margin-top: 10px;
}

#disclaimer-dialog
{
  background-color: rgb(22, 22, 22);
  color: #eee;
  border-color: #333;
}

#quickReply
{
  background-color: #222;
  border-color: #333;
}
#quickReply #qrHeader
{
  background-color: #222;
  color: #eee;
  border-color: rgb(22, 22, 22);
}

.boxcontent dt
{
  color: #bee;
}
.boxcontent dd
{
  border-color: #333;
}
.boxcontent dd span
{
  color: #789922 !important;
}

.extPanel
{
  background-color: rgb(22, 22, 22) !important;
  border-color: rgb(22, 22, 22) !important;
}
.extPanel a
{
  color: #aae !important;
}
}
