css:
@-moz-document domain("strava.com")
{
body{
	font-size: 12px;
    background-color: #f5f5fa;
	}

	.promo {
        margin-bottom: 0;
        height: 0;
        visibility: hidden;
    }
    .col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
        padding-right: 0px;
    }
    .feed-entry .entry-media.entry-outset:first-child {
        margin-bottom: 0;
    }
    .feed-entry .entry-block:last-of-type, .feed-entry .entry-header:last-of-type, .feed-entry .entry-body:last-of-type, .feed-entry .entry-media:last-of-type, .feed-entry .entry-footer:last-of-type {
        margin-bottom: 16;
    }
    .feed-entry .entry-block, .feed-entry .entry-header, .feed-entry .entry-body, .feed-entry .entry-media, .feed-entry .entry-footer {
        margin-top: 0;
    }
    .feed-entry .entry-media.entry-outset {
        margin-bottom: 0;
        height: 0;
        visibility: hidden;
    }
	.global-nav {
	    font-size: 12px;
	}
	.card .card-body, .card .card-footer {
	    padding: 10px;
	}
	.feed-entry .entry-block:first-of-type, .feed-entry .entry-header:first-of-type, .feed-entry .entry-body:first-of-type, .feed-entry .entry-media:first-of-type, .feed-entry .entry-footer:first-of-type {
	    padding-top: 10px;
	}
	.feed-entry .entry-header, .feed-entry .entry-body, .feed-entry .entry-media, .feed-entry .entry-footer {
	    padding-left: 10px;
	    padding-right: 10px;
	}
	.feed h3 {
    	font-size: 14px;
	}
	h5, .h5 {
	    font-size: 14px;
	}
	#dashboard-sidebar .section .section-title {
	    font-size: 14px;
	}
	.feed-entry .entry-header .avatar, .feed-entry .entry-header .avatar .avatar-img {
	    width: 70px;
	    height: 70px;
	}
	.feed .group-activity .list-entries .avatar img {
	    height: 62px;
	    width: 62px;
	}
	.stat {
    	font-size: 14px;
	}
	#dashboard-sidebar .section .media .media-title {
    	font-size: 12px;
	}
	#progress-goals .upsell {
	    visibility: hidden;
	    height: 0;
        padding: 0;
	}
	#upcoming-goals {
	    font-size: 12px;
	}
	#system-messages-js {
	    margin-top: 52px;
	}
    .feed-header {
        padding-top: 0;
        padding-bottom: 0;
		height: 40px;
    }
	#athlete-profile {
    	padding-top: 10px;
    	margin-top: 48px;
	}
    #dashboard-sidebar .section {
        padding-top: 0;
        padding-bottom: 0;
    }
    .card {
        margin-top: 7px;
        margin-bottom: 0px;
    }
    #explore-strava {
        visibility: hidden;
    }
    #your-challenges {
        height: 0;
        visibility: hidden;
    }
    #explore-strava{
        height:0;
    }
	#dashboard-sidebar .section:first-child {
	    margin-top: 33px;
	}
	.section {
		border-radius: 4px;
    	border-top: 0px solid #e6e6eb;
    	background: white;
	}
	#dashboard-sidebar .section {
	    padding-left: 10px;
	}
	.sidebar-footer {
    	visibility: hidden;
	}
	#dashboard-sidebar .section {
	    margin-bottom: 7px;
	}
	section.with-border, .section.with-border {
    	border: 0px solid #ccccd1;
    	background: white;                                            
	}
	#heading header {
    	background: white;
		border-top-left-radius: 4px;
        border-top-right-radius: 4px;                                            
	}
	section header, section .header, .section header, .section .header {
    	border-top: 0px solid #FFF;
	}
	.pagenav>li {
    	background: white;
	}
	.actions-menu>.button, .actions-menu>.slide-menu .selection {
	    background-color: white;
	}
	.global-nav .selection {
	    font-size: 12px;
	}
	.activity-stats .section {
		border-radius: 0px;
	}
	.activity-stats .secondary-stats.inline-stats {
	    background-color: white;
     	border-bottom: 1px solid #ccccd1;
    	margin: 0px;
	}


	#trophy-case-summary {	
    	padding-left: 15px;
	}
	.athlete-achievements {	
    	padding-left: 15px;
	}
	#athlete-recent-photos {	
    	padding-left: 15px;
	}
	#activity-log {
    	padding-left: 15px;
    	padding-right: 15px;
	}
	footer {
	    visibility: hidden;
	}
	.row .offset1 {
	    margin-left: 0%;
	}
	.row .spans10 {
	    width: 65%;
	}
	.row .spans5 {
	    width: 35%;
	}
	section, .section {
	    padding: 0px 15px;
	    position: relative;
	}
.feed .group-activity .group-activity-icon {
    height: 62px;
    width: 62px;
}

.feed-entry .media-left, .feed-entry .media-actions {
    width: 70px;
}
}
