@-moz-document regexp("^https?\\:\\/\\/.*") {

/* action toggle */
.c-action-toggle::after {
    content: "Action toggle" !Important;
    background: #FFFFEB !important;
    color: #000 !important;
    border: 1px solid yellow !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    font-size: 10px;
    font-family: inherit !important;
    display: block !important;
    line-height: 1.2 !important;
}
.c-action-toggle {
    border: 1px solid yellow !important;
    box-shadow: 0 0 0 1px #000;
}
/* action trigger */
/* doesn't fit in element
.c-action-trigger::before {
    content: "Action trigger" !Important;
    background: #FFFFEB !important;
    color: #000 !important;
    border: 1px solid yellow !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    font-size: 10px;
    font-family: inherit !important;
    display: block !important;
    line-height: 1.2 !important;
}
.c-action-trigger {
    border: 1px solid yellow !important;
    box-shadow: 0 0 0 1px #000;
}
*/
/* additional information */

m-additional-information::before {
    content: "MWF Additional info" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px;
    font-size: 12px;
}
/* ambient video */

.m-ambient-video:before {
    content: "Ambient video" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    position: absolute !important;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.m-ambient-video {
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* pagination */

.m-pagination:before {
    content: "pagination" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    position: absolute !important;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.m-pagination {
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* auto suggest */

.m-auto-suggest::before {
    content: "Auto suggest" !Important;
    background: #FFFFEB !important;
    color: #000 !important;
    border: 1px solid #FF9440 !important;
    padding: 0 2px;
    font-size: 10px;
    font-family: inherit;
    display: block;
}
.m-auto-suggest {
    border: 1px dashed #FF9440 !important;
}
/* MWF Label */

.c-label::before {
    content: "MWF Label: for=" attr(for) !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px;
    font-size: 12px;
}
/*
.c-password::before {
    content: "MWF Password: id=" attr(id) !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px;
    font-size: 12px;
    position:absolute !important;
}
*/
/* call to action  */

.c-call-to-action::before {
    content: "Call to action" !Important;
    background: #FFFFEB !important;
    color: #000 !important;
    border: 1px solid #FF9440 !important;
    padding: 0 2px !important;
    font-size: 10px !important;
    font-family: 'Segoe UI' !important;
    font-weight: normal !important;
    display: block;
    max-width: 80px;
}
.c-call-to-action {
    border: 1px solid #FF9440 !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* mwf Card group */

.m-card-group::before {
    content: "Card group" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px;
    font-size: 12px;
}
.m-card-group {
    border: 1px dashed lime !important;
}
/* mwf carousel */

.c-carousel.f-multi-slide::before {
    content: "Carousel" !important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid blue !important;
    position: absolute;
    z-index: 200;
    padding: 0 2px;
    font-size: 12px;
    left: 80px;
}
.c-carousel.f-single-slide::before {
    content: "Carousel Single Slide" !important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid blue !important;
    position: absolute;
    z-index: 200;
    padding: 0 2px;
    font-size: 12px;
    left: 80px;
}
.c-carousel.f-multi-slide,
.c-carousel.f-single-slide {
    border: 1px dashed lime !important;
}
/* choice summary */

.c-choice-summary::before {
    content: "Choice summary" !Important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid lime !important;
    box-shadow: 0 0 0 1px #000;
    position: absolute !important;
    top: -14px !important;
    padding: 0 2px;
    font-size: 12px;
}
.c-choice-summary {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000;
}
/* Combo  */

.c-combo::before {
    content: "Combo" !Important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid red !important;
    position: absolute !important;
    top: -14px !important;
    padding: 0 2px;
    font-size: 12px;
}
.c-combo {
    border: 1px dashed red !important;
}
/* Content placement */

.m-content-placement::before {
    content: "Content placement" !important;
    background: #000 !important;
    color: #fff !important;
    border: 1px dashed lime !important;
    position: absolute;
    font-size: 13px;
    font-weight: normal;
    padding: 0 2px;
    top: 0;
    width: 130px;
    box-shadow: 0 0 0 1px #000 !important;
}
.m-content-placement {
    border: 2px dotted lime !important;
    box-shadow: 0 0 0 1px #000 !important;
}

.m-content-placement-item::before {
    content: "Content placement item" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px dotted #000 !important;
    box-shadow: 0 0 0 1px yellow !important;
    font-size: 13px;
    font-weight: normal;
    padding: 0 2px;
    width: 170px;
}
.m-content-placement-item {
    border: 2px dotted #000 !important;
    box-shadow: 0 0 0 1px yellow !important;
}
/* Content toggle */

.c-content-toggle::before {
    content: "Content toggle" !important;
    background: #fff !important;
    font-size: 12px;
    color: #000 !important;
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000 !important;
}
.c-content-toggle {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Dialog */

.c-dialog::before {
    content: "Dialog" !Important;
    background: yellow !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px;
    font-size: 12px;
}
.c-dialog {
    border: 1px dashed yellow !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Group  */

.c-group::before {
    content: "Content group" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    position: absolute !important;
    top: 0;
    left: 0;
    z-index: 200;
    font-size: 12px;
    padding: 0 2px;
}
.c-group {
    border: 1px solid lime !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Hero item */

.m-hero-item::after {
    content: "Hero item" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    position: absolute !important;
    left: calc(50% - 50px);
    top: 10px !Important;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.m-hero-item {
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Histogram */

.m-histogram::before {
    content: "Histogram" !Important;
    background: #FFFFEB !important;
    color: #000 !important;
    border: 1px solid #FF9440 !important;
    padding: 0 2px;
    font-size: 10px;
    font-family: inherit;
    display: block;
}
.m-histogram {
    border: 1px dashed #FF9440 !important;
}
/* Hyperlink group */

.m-hyperlink-group-content-placement::before {
    content: "Hyperlink group: content placement" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    position: absolute !important;
    top: 0;
    z-index: 200;
    font-size: 12px;
}
.m-hyperlink-group-content-placement {
    border: 1px dashed lime !important;
}
.m-hyperlink-group::before {
    content: "Hyperlink group" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid blue !important;
    position: absolute !important;
    z-index: 200;
    font-size: 12px;
}
.m-hyperlink-group {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000;
}
.m-mosaic .m-hyperlink-group::before {
    content: "Hyperlink group: mosaic" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    position: absolute !important;
    z-index: 200;
    font-size: 12px;
}
.m-mosaic .m-hyperlink-group {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000;
}
/* Immersive hero */

.m-immersive-hero::before {
    content: "Immersive hero" !important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px !important;
}
.m-immersive-hero {
    border: 1px dashed lime !important;
}
/* Immersive hero item */

.m-immersive-hero-item::before {
    content: "Immersive hero item" !important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px !important;
}
.m-immersive-hero-item {
    border: 1px dashed lime !important;
}
/* Media gallery */

.m-media-gallery::before {
    content: "Media gallery" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px !important;
}
.m-media-gallery {
    border: 1px dashed lime !important;
}
/* Multi feature */

.m-multi-feature:before {
    content: "Multi feature" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px dotted #00C0C0 !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.m-multi-feature {
    border: 2px dotted #00C0C0 !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Persona */

div[class^="m-persona"]::before {
    content: "MWF Persona" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
}
/* MWF Pivot */

.c-pivot::before {
    content: "MWF Pivot" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid red !important;
    position: absolute !important;
    padding: 0 2px !important;
    font-size: 12px !important;
    top: -10px;
    z-index: 200;
}
.c-pivot {
    border: 1px dashed red !important;
}
/* Radio  */

.c-radio::before {
    content: "Radio" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid lime !important;
    box-shadow: 0 0 0 1px #000;
    padding: 0 2px !important;
    font-size: 12px !important;
    line-height: 1.2;
    z-index: 200;
}
.c-radio {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000;
}
/* Rating */

.c-rating.f-interactive::before {
    content: "MWF Rating" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
}
/* Refine menu  */

.c-refine-menu::before {
    content: "Refine menu" !Important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid blue !important;
    position: absolute !important;
    padding: 0 2px;
    font-size: 12px;
}
.c-refine-menu {
    border: 1px dotted blue !important;
}
/* search results */

.m-search-results::before {
    content: "Search results" !Important;
    background: yellow !important;
    color: #000 !important;
    border: 1px solid yellow !important;
    box-shadow: 0 0 0 1px #000;
    position: absolute !important;
    padding: 0 2px;
    font-size: 12px;
}
.m-search-results {
    border: 1px dotted aqua !important;
    box-shadow: 0 0 0 1px #000;
}
.m-search-results .f-result-item::before {
    content: "Result item" !Important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid aqua !important;
    box-shadow: 0 0 0 1px #000;
    padding: 0 2px;
    font-size: 12px;
}
.m-search-results .f-result-item {
    border: 2px dotted aqua !important;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .4);
}
/* end search results */
/* Select */

.c-select::before {
    content: "Select" !Important;
    background: #EEFFE6 !important;
    color: #000 !important;
    border: 1px solid green !important;
    padding: 0 2px;
    font-size: 12px;
}
.c-select {
    border: 1px dashed green !important;
}
/* Select button */

.c-select-button::before {
    content: "Select button" !important;
    background: #ddeeff !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    padding: 0 2px;
    font-size: 12px;
}
.m-content-placement {
    border: 1px dashed lime !important;
}
/* Sequence indicator  */

.c-sequence-indicator:before {
    content: "Sequence indicator" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.c-sequence-indicator {
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
}
/* Social */

.m-social::before {
    content: "Social" !Important;
    background: #EEFFE6 !important;
    color: #000 !important;
    border: 1px solid green !important;
    box-shadow: 0 0 0 1px #fff !important;
    padding: 0 2px;
    position: relative;
    font-size: 12px;
    top: -22px;
    z-index: 200;
}
.m-social {
    border: 1px solid green !important;
    box-shadow: 0 0 0 1px #fff !important;
}
/* MWF Supplemental nav  */

.m-supplemental-nav::before {
    content: "Supplemental nav" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid #000 !important;
    position: absolute !important;
}
.m-supplemental-nav {
    border: 1px dashed lime !important;
}
/* MWF Table */

.c-table:before {
    content: "MWF Table" !important;
    background: #fff !important;
    color: #000 !important;
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
    padding: 0 2px !important;
    position: relative;
    top: -2px;
    font-size: 12px !important;
    font-weight: normal !Important;
}
.c-table {
    border: 2px solid #00FFFF !important;
    box-shadow: 0 0 0 1px #000 !important;
}
.c-table caption:before {
    content: "caption element";
    background: #fff !important;
    color: #000 !important;
    box-shadow: 0 0 0 1px #000 !important;
    font-size: 12px !important;
    font-weight: normal !Important;
    padding: 0 2px !important;
    margin-right: 2px;
}
.c-table th[scope="col"]::before {
    content: "th (scope=col)" !important;
    font-size: 12px !important;
    font-weight: normal !Important;
    text-align: center;
    background-color: #efefef;
    display: block;
}
.c-table th[scope="row"]::before {
    content: "th (scope=row)" !important;
    font-size: 12px !important;
    font-weight: normal !Important;
    color: #000;
    background-color: #efefef;
    display: block;
}
.c-table th button {
    background: yellow !important;
}
.c-table th button::before {
    content: "button: " !important;
}
/* Typographic info */
.m-typographic-intro::before {
    content: "Typographic info" !Important;
    background: #fff !important;
    color: #000 !important;
    border: 1px solid #fff !important;
    box-shadow: 0 0 0 1px #000;
    padding: 0 2px !important;
    font-size: 12px !important;
    line-height: 1.2;
    z-index: 200;
}
.m-typographic-intro {
    border: 1px dashed #fff !important;
    box-shadow: 0 0 0 1px #000;
}

/* Toggle */

.c-toggle::before {
    content: "Toggle" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid lime !important;
    box-shadow: 0 0 0 1px #000;
    padding: 0 2px !important;
    font-size: 12px !important;
    line-height: 1.2;
    z-index: 200;
}
/*.c-toggle {
    border: 1px dashed lime !important;
    box-shadow: 0 0 0 1px #000;
}*/
/* Vid player OLD */

.c-video-player::before {
    content: "Vid player OLD" !Important;
    background: #FFFF80 !important;
    color: #000 !important;
    border: 1px solid lime !important;
    box-shadow: 0 0 0 1px #000;
    padding: 0 2px !important;
    font-size: 12px !important;
    font-weight: normal !Important;
    line-height: 1.2;
    z-index: 200;
    position: absolute;
}
.c-video-player {
    border: 2px dashed blue !important;
    box-shadow: 0 0 0 1px #000;
}

[class*=product-placement-item]::before{
    content: "core-ui-product-placement-item" !important;
    background: blue !important;
    color: #ffffff !important;
    padding: 0 2px !important;
    font-size: 12px !important;
    font-weight: normal !Important;
    display: block;
    word-wrap: break-word;
    max-width: 100%;
}
[class*=product-placement-item]{
    border:2px solid blue !Important;
    
}


}
