css:
@-moz-document domain("yaplakal.com")
{
body, table, tbody, td, tr  {
	background: #333 !important;
	color: #aaa !important;}


/* Menu */
#main-menu a {
	color: #dfdf00 !important;
}

/* Form elements */
.forminput, .textinput {
    background-color: #cacaca !important;
}


#header-container {
    background-color: black;
}

#user-box .user-info {
    background-color: #000;
}

#footer-inner {
    color: #fff;
    text-align: left;
    background-color: black;}

#scrollTop {
    font-weight: bold;
    box-shadow: 2px 0px 9px 7px black;
    background: transparent url(http://www.animatedimages.org/data/media/111/animated-arrow-image-0138.gif) 98% 0% no-repeat;}

.subtitle {
    color: #8ec413 !important;}

.holder .icon-user {
    background-position: 0 -4px;
    text-shadow: 1px 1px 1px #37b8ed;}

.postdetails {
    font-size: 10px;
    text-shadow: 0 0 2px black;}

.row4 {
    background: #5e5e5e !important;
    text-shadow: 0 0 9px black;}

.mainpage a, .mainpage a:link, .mainpage a:visited, .subpage a, .subpage a:link, .subpage a:visited {
    text-shadow: 0 0 5px black;}

.postcolor {
    max-width: 100%;}

.tablepad {
    background-color: #333333;
    padding: 6px;}

body:not(#id) {
    background-color: #000 !important;}

#tabs a.active, #tabs a:hover {
    color: #000;
    background-color: #333333 !important;}
#tabs a {background-color: transparent !important;}
.std-input {background-color: #767676;}
.desc, .desc a {color: #ccc;}

.wcommunity_wrap {
    background: #00000026 !important;}

td#in-feed-2 {
    display: none;}
td#in-feed-1 {
    display: none;}

a#clk-rt {
    background: none;
    display: none;}

a#clk-lt {
    background: none;
    display: none;}

#ipbwrapper {
    background-color: #333 !important;}

#tabs a.active, #tabs a:hover {
    color: #f00;}

a:link, a:visited, a:active {
    text-decoration: none;
    color: #5b88c9;}

h4 {color: #f00;}

.wcommunity_wrap {
    background: #222 !important;}

._2lqg {
    background: #333 !important;
    border: none;}

._h7n {
    background: #222;}

._517h, ._59pe:focus, ._59pe:hover {
    background-color: #222;}

.darkrow3 {
    background-color: transparent;}

.post-rank {background-color: transparent;}

.darkrow1 {
    background-color: #333;
}

.wcommunity_wrap {
    background: #222 !important;}

}
