@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("onvasortir.com"), domain("urbeez.com") {

  body, #divbody
  {
    width:1500px;
    margin:0px auto 0px !important;;
    background-image: none !important;;
  }

  #debutpage, .Site
  {
  /* width:1200px;*/

   width: 100% !important;
  /* max-width: 100% !important;*/

  }

  /* taille des menus et des barres de titres */
  .SiteColC, .legende, .PADentete_corps, .Event_Line, .Event_LineMois, .Event_LineJour
  {
  width: 720px;
  }

  /* bloc membre en ligne */
  .SiteColD, .SiteColD div
  {
    float:initial;
    margin-left:0px;
  }
  
  
  .titrepage, .encadrage, .corpsMax, .titreprofil
  {
    width:698px;
  }
  
  .titreMax
  {
    width:680px;
  }
  
 div.titreComplement300Profil
  {
    width:330px;
  }
  
  div.titre300, div.corps300, div.titreComplement300, div.corpsComplement300
  {
    width:330px;
    min-height: 3em;
  }  
  
  .Event_TitleTR, .Event_Title
  {
    width:380px;
  }
  
  .Event_OrgaTR, .Event_Orga
  {
    width:200px;
    
  }
  
  /* Boite d'édition du message de profil, Boite d'édition du pense bete */
  div#mceu_9.mce-tinymce, .expanding
  {
    width:700px !important;
  }
  
  /* Boite d'édition du message en bas du profil */
  textarea#Message
  {
    max-width:550px;
  }
  
  /* taille de la carte */
  #map
  {
    width:720px !important;
    height:650px !important;
  }
  
  /* taille de la case pour entrer l'URL de la video de profil */
  #Dailymotion
  {
    width:200px;
  }
  
  /***************** Captcha   ***************/
  
  div[class^="rc-anchor"]
  {
    display:inline-block;
  }
  
  
  
  /****** DGRP qui se réaffiche à chaque visite pour rien *****/

 /* ne fonctionne pas
  div[class^="qc-cmp"]
  {
    display:none !important;
    height:0px !important;
    width:0px !important;
  }
  */
  
  /***************** publicites **************/
  
  /* interdit tous les iframes publicitaire, mais autorisé pour video de profile, commentaires, etc.) */
  
/*  iframe[class="x-adloox"], iframe[id^="sublime"] , iframe[src^="https://secure-assets.rubiconproject.com/"]  */
  iframe 
  {
    display:none !important;
   /* height:0px !important; */
  }
    
  /* autorise ces iframes */
  iframe[src*="youtube"], iframe[id^="DescriptionProfil_"], iframe[src*="captcha"]
  {
    display:block !important;
  }
  
  /* Pub sur l'entete du site */
  .ak-suit-header-anchor
  {
    display:none !important;
    height:0px !important;
  }
   
  
  /* toutes les pubs commencent par ak_  */
  div[class^="ak_"]
  {
    display:none !important;
    height:0px !important;
    width:0px !important;
  }
  
  /* toutes les pubs commencent par pub_  */
  div[id^="pub_"]
  {
    display:none !important;
    height:0px !important;
    width:0px !important;
  }
  
  
  
}
