css:
@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
.maincontent {
    background-color: #121212;
    border: none;
}
p,
td,
table.list td#view,
td#viewclose,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #A4A4A4;
}
table.list td#view,
table.list td#viewclose,
td#viewclose,
table.list td,
table.list,
table.list th {
    background: #121212;
    border: none;
    font-size: 12px;
}

table.list th {
    font-size: 16px;
    color: #cccccc;
}

div.important {
    border: none;
    background-color: #121212;
    background-image: url(data:image/png;base64,);
    color: #A4A4A4;
}
div.information {
    border: none;
    background-color: #121212;
    color: #A4A4A4;
}
th#viewclose,
table.list th#viewclose {
    background-color: #121212;
    border: none;
}
.buttonscontainer {
    background-color: #1c1c1c;
    box-shadow: 0.25px 0 10px black;
    z-index: -1;
}
.buttons a,
.buttons a.layer3 {
    color: #A4A4A4;
    font-size: 16px;
}
html {
    background-color: #121212;
}
#nav-menu ul li {
    background-color: #2971AD;
    border-radius: 1px;
    margin: 1px;
}
#nav-menu {
    top: 11px;
}
#nav-menu ul li a {
    color: #E5E5E5;
    font-size: 25px;
    font-family: Bahnschrift SemiBold;
    font-weight: 1000;
}
#nav-menu ul li:hover a,
#nav-menu ul li#aktuell a {
    color: white;
}
button,
input,
optgroup,
select,
textarea {
    background-color: #121212 !important;
    color: #A4A4A4;
    border-radius: 1px !important;
    border: #A4A4A4 1px solid;
}
a.linkbtn,
a.btnlink {
    background-color: #0a0a0a;
    border-radius: 1px;
}
caption {
    color: #A4A4A4;
}
h2,
h3.tabletitle,
h3,
h4 {
    border-left: 5px solid #2971AD;
    border-bottom: none;
    font-family: Bahnschrift SemiBold;
    font-size: 18px;
    font-weight: 1000;
    color: #CCCCCC;
}
h2,
h3 {
    font-size: 22px;
}
.btnsubmit,
.btnreset {
    color: #a4a4a4;
    border-width: 1px;
    border-radius: 0;
    -webkit-border-radius: 0;
    margin-top: 8px;
}
span.pdf_output {
    background-color: #121212;
    border-radius: 0;
    border-width: 1px;
}
.titelleiste {
    box-shadow: 0 1.25px 11px black;
    background-image: url(data:image/png;base64,);
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=21411") {
td {
    background-color: #121212;
    color: #A4A4A4;
}
#greenft {
    color: #15B500;
}
#redft {
    color: #D3002D;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=22326"), url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=90001"), url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=22108"), url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=21311") {
tr {
    background: #121212;
    border-style: solid;
    border-width: 0px;
}
tbody {
    border-width: 0px;
    border-color: black;
}
table.list td#view,
table.list td#viewclose,
td#viewclose,
table.list td,
table.list {
    border-width: 0px;
    background-color: #121212;
    font-size: 12px;
}
.auswahlbuttons {
    background-color: #1c1c1c;
}
.auswahlbuttons a {
    color: #A4A4A4;
}
span.filter {
    color: #A4A4A4;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/") {
.dhx_cal_container,
.dhtmlx_modal_box *,
.dhx_cal_data,
.dhx_cal_data div,
.dhx_cal_data table *,
.dhx_cal_header,
.dhx_cal_header div,
.dhx_cal_light .dhx_cal_lsection,
.dhx_cal_light .dhx_wrap_section,
.dhx_cal_navline div,
.dhx_cal_quick_info,
.dhx_cal_quick_info div,
.dhx_multi_day,
.dhx_multi_day div,
.dhx_tooltip_line div,
.dhx_scale_bar,
.dhx_scale_holder,
.dhx_scale_holder_now {
    background: #121212 !important;
    color: #A4A4A4;
    border-color: #0F0F0F;
}
dhx_scale_holder {
    border-top: none;
    border-bottom: none;
    border-left: 5px #0F0F0F solid;
    border-right: none;
}
div.dhx_cal_event.stpt_event {
    background: #1c1c1c;
    border: none;
}
.maincontent {
    color: #A4A4A4;
}
.auswahlbuttons {
    background-color: #1c1c1c;
}
.auswahlbuttons a {
    color: #A4A4A4;
}
.stpt_event_text {
    color: #A4A4A4;
    background: #1c1c1c;
}
.stpt_event_date {
    color: #00B0C4;
    background: #1c1c1c;
}
.dhx_cal_event.stpt_event_strike {
    border-color: #FF6600;
    border-style: dashed;
}
.dhx_cal_tab.active,
.dhx_cal_tab.dhx_cal_tab_first,
.dhx_cal_tab.dhx_cal_tab_last {
    color: #a4a4a4;
    border: 1px solid #0F0F0F !important;
    border-radius: 0;
    text-shadow: none;
}
.dhx_scale_bar,
dhx_cal_header {
    background: #1c1c1c !important;
}
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/loginto.php") {
input[type="submit"],
input#user.textinput,
input#passwort.textinput,
input:-webkit-autofill,
textarea:-webkit-autofill,
select:-webkit-autofill,
input:not([type]),
input[type="email"],
input[type="number"],
input[type="password"],
input[type="tel"],
input[type="url"],
input[type="text"] {
    background: rgba(0, 0, 0, 0.4) !important;
    border: none;
    height: 35px;
    font-size: 25px;
    margin-top: 20px;
    margin-left: 10px;
}
#loginpanel {
    height: 200px;
    width: 400px;
    background-color: rgba(0, 0, 0, 0.7);
}
input[type="submit"] {
    margin-top: 20px;
    margin-left: 90px;
    height: 50px;
    width: 100px;
}
label {
    margin-left: 10px;
    font-size: 25px;
    font-family: Bahnschrift SemiBold;
}
#loginfooter {
    background-color: rgba(0, 0, 0, 0.4);
    color: #a4a4a4;
}
#loginheader {
    background-color: rgba(0, 0, 0, 0.4);
    background-image: url(data:image/png;base64,);
}



/* i really want this to be global */
}

@-moz-document url-prefix("https://www.schul-netz.com/altekanti/index.php?pageid=22500") {
.formgroup {
    background: #121212;
    box-shadow: none;
    border: none;
    border-radius: 0px;
}
input#f1,
input#f2,
input#f3,
input#f4 {
    border: #A4A4A4 1px solid;
}
span.fragezeichen {
    color: #618ac0;
}
#linkbtn,
#btnlink {
    background-color: #121212;
    color: #a4a4a4;
    border: 1px #618ac0 solid;
    border-radius: 0;
}
}
