css:
@-moz-document url-prefix("https://www.w3schools.com/") {
html, body {
    background: #121212!important;
    border-color: #464d54!important;
}
#codecontainer,
.searchinput {
    border-color: #464d54!important;
}

canvas {
    filter: invert(70);
}
p,
span {
    color: #aaa!important;
}
/* 'w3schools' */
body > div.w3-container.top > a {
    background-color: #121212!important;
    color: #CCC !important;
}
/* '.com' */
body > div.w3-container.top > a > span {
    color: #A6E22E;
}
@media (min-width: 993px) {
    div.w3-right {
        color: #CCC !important;
    }
}
#htmltags .w3-table-all td:first-child {
    background: #182122;
}
.w3-table td, .w3-table th, .w3-table-all td, .w3-table-all th {
     background: #182122;
}
table.grid td,
.test [class*="col-"] {
    background-color: #182122;
    border: 1px solid #6b6b6b;
}
#sidenav a.active {
    background-color: #064067;
    color: #ffffff;
}
.smallbtn,
.bigbtn {
    border: 1px solid #313030;
	background-color: #1f1f1f;
}
.w3-light-grey, .w3-example, .w3-sidenav, .w3-container.w3-padding-large {
    background-color: #182122 !important;
}
#tryitLeaderboard {
    background-color: #222!important;
}
.w3-theme {
    color: #fff !important;
    background-color: #453a56 !important;
}
.w3-pale-green,
.w3-hover-pale-green:hover {
    color: #c2c2c2!important;
    background-color: #262626!important;
}
.w3-pale-red,
.w3-hover-pale-red:hover {
    color: #c2c2c2!important;
    background-color: #262626!important;
}
.w3-sidebar {
    background-color: #262626!important;
}
.w3-codespan {
    color: #b28fff;
    background-color: #232629;
}
.w3-note {
    background-color: #484846;
    border-left: 6px solid #218ad0;
}
.w3-white,
.w3-hover-white:hover {
    color: #e2e2e2!important;
}
searchinput {
    border: 1px solid #4c4c4c;
}
#sidenav a:hover, #sidenav a:focus {
    background-color: #455a64;
}
input {
    color: #aaa!important;
    background-color: #1a1a1a;
}
.w3-sand,
.w3-hover-sand:hover {
    color: #d8d8d8!important;
    background-color: #464544!important;
}
.w3-green,
.w3-hover-green:hover {
    color: #fff!important;
    background-color: #385639!important;
}
#main > div > a.w3-btn {
    color: #fff!important;
    background-color: #1c689a!important;
    border-color: #218ad0!important;
}
.w3-info {
    background-color: #232629;
    border-left: 6px solid #218ad0;
}
.w3-theme-border {
    border-color: #218ad0 !important;
}
hr {
    border: 0;
    border-top: 1px solid #505050;
    margin: 20px 0;
}
.w3-ul li {
    padding: 8px 16px;
    border-bottom: 1px solid #4e4d52;
}
.w3-border {
    border: 1px solid #4e4d52!important;
}
.w3-light-grey,
.w3-hover-light-grey:hover,
.w3-light-gray,
.w3-hover-light-gray:hover {
    color: #fff!important;
    background-color: #1c689a!important;
}
.well {
    min-height: 20px;
    padding: 19px;
    margin-bottom: 20px;
    background-color: #182122;
    border: 1px solid #218ad0;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .05);
}
.w3-code {
    border-left: 4px solid #218ad0;
}
/* Site bg + inner tables */
body > div.w3-container.top,
.w3-white,
.w3-code.notranslate,
.w3-code.w3-border.notranslate,
.w3-row,
th,
#entercolor {
    background: #121212!important;
}
.w3-bordered tr,
.w3-table-all tr {
    border-bottom: 1px solid #4e4d52;
}
#lumtopcontainer > table > tbody > tr > td:nth-child(1),
#lumtopcontainer > table > tbody > tr > td:nth-child(3){
    background: #262626!important;
    border-color: #4e4d52!important;
}
.w3-table-all tr:nth-child(odd) {
    background-color: #222;
}
.w3-table-all tr:nth-child(even) {
    background-color: #333;
}
.w3-table, .w3-table-all {
    border-color: #343637!important;
}
#huecontainer > div > table > tbody > tr  > td:nth-child(2),
#huecontainer > div > table > tbody > tr  > td:nth-child(3),
#huecontainer > div > table > tbody > tr  > td:nth-child(4),
#huecontainer > div > table > tbody > tr  > td:nth-child(5){
     background: #262626!important;
    border-color: #4e4d52!important;
}
.w3-example {
    background: #121212;
}
/* Left sidebar bg + outer tables */
.w3-light-grey,
.w3-sidenav,
.w3-container.w3-padding-large {
    background-color: #182122 !important;
}
#leftmenuinnerinner > a.active {
    color: #e2e2e2 !important;
    background-color: #464646;
}
#leftmenuinnerinner > a:hover {
    color: #e2e2e2 !important;
    background-color: #464646;
}
/* Navbar bg */
#topnav > div:nth-child(1),
.w3-topnav {
    background-color: #181818;
    ;
}
.topnav .w3-bar a:focus {
    background-color: #1c689a!important;
    color: #ffffff !important;
}
.topnav .w3-bar a:hover {
    background-color: #2687c8!important;
    color: #ffffff !important;
}
/* Navbar hover bg */
#topnav > div:nth-child(1) > div > a.active {
    color: #ffffff !important;
    background-color: #1c689a;
}
table.browserref th {
    height: 32px;
    border: 1px solid #4e4d52;
}
input[type=text] {
    color: #ddd!important;
    background: #232629!important;
    border: 2px solid #464646;
}
/* Site text + in tables + buttons */
div#main > p,
td,
th,
li,
[id*="color"],
#main > div > a.w3-btn,
a.w3-btn.w3-theme.w3-margin-0,
div#topnav > div > ul.w3-navbar.w3-left > li > a.active,
body > div.w3-container.top > div,
#main > div > div > span,
.w3-code.notranslate,
a.w3-btn.w3-light-grey.w3-hide-small.w3-hide-medium.w3-left.w3-padding,
#main > div > span:nth-child(1),
.w3-text-dark-grey,
.w3-hover-text-dark-grey:hover,
.w3-text-dark-gray,
.w3-hover-text-dark-gray:hover,
.w3-light-grey,
.w3-hover-light-grey:hover,
.w3-light-gray,
.w3-hover-light-gray:hover {
    color: #ccc !important;
}
/* Headings (beige) */
span,
h1,
h2,
h3,
h4,
h5,
h6,
#sidemenu > h2 {
    color: #74bfe6;
}
/* Table text */
td,
#main > div.w3-example > p {
    color: #ccc;
}
/* Prevus + Next btns */
#main > div > a.w3-btn {
    background: #4CAF50;
    border: none;
}
.w3-button:hover {
    color: #fff!important;
    background-color: #ce3232!important;
}
div#main > p,
th,
li,
[id*="color"],
#main > div > a.w3-btn,
a.w3-btn.w3-theme.w3-margin-0,
div#topnav > div > ul.w3-navbar.w3-left > li > a.active,
body > div.w3-container.top > div,
#main > div > div > span,
.w3-code.notranslate,
a.w3-btn.w3-light-grey.w3-hide-small.w3-hide-medium.w3-left.w3-padding,
#main > div > span:nth-child(1),
.w3-text-dark-grey,
.w3-hover-text-dark-grey:hover,
.w3-text-dark-gray,
.w3-hover-text-dark-gray:hover,
.w3-light-grey,
.w3-hover-light-grey:hover,
.w3-light-gray,
.w3-hover-light-gray:hover {
    color: #ccc !important;
}
/* Links */
a {
    color: #66D9EF !important;
}
a:visited {
    color: #139db9 !important;
}
.w3-margin-bottom {
    margin-bottom: 0px!important;
    background: #121212!important;
}
a:hover {
    color: #A6E22E !important;
}
.w3-dark-grey, .w3-hover-dark-grey:hover, .w3-dark-gray, .w3-hover-dark-gray:hover {
    color: #fff!important;
    background-color: #9a2222!important;
}
body > ul.w3-navbar.w3-theme.w3-card-2.w3-wide > li > a.w3-hover-white.w3-padding-16 {
    color: #fff !important;
}
/* Table links (dark blue) */
#main > table > tbody > tr > td > a {
    color: #16b1d0 !important;
}
/* Table links (dark blue) */
#main > table > tbody > tr > td > a:visited {
    color: #1189a2 !important;
}
#leftmenuinnerinner > a {
    color: red;
}
/* Editor  */
div#tryitLeaderboard,
div#container > div#textareacontainer,
div.CodeMirror.cm-s-default.CodeMirror-wrap,
div#iframecontainer > div#iframe,
iframe#iframeResult {
    background: #121212!important;
}
span.cm-tag {
    color: #d55d5d !important;
}
span.cm-tag.cm-bracket {
    color: #4CAF50 !important;
}
span.cm-string {
    color: #9999ff !important;
}
span.cm-variable,
span.cm-def {
    color: #a6a6a6 !important;
}
span.cm-property {
    color: #ff6666 !important;
}
tbody > tr > th,
tbody > tr > td {
     border-color: #5b5e64!important;
}

tbody > tr:nth-child(even) {
    background-color: #111!important;
   
}

tbody > tr:nth-child(odd) {
    background-color: #222!important;
}


/* Hide some things I dislike seeing */

/* Very simple ad hider */
[class="ad"],
[class *="ads"],
[class ^="ad-"],
[class ^="ad_"],
[class *="-ad-"],
[class $="-ad"],
[class $="_ad"],
[id *="_ad"]{
    display: none;
}
#ad-container {
    display: none;
}
[id="#ad-container"] {
    display: none;
}

/* Very simple 'nocontent' hider */
[class *="nocontent"],
.hidden {
    display: none;
}
.met-rule-660 p
/* Very simple popup hider */
[class *="popup"],
[class *="met-flyout met-rule-660"] {
    display: none;
}

/* Very simple sharing hider */
[class *="share"],
[class *="sharing"],
[class *="social"],
[class *="follow"],
[class *="newsletter"] {
    display: none;
}
}
