@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("facebook.com") {





	#tabs .activetab a, .updates {

		background: #606060 !important;

	}



	#tabs .inactivetab a:hover {

		background: #333 !important;

	}





	a:link {

		color: #9f9 !important;

		text-decoration: none !important;

	}

	a:visited {

		color: #8d8 !important;

		text-decoration: none !important;

	}

	a:hover, a:active {

		color: #fff !important;

		text-decoration: none !important;

	}

	#sidebar {

		background: transparent !important;

	}

	#sidebar a:hover {

		background: #333 !important;

	}

	#header h1 {

		color: #ccc !important;

		font-variant: small-caps !important;

	}



	.inputsearch {

		padding: 2px !important;

	}

	

	#content, #header { border: 0 !important }

	

	.flex_header,.flex_header * {

		background-color: #333 !important; 

		border-color:transparent !important;  

	}



	.flex_header { padding:2px 8px !important; }



	.flex_subheader, .flex_subheader *,

	.sidebar_item_header, .sidebar_item_header * { 

		border-color:transparent !important;  

		background-color: #555 !important; 

	}

	

	.wallheader, .album, .wallpost .wallinfo {

		border-color:transparent !important;  

	}



	.wallheader, .wallheader * {

		background-color: #555 !important; 

	}



	.typeahead_list .typeahead_selected,

	.typeahead_list .typeahead_selected * {

		background: #555 !important;

	}





	.typeahead_list .typeahead_selected em {

		background: #333 !important;

	}







	.typeahead_list .typeahead_not_selected em {

		background: #333 !important;

	}





	#tagframeinside, #userprofile,

	#tagframe, #pageheader *,.sharebox #shares,

	#group, .knob_holder, .slider_holder,

	.feedback .mixer_panel .sliders,

	#event.profile, .typeahead_list,

	#int_sharer #sharer_stage

	{ background: transparent !important }



	#tagframe, #frame {

		border-color: #333 !important;

		background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAACAAAAAgAgMAAAAOFJJnAAAADFBMVEX///8AAAD///////8Il07uAAAABHRSTlMAv7+dkHKCOQAAACRJREFUeF7NzjEBAAAIw7AiEpNTMWlwzAPk6llWGTiODhRPxgYfWh6XccoYxgAAAABJRU5ErkJggg==)		center no-repeat !important;

	}



	#frame { border: 1px solid #333 !important }







	html * {

		background-color: #444 !important;

		color: #fff !important;

		border-color:#555 !important;  

	}



	.inputsearch {

		background: #444 !important;		

	}



	.wall_post .quote, .share_media .owner_comment .text {

		background: transparent url(data:image/png;base64,)		no-repeat scroll left top !important;

	}



	.wall_post span.em, .share_media .owner_comment .text span {

		background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABQAAAAKCAMAAACDi47UAAAAM1BMVEX///8iIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiIiK/ar/wAAAAEHRSTlMAAH8wEK/fYL/PUCCPcEDv4IRRHgAAAEpJREFUeF59yDcCgEAMxEDkcInk/7+WK7dChYo5fuOu5kSrC8Gqmtj+Emxb6XsmeNoo6KYIZAJerviUAyMRXG+HyBmCMRwwA0HtA//qAigzJsS+AAAAAElFTkSuQmCC)		no-repeat scroll right bottom !important;

	}





	#pageheader {

	background: #444 url(data:image/png;base64,)	!important

	}



}
