css:
@-moz-document domain("gocd.cd.vendavo.com") {
.pipeline_bundle.pipeline_group, .page_header, .application_nav, #header, #yui-main, div.yui-b, #pipeline-history, .container-in-body, .pipeline-info, .pipeline-name, .pipeline-run, .spacer, td, th, input, div.content_wrapper_inner_pipeline_history, table, tr, .row, .pipeline_history_table_container, div.wrapper, #body_content, svg, .page-wrap, #page_links, .pagination, #vsm-container, #canvas, .highlight, .dashboard, body {
    background-color: #000000!important;
}
.page_header, .application_nav, #header, #yui-main, div.yui-b, #pipeline-history, .container-in-body, .pipeline-info, .pipeline-name, .pipeline-run, .spacer, td, th, input, div.content_wrapper_inner_pipeline_history, table, tr, .row, .pipeline_history_table_container, div.wrapper, #body_content {
    background-color: transparent!important;
}
.pipeline, .pipeline_header, .pipeline_instance, .pipeline_operations, .actions, .vsm-entity, .material, .git, .material_revisions_label, .message.waiting {
    background-color: #202020!important;
}
.page_header, #header, .pipeline_bundle, .pipeline_group, .pipelines, .content_wrapper_outer, .content_wrapper_inner {
    border: 0px solid #404040!important;
}
.pipeline_operations, .pipeline_header, .pipeline-group_title {
    border: 0px solid black!important;
    display:none;
}
span, h1, h2, h3, .entity_title, div.latest_stage, div.schedule_time, #page-title.entity_title, h1.entity_title {
    color: #fff!important;
}
a {
    color: #2980EC;
}
/* Hide Elements*/
 input, button, .link_as_button, .app-footer, .pagination, .row.expanded, .app-header {
    display: none!important;
}
.passed-stage, .failed-stage, .empty-stage, .building-stage, .cancelled-stage {
    border: 0px solid yellow;
    min-height: 3em!important;
    padding-top: 2em!important;
    vertical-align: middle!important;
}
.empty-stage {
    background-color: black!important;
    min-height: 0px!important;
    height: 0px!important;
    border: 0px solid yellow;
    padding: 0!important;
}
.pipeline-name, thead, tr, td {
    border-bottom: 1px dotted #202020!important;
}
#body_content, #pipelines, #body_bg, #body_content, #messaging_wrapper, #body_wrapper, #doc3, #bd, #pipeline_groups_container, .pipelines, .content_wrapper_inner, div.page-wrap, #body_bg_highlight, #pipeline-history, .container-in-body, #bd, .yui-b, #yui-main, #new-pipeline-history, #content_wrapper_outer_pipeline_history, .row.pipeline_history_table_container, .pipeline-history-group, .pipeline_bundle .pipeline_operations, .previously_wrapper {
    padding: 0px!important;
    margin: 0px!important;
}
.pipeline_bundle .pipeline {
    width: auto-flow;
}
#doc3 {
    -moz-transform: scale(1.66);
    /* Firefox */
    -webkit-transform: scale(1.66);
    -webkit-transform-origin: 0 0;
    -moz-transform-origin: 0 0;
}
#pipeline_groups_container {
    -moz-transform: scale(1.1);
    -webkit-transform: scale(1.1);
    -moz-transform-origin: 0 0;
    -webkit-transform-origin: 0 0;
    /* margin-top:2em!important; */
    /* min-width:160%; */
}
html {
    overflow: -moz-scrollbars-none;
}
::-webkit-scrollbar {
    display: none;
}
#vsm-container {
    overflow: -moz-scrollbars-none!important;
    margin: 2em 0 0!important;
    padding: 0px!important;
    border: 0px solid orange;
    -moz-transform: scale(1);
}
#value_stream_map {
    border: 0px solid orange;
    -moz-transform: scale(1);
}
#vsm-container div {
    /* -moz-transform: scale(.5); */
}
.pipeline_bundle.pipeline_group, .pipeline-group_items {
    border: 0px solid black!important;
    float: left;
    width: auto;
    min-width:auto;
    display:inline;
    clear:right;
}
th.pipeline-instance-header, th.column-header {
    border: 0px solid orange!important;
    color: #ececec!important;
    padding-top: 0em!important;
}
tbody, thead, tr {
    border: 0px solid red!important;
}
table.pipeline-history-group td, table.pipeline-history-group th {
    border-top: 1px solid #101010!important;
}
.pipeline-group_pipeline {
    background-color: #202020!important;
    border: 1px solid #404040!important;
}
.pipeline_instance-details {
    color: #dcdcdc!important;
}
td {
    border:0px solid orange;
    width: 8%!important;
}
.passed-stage, .building-stage, .failed-stage, .cancelled-stage {
    border:0px solid blue;
    width: auto!important;
    min-width: auto!important;
    height:60px!important;
}
/* ******************************************************************** */
 .page_header {
    border:3px dotted red;
    z-index:10;
    background-color:transparent!important;
}
span.page_name {
    display:none!important;
}
#header {
    border:0px dotted orange;
    z-index:0;
    display:none;
}
.yui-b, .label-filter-group {
    border:0px dotted red;
    display:none;
}
.row.pipeline_history_table_container, #pipeline-history {
    border:0px dotted blue!important;
    margin:0!important;
    top:0!important;
    /* display: none!important; */
}
.yui-b {
    border:0px dotted green!important;
    padding:0!important;
    margin-top:0!important;
    top:0!important;
    height:0!important;
    min-height:0!important;
    /* display: none!important; */
}
#bd .yui-b {
    padding-top: 0px !important;
}
.page-wrap:before {
    display: none;
}
#bd .yui-b {
    padding-top: 0px !important;
}
#page-title {
    border:0px dotted red!important;
    display:inline-box;
    z-index:11;
    position:fixed;
    top:1200px;
    left:0px;
    width:59%;
    text-align:right;
    color:white;
    font-size:3em!important;
}
}
