css:
@-moz-document domain("itch.io") {
.main_layout, .game_header_widget, .header_widget input {
    font-family: -apple-system, BlinkMacSystemFont;
}

.main_layout {
background: #fefefe;
}
.main .inner_column {
box-shadow: 0 3px 6px rgba(0,0,0,0.5);
}

.button, a.tab_btn.selected, a.nav_btn.active, .dashboard_btn.active, .graph_container {
	text-shadow: none;
    filter: grayscale(1);
}

.dashboard_page .header_notification.itchio_tip {
    filter: grayscale(1);
    background: goldenrod;
    color: white;
}

.header_widget .primary_header {
	background: #222;
}

.header_nav, .stat_header_widget {
border-bottom: none;
}

.stat_header_widget .stat_box {
border-left: none;
}

.dashboard_page .game_list .game_row {
border: none;
}

.dashboard_page .game_list .game_links a {
text-decoration: none;
}

.main_layout a {
color: #222;
    font-weight: 500;
    text-decoration: none;
}

.stat_header_widget .stat_box, .stat_header_widget .stat_box:hover {
	font-weight: 200;
    border-bottom: none;
}

.main_layout a:hover {
	border-bottom: 1px solid black;
	margin-bottom: -1px;
}

.main_layout {
font-weight: 200;
}

.dashboard_page .game_list .game_row {
margin-bottom: 2em;
    font-weight: 300;
}

.dashboard_page .game_list .game_row a {
font-weight: 300;
}

.dashboard_page .game_list .game_row a:hover {
border-bottom: none;
}

.stat_header_widget .stats_container a:hover {
border-bottom: none;
color: black;
}
}
