css:
@-moz-document domain("doc.rust-lang.org"), domain("docs.rs") {
  /*
          * - Fixes width for large screens
          * - Boxes up, and spaces up list items
          * - Puts background color and subtle border color around listed methods
          * - Removes boldness from [+] buttons and makes them smaller and dimmer
          * - Changes color highlighting of documentation and code blocks
          */
  ::-webkit-scrollbar {
    width: 5px;
    height: 5px;
  }
  ::-webkit-scrollbar-track-piece {
    background-color: #0f1419;
  }
  ::-webkit-scrollbar-thumb:vertical {
    min-height: 50px;
    background-color: #191f26;
    border-radius: 2px;
  }
  body {
    font: 16px/1.4 "Open Sans", Georgia, Times, "Times New Roman", serif;
    background-color: #0f1419;
    color: #c5c5c5;
  }

  /* next three selector for https://docs.rs*/
  div.container-rustdoc {
    max-width: 100%;
  }
  nav.sub,
  .content {
    margin-left: 245px;
  }
  div.nav-container form.landing-search-form-nav input.search-input-nav,
  div.nav-container {
    background-color: inherit;
    color: #c5c5c5;
  }
  div.nav-container form.landing-search-form-nav input.search-input-nav,
  div.nav-container,
  div.nav-container li {
    border-color: #2a2c30;
  }
  .pure-menu-children {
    background-color: #0f1419;
  }
  div.nav-container
    div.rustdoc-navigation
    div.package-details-menu
    div.right-border,
  div.nav-container .pure-menu-children,
  .menu-item-divided {
    border-color: #2a2c30;
  }
  a {
    color: #ddd;
  }
  .content,
  nav {
    max-width: 100%;
  }
  h1,
  h2,
  h3:not(.impl):not(.method):not(.type):not(.tymethod):not(.important),
  h4:not(.method):not(.type):not(.tymethod):not(.associatedconstant) {
    padding: 0;
    margin: 0;
  }
  h1,
  h2,
  h3:not(.impl):not(.method):not(.type):not(.tymethod),
  h4:not(.method):not(.type):not(.tymethod) {
    color: inherit;
    margin-top: 2em;
  }
  h1.fqn {
    border-bottom-color: #d2d2d2;
  }
  h2,
  h3:not(.impl):not(.method):not(.type):not(.tymethod),
  h4:not(.method):not(.type):not(.tymethod) {
    border-bottom-color: #2a2c30;
  }
  .theme-picker {
    display: none;
  }
  .sidebar {
    width: 230px;
    overflow-x: hidden;
    background-color: inherit;
  }
  .sidebar a {
    color: inherit;
  }
  .sidebar .sidebar-elems {
    margin: 0 10px;
  }
  .sidebar .sidebar-elems > .block:not(:first-child),
  .sidebar .sidebar-elems > .block:first-child > * {
    border: 1px solid #2a2c30;
    background-color: #191f26;
  }
  a.sidebar-title,
  .sidebar-links {
    margin: 0;
    border: none;
  }
  .sidebar .sidebar-elems > .block .sidebar-title {
    margin-top: 20px;
    background-color: #191f26;
    color: #ddd;
    border: 1px solid #2a2c30;
  }
  .sidebar-elems > .block.items > .sidebar-links {
    border-top: unset;
  }
  .sidebar .current {
    color: inherit;
    background-color: inherit;
    margin: 0;
  }
  .sidebar .location {
    padding: 5px 0;
    border: 1px solid #2a2c30;
    background-color: #191f26;
    color: inherit;
  }
  .sidebar .location:before {
    content: "? ";
  }
  .source .sidebar {
    background-color: inherit;
  }
  .source .line-numbers span {
    background-color: inherit;
  }
  .source .line-numbers {
    background-color: #191f26;
  }
  .sidebar .version {
    border-bottom-color: #888;
  }
  .sidebar .block > ul > li {
    margin: 0 !important;
  }
  .line-numbers span {
    color: #0263bf;
  }
  .line-numbers .line-highlighted {
    background-color: #0f1419 !important;
    color: #ffa500;
  }
  nav {
    border-bottom: unset;
  }
  .block ul,
  .block li {
    margin: 0;
    padding: 0;
  }
  .docblock h1,
  .docblock h2,
  .docblock h3,
  .docblock h4,
  .docblock h5 {
    border-bottom-color: #888;
  }
  .docblock table {
    border-color: #888;
  }
  .docblock table td {
    border-top-color: #888;
    border-bottom-color: #888;
  }
  .docblock table th {
    border-top-color: #888;
    border-bottom-color: #888;
  }
  :target {
    background: inherit;
  }
  :target > .in-band {
    background: inherit;
  }
  .search-input {
    padding: 5px 8px;
    font-size: 14px;
    background-color: #191f26;
    color: #c5c5c5;
    box-shadow: 0 0 0 1px #3e4045, 0 0 0 2px transparent;
  }
  .search-input:focus {
    box-shadow: 0 0 0 1px #6c6f77fa, 0 0 0 2px transparent;
    transition: border-color 300ms ease;
    transition: border-radius 300ms ease-in-out;
    transition: box-shadow 300ms ease-in-out;
    padding: 10px 8px;
  }
  .docblock h1,
  .docblock h2,
  .docblock h3,
  .docblock h4,
  .docblock h5 {
    border-bottom-color: #2a2c30;
  }
  .docblock a,
  .docblock-short a,
  .stability a {
    color: #d2991d;
    background-color: inherit;
  }
  .docblock code,
  .docblock-short code {
    background-color: inherit;
    font-weight: 500;
  }
  a.test-arrow {
    padding: 3px 4px 3px 3px;
    border-radius: 2px;
    font-size: 80%;
    top: 3px;
    right: 3px;
    color: #dedede;
    background-color: rgba(78, 139, 202, 0.2);
  }
  a.test-arrow:hover {
    background-color: #4e8bca;
  }
  .content .methods > div:not(.important-traits) {
    margin-top: 1em;
    margin-left: 3em;
    margin-bottom: 3em;
  }
  .content .method .where,
  .content .fn .where,
  .content .where.fmt-newline {
    padding-left: 3.5em;
  }
  .content .impl-items .docblock {
    margin-top: 1em;
    margin-left: 3em;
    margin-bottom: 3em;
  }
  .content .methods > div.stability {
    margin: 0;
  }
  .content .impl-items .stability {
    margin-left: 20px;
  }
  .stability > .stab.unstable,
  .stability > .stab.deprecated,
  .stability > .stab.portability {
    display: block;
    padding: 5px;
    width: 100%;
    color: inherit;
    font-size: 0.8em;
  }
  .stability > .stab.unstable {
    background-color: #0b1620;
    border-color: #082e51;
  }
  .stab.deprecated {
    background: #15071e;
    border-color: #57015d;
  }
  .stab.portability {
    background: #0c2633;
    border-color: #1f406b;
  }

  /* Reference list */
  h3.impl,
  h3.method,
  h4.method,
  h3.type,
  h4.type,
  h4.associatedconstant {
    font-weight: 500;
    margin-top: 15px;
    padding: 5px 10px;
  }
  .impl-items {
    margin-left: 3em;
  }
  .content .impl-items .method,
  .content .impl-items > .type,
  .impl-items > .associatedconstant {
    font-weight: 500;
  }
  h3.impl a,
  h3.method a,
  h4.method a,
  h3.type a,
  h4.type a,
  h4.associatedconstant a {
    font-weight: 800;
  }
  .content h1 > .out-of-band,
  ul.item-list > li > .out-of-band,
  h3.impl > .out-of-band,
  h4.method > .out-of-band {
    font-size: 0.85em;
    right: 0;
    top: 0.2em;
    margin-right: 3px;
  }
  ul.item-list > li > .out-of-band a,
  h3.impl > .out-of-band a,
  h4.method > .out-of-band a {
    font-weight: normal;
  }
  h1 .in-band,
  h3.impl .in-band {
    background-color: inherit;
  }
  h1 #toggle-all-docs,
  h3 > a.collapse-toggle,
  h4 > a.collapse-toggle {
    font-weight: normal;
    top: -3px;
    left: -26px;
  }
  div.nav-container .pure-menu-link:hover,
  .block a:hover,
  .content .collapse-toggle:hover,
  h1 .srclink:hover,
  h3.impl .srclink:hover,
  h4.method .srclink:hover,
  h1 #toggle-all-docs:hover,
  h3 > a.collapse-toggle:hover,
  h4 > a.collapse-toggle:hover,
  h1 #toggle-all-docs:focus,
  h3 > a.collapse-toggle:focus,
  h4 > a.collapse-toggle:focus {
    outline: none;
    color: #0470df;
    background-color: #0f1419;
  }
  .sidebar-links a {
    display: inline-block;
    text-decoration: none;
  }
  .content .highlighted a::after,
  div.block > ul > li a::after,
  .sidebar-links a::after {
    content: "";
    display: block;
    width: 0;
    height: 1px;
    background: #0470df;
    transition: width 0.3s;
  }
  .content .highlighted a:hover::after,
  div.block > ul > li a:hover::after,
  .sidebar-links a:hover::after {
    width: 100%;
    transition: width 0.3s;
  }
  .content > .methods > div.important-traits {
    left: -25px;
    margin-top: 20px;
  }
  h4 > .important-traits {
    color: #f6003d;
    font-weight: bold;
    left: -50px;
    top: -5px;
  }
  nav {
    border-bottom-color: #4e4e4e;
  }
  nav.main .current {
    border-top-color: #eee;
    border-bottom-color: #eee;
  }
  nav.main .separator {
    border-color: #eee;
  }
  .collapse-toggle {
    color: #999;
  }
  .module-item .stab {
    color: #ddd;
  }
  #help > div {
    background: #4d4d4d;
    border-color: #bfbfbf;
  }
  #help dt {
    border-color: #bfbfbf;
    background: rgba(0, 0, 0, 0);
    color: black;
  }
  .since {
    font-size: 1em;
    color: grey;
  }
  tr.result span.primitive::after {
    color: #ddd;
  }
  .toggle-label {
    color: #999;
  }
  :target > code {
    background: #fdffd3;
  }
  .item-spacer {
    width: 100%;
    height: 1.5em;
  }
  pre.compile_fail {
    border-left: 2px solid rgba(255, 0, 0, 0.6);
  }
  pre.compile_fail:hover,
  .information:hover + pre.compile_fail {
    border-left: 2px solid #f00;
  }
  pre.ignore {
    border-left: 2px solid rgba(255, 142, 0, 0.6);
  }
  pre.ignore:hover,
  .information:hover + pre.ignore {
    border-left: 2px solid #ff9200;
  }
  .tooltip.compile_fail {
    color: rgba(255, 0, 0, 0.6);
  }
  .information > .compile_fail:hover {
    color: #f00;
  }
  .tooltip.ignore {
    color: rgba(255, 142, 0, 0.6);
  }
  .information > .ignore:hover {
    color: rgba(255, 142, 0, 1);
  }
  .search-failed > a {
    color: #0089ff;
  }
  .tooltip .tooltiptext {
    color: #e6e1cf;
    background-color: #0b1620;
    border-color: #675d38;
    padding: 5px 10px;
  }
  .tooltip .tooltiptext::after {
    border-color: transparent black transparent transparent;
  }
  .important-traits .tooltip .tooltiptext {
    color: inherit;
    border-radius: 3px;
    background: #15071e;
    border-color: #57015d;
  }
  #titles > div {
    border-bottom-color: #ccc;
  }
  #titles > div.selected {
    border-bottom-color: #0078ee;
  }
  #titles > div:hover {
    border-bottom-color: #0089ff;
  }
  #titles > div > div.count {
    color: #888;
  }
  .modal {
    background-color: rgba(0, 0, 0, 0.8);
  }
  .modal-content {
    background-color: #0f1419;
    border: 1px solid #2a2c30;
  }
  .modal-content > .close {
    background-color: #0f1419;
    border-top: 2px solid #2a2c30;
    border-right: 1px solid #2a2c30;
    border-bottom: 1px solid #2a2c30;
  }
  .modal-content > .close:hover {
    background-color: #ff1f1f;
    color: white;
  }
  .modal-content > .whiter {
    background-color: #0f1419;
  }
  .modal-content > .close:hover + .whiter {
    background-color: #2a2c30;
  }
  @media (max-width: 700px) {
    nav.sub,
    .content {
      margin-left: 0;
    }
    .sidebar {
      min-height: 50px;
      position: relative;
      width: 100%;
      border-bottom: unset;
      margin: 0;
      padding: 0;
    }
    .sidebar.mobile {
      position: relative;
    }
    .sidebar .location {
      font-size: 120%;
      margin-top: 10px;
      min-height: 30px;
    }
    .sidebar-menu {
      background-color: #191f26;
      border: unset;
    }
    .sidebar .sidebar-elems {
      background-color: #0f1419;
      border-right-color: #000;
      border-top: 1px solid #000;
      margin: 0;
    }
    #sidebar-filler {
      background-color: #0f1419;
      border: unset;
    }
    .show-it > .block.items {
      margin: 0;
    }
    nav.sub {
      padding-bottom: 20px;
      padding-top: 20px;
      width: 100%;
    }
    .content h1 > .out-of-band,
    ul.item-list > li > .out-of-band,
    h3.impl > .out-of-band,
    h4.method > .out-of-band {
      float: right;
      position: unset;
    }
    .content .in-band {
      width: unset;
    }
  }
  @media (max-width: 416px) {
    nav.sub {
      float: right;
      width: 100%;
    }
  }
  kbd {
    color: #000;
    background-color: #fafbfc;
    border-color: #888;
    border-bottom-color: #888;
    box-shadow-color: #c6cbd1;
  }
}

@-moz-document domain("doc.rust-lang.org"), domain("docs.rs") {
  /*
       * Documentation highlighting
       */
  .content .method .where,
  .content .fn .where,
  .content .where.fmt-newline {
    color: #4e4c4c;
  }
  .content .highlighted {
    color: inherit;
    background-color: #0f1419;
  }
  .content .highlighted a,
  .content .highlighted span {
    color: #0470df !important;
  }
  .sidebar .current:before {
    content: "▶️ ";
  }
  .sidebar .crate,
  .content span.crate,
  .content a.crate,
  .sidebar .current.crate {
    color: #ddd;
  }
  .sidebar .current.crate,
  .content .highlighted.crate {
    background-color: #0f1419;
  }
  .sidebar .externcrate,
  .content span.externcrate,
  .content span.externcrate {
    color: #ddd;
  }
  .sidebar .current.externcrate,
  .content .highlighted.externcrate {
    background-color: #0f1419;
  }
  .sidebar .constant,
  .content span.constant,
  .content a.constant,
  .block a.current.constant,
  .sidebar .static,
  .content span.static,
  .content a.static,
  .block a.current.static {
    color: #05b407;
  }
  .sidebar .current.constant,
  .content .highlighted.constant,
  .sidebar .current.static,
  .content .highlighted.static {
    background-color: #0f1419;
  }
  .sidebar .enum,
  .content span.enum,
  .content a.enum,
  .block a.current.enum {
    color: #008543;
  }
  .sidebar .current.enum,
  .content .highlighted.enum {
    background-color: #0f1419;
  }
  .sidebar .fn,
  .content span.fn,
  .content a.fn,
  .block a.current.fn,
  .content span.method,
  .content a.method,
  .sidebar .method,
  .block a.current.method,
  .content span.tymethod,
  .content a.tymethod,
  .sidebar .tymethod,
  .block a.current.tymethod,
  .content .fnname {
    color: #cd00e2;
  }
  .sidebar .current.fn,
  .content .highlighted.fn,
  .sidebar .current.method,
  .content .highlighted.method,
  .sidebar .current.tymethod,
  .content .highlighted.tymethod {
    background-color: #0f1419;
  }
  .sidebar .foreigntype,
  .content span.foreigntype,
  .content a.foreigntype,
  .block a.current.foreigntype {
    color: #2aa198;
  }
  .sidebar .current.foreigntype,
  .content .highlighted.foreigntype {
    background-color: #0f1419;
  }
  .sidebar .macro,
  .content span.macro,
  .content a.macro,
  .block a.current.macro {
    color: #df5000;
  }
  .sidebar .current.macro,
  .content .highlighted.macro {
    background-color: #0f1419;
  }
  .sidebar .mod,
  .content span.mod,
  .content a.mod,
  .block a.current.mod {
    color: #6b8e23;
  }
  .sidebar .current.mod,
  .content .highlighted.mod {
    background-color: #0f1419;
  }
  .sidebar .primitive,
  .content span.primitive,
  .content a.primitive,
  .block a.current.primitive {
    color: #2987a6;
  }
  .sidebar .current.primitive,
  .content .highlighted.primitive {
    background-color: #0f1419;
  }
  .sidebar .struct,
  .content span.struct,
  .content a.struct,
  .block a.current.struct {
    color: #856f00;
  }
  .sidebar .current.struct,
  .content .highlighted.struct {
    background-color: #0f1419;
  }
  .sidebar .trait,
  .content span.trait,
  .content a.trait,
  .block a.current.trait {
    color: #f6003d;
  }
  .sidebar .current.trait,
  .content .highlighted.trait {
    background-color: #0f1419;
  }
  .sidebar .type,
  .content span.type,
  .content a.type,
  .block a.current.type {
    color: #ba5d00;
  }
  .sidebar .current.type,
  .content .highlighted.type {
    background-color: #0f1419;
  }
  .sidebar .union,
  .content span.union,
  .content a.union,
  .block a.current.union {
    color: #499650;
  }
  .sidebar .current.union,
  .content .highlighted.union {
    background-color: #0f1419;
  }
}

@-moz-document domain("doc.rust-lang.org"), domain("docs.rs") {
  /* Pre code highlighting 
          * Examples uses different code highlighting than the documentation
          */
  h3.impl,
  h3.method,
  h4.method,
  h3.type,
  h4.type,
  h4.associatedconstant,
  pre.rust {
    border: 1px solid #2a2c30;
    background-color: #191f26;
    color: #e6e1cf;
  }
  pre,
  code {
    font-family: "Fira Code", "Source Code Pro", Menlo, Monaco, Consolas,
      "DejaVu Sans Mono", Inconsolata, monospace;
    font-size: 1em;
    line-height: 1.5;
  }
  pre.rust-example-rendered {
    background-color: #191f2680;
    border: unset;
  }

  /* let for in use while unsafe as */
  pre.rust .kw {
    color: #cd00e2;
  }

  /* mut & * */
  pre.rust .kw-2,
  pre.rust .prelude-ty {
    color: #00a4d9;
  }

  /* nuber string char */
  pre.rust .number,
  pre.rust .string {
    color: #edde02;
  }
  pre.rust .self,
  pre.rust .bool-val,
  pre.rust .prelude-val,
  pre.rust .attribute,
  pre.rust .attribute .ident {
    color: #f00;
  }
  pre.rust .macro,
  pre.rust .macro-nonterminal {
    color: #df5000;
  }
  pre.rust .lifetime {
    color: #7fff00;
  }
  pre.rust .question-mark {
    color: #ff9011;
  }
  pre.rust .comment,
  pre.rust .doccomment {
    color: #808080;
    display: inline-block;
    padding: 5px 0;
  }
}
