css:
@-moz-document domain("www.lydsy.com") {
* {
		font-family: Monaco, Consolas, "Microsoft YaHei", monospace !important;
	}
	
	/* main */
  body{
		background-image:url("http://");
	}
	tr[class="hd"]{
		width: 100%;
		border-color: #101010;
		opacity: 0.8;
    box-shadow: 1px 1px 10px #888888;
		height: 40px;
	}
	div[class="notice"]{
		width: 80%;
		border-color: #101010;
		opacity: 0.5;
    box-shadow: 1px 1px 10px #888888;
	}
	h3{
		position:relative;
		left: 200px;
		right:500px;
		width: 70%;
		border-color: #101010;
		opacity: 0.8;
    box-shadow: 1px 1px 10px #888888;
		height: 40px;
	}
	th{
		padding:15px;
  }
	a{
		text-decoration: none;
		-webkit-transition: all 0.1s ease-in;
		-moz-transition: all 0.1s ease-in;
		-o-transition: all 0.1s ease-in;
		transition: all 0.1s ease-in;
	}
	a:link{
		text-decoration: none;
		-webkit-transition: all 0.1s ease-in;
		-moz-transition: all 0.1s ease-in;
		-o-transition: all 0.1s ease-in;
		transition: all 0.1s ease-in;
	}
	a:hover{
		color: rgba(218,165,32,0.8);
	}
	.evenrow{
		background-color: #FFFFFF;
	}
  .oddrow{
		background-color: rgba(220,220,220,0.2);
	}

	td{
		text-align: left;
		padding:15px;
		padding-top:10px;
		border-bottom:1px solid rgba(112,128,144,0.3);
	}


	.search-query {
		width: 50% !important;
	}

	button.form-control {
		width: auto;
	}
	.tr{
		color:#000000;
	}
	th {
		text-align: center;
	}
  .toprow {
		background: #FFFFFF;
		color:black;
	}
	.toprow a, .toprow a:visited {
    font-family: Arial;
		cursor:pointer;
    font-weight: bold;
    font-size: 18px;
    color: #4183c4;
    margin: 15px;
}
	.headerSortDown {
		background: #0482ba;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075);
	}
	input[type="text"]{
		background: #FFFFFF;
		position: relative; 
		border: 1px solid rgba(0,0,0,0.5);
		height: 25px;
		left: 5px;
	}
	input[type="text"]:focus{
		-webkit-animation: glow 800ms ease-out infinite alternate;
	-moz-animation: glow 800ms ease-out infinite alternate;
	-o-animation: glow 800ms ease-out infinite alternate;
	-ms-animation: glow 800ms ease-out infinite alternate;
	animation: glow 800ms ease-out infinite alternate;
		border-color: #393;
		box-shadow: 5px 5px 10px rgba(0,255,0,0.2), inset 5px 5px 10px rgba(0,255,0,0.1), 5px 5px 10px #FFFFFF;
		outline: none;
	}
}

@-moz-document url("https://www.lydsy.com/JudgeOnline/") {
/* index */

	tr > th:first-child,
	tr > td:first-child {
		display: none;
	}

	td img {
		width: 100%;
		height: auto;
	}
}

