	}

	td{
		text-align: left;
		padding:15px;
		padding-top:10px;
		border-bottom:1px solid rgba(112,128,144,0.3);
	}


	.search-query {
		width: 50% !important;
	}

	button.form-control {
		width: auto;
	}
	.tr{
		color:#000000;
	}
	th {
		text-align: center;
	}
  .toprow {
		background: #FFFFFF;
		color:black;
	}
	.toprow a, .toprow a:visited {
    font-family: Arial;
		cursor:pointer;
    font-weight: bold;
    font-size: 18px;
    color: #4183c4;
    margin: 15px;
}
	input[type="submit"] {
		position: relative; 
		left: 20px;
		width: 3%;
		height: 30px;
		cursor: pointer;
		color:rgba(0,0,0,0);
		background-color: rgba(0,0,0,0);
		border: rgba(0,0,0,0);
		background-image: url(data:image/svg+xml;base64,) !important;
		background-repeat: no-repeat;
	}

	.headerSortDown {
		background: #0482ba;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075);
	}
}

@-moz-document url-prefix("https://www.lydsy.com/JudgeOnline/problem.php") {
/* problem */

	.content {
		display: block;
		padding: 9.5px;
		margin: 0 0 10px;
		color: #333;
		word-break: break-all;
		word-wrap: break-word;
		background-color: #f5f5f5;
		border: 1px solid #ccc;
		border-radius: 4px;
	}
	h2{
		color: #000000;
		font-size: 30px;
	}
	span[class="green"]
	{
		font-family: Consolas;
		display: inline-block;
		line-height: 1;
		vertical-align: baseline;
		margin: 0 .14285714em;
				margin-left: 0.142857em;
		background-color: #e8e8e8;
		background-image: none;
		padding: .5833em .833em;
		color: rgba(0,0,0,.6);
		text-transform: none;
		font-weight: 700;
		border: 0 solid transparent;
		border-radius: .28571429rem;
	}
	span[class="red"]
	{
		font-family: Consolas;
		display: inline-block;
		line-height: 1;
		vertical-align: baseline;
		margin: 0 .14285714em;
				margin-left: 0.142857em;
		background-color: #e8e8e8;
		background-image: none;
		padding: .5833em .833em;
		color: rgba(255,0,0,.6);
		text-transform: none;
		font-weight: 700;
		border: 0 solid transparent;
		border-radius: .28571429rem;
	}
	div[class="content"]{
		position: relative;
		left: 100px;
		opacity: 0.8;
    box-shadow: 1px 1px 10px #888888;
		width:80%;
	}
	span[class="sampledata"]
	{
		white-space: normal;
		background: #e4f0f8;
	}
}

@-moz-document url-prefix("https://www.lydsy.com/JudgeOnline/status.php") {
/* status */

	.input-append {
		width: 100%;
		margin-left: auto;
		margin-right: auto;
	}
	table[width="90%"]{
		opacity: 0.8;
    box-shadow: 1px 1px 10px #888888;
		width:60%;
	}
	input[type="submit"]{
		height: 60px;
	}
	select {
		width: 15% !important;
	}

	input[type="submit"] {
		position: relative; 
		left: 5px;
		width: 3%;
		height: 30px;
		cursor: pointer;
		color:rgba(0,0,0,0);
		background-color: rgba(0,0,0,0);
		border: rgba(0,0,0,0);
		background-image: url(data:image/svg+xml;base64,) !important;
	}
	table[align="center"]{
		position: relative;
		opacity: 0.8;
    box-shadow: 1px 1px 10px #888888;
		width:80%;
	}
}

@-moz-document url-prefix("https://www.lydsy.com/JudgeOnline/loginpage.php") {
/* login */

	#login > div * {
		float: none !important;
		margin: 0 !important;
	}

	.form-group {
		margin-left: auto !important;
		margin-right: auto !important;
	}

	.col-sm-4,
	.col-sm-8 {
		display: inline-block !important;
	}

	label.col-sm-4 {
		width: 35%;
		text-align: right;
	}

	div.col-sm-8 {
		width: 30%;
	}

	@media screen and (max-width: 767px) {
		div.col-sm-8 {
			width: 50%;
		}
	}

	div.col-sm-4 {
		width: 24%;
	}

	#login > div:nth-child(4) {
		text-align: center;
	}

	#login > div:nth-child(4) > div {
		width: 15%;
	}

	@media screen and (max-width: 767px) {
		#login > div:nth-child(4) > div {
			width: 30%;
		}
	}

	.btn-block {
		color: #fff !important;
		background: #0593d3;
	}

	.btn-block:hover,
	.btn-block:focus {
		background: #0482ba;
		box-shadow: inset 0 0 2em rgba(0, 0, 0, 0.075);
	}
}

