css:
@-moz-document domain("swgoh.gg")
{
body {
    background: #202020 !important; }

					/* ---------- Homepage ---------- */
    /* Navbar */
.app-navbar a {
    color: #CCC !important; }
.app-navbar {
    background: #303030 !important; }
.navbar-inverse {
    border-color: #CCC !important; }
.navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, 

.navbar-inverse .navbar-nav > .active > a:focus {
    border-bottom: 4px solid #CCC !important; }

.dropdown-menu, .dropdown-menu > li > a {
    color: #CCC !important;
    background: #303030 !important; }
.dropdown-menu > li > a:hover {
    background: #404040 !important; }

.dropdown-menu {
    border: 1px solid #050505 !important; }

	/* Search Form */
.form-control, .input-group-btn .btn {
    color: black !important;
    border: 1px solid #050505 !important;
    background: #707070 !important; }
.input-group-btn .btn:hover {
    background: #909090 !important; }
  
.form-control::placeholder {
    color: #CCC !important; }

.modal-content {
    background: #404040 !important; }
.modal-title {
    color: #CCC !important; }
.close {
    opacity: .5 !important;
    color: red !important; }

 .modal-header .btn, .btn-sm, /* Character stat page */.panel-body .btn-sm, /* Guilds > Unit 

Search */.btn-default {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #505050 !important; }
.modal-header .btn:hover, .btn-sm:hover, .panel-body .btn-sm:hover, .btn-default:hover {
    background: #707070 !important; }
.modal-header .btn:focus, .btn:active, .btn.active {
    outline: none !important;
    border: 1px solid white !important;
    color: black !important;
    background: #909090 !important; }
.btn-success, .btn-warning {
    color: black !important; }


	/* Lists, Tables */
.character-list .character {
    border-color: #050505 !important;
    color: #CCC !important;
    background: #303030 !important; }
.character-list .character:hover {
    background: #404040 !important; }

.media-heading > h5 /* characters/specific */ {
    color: white !important; }

.panel-default {
    border-color: #050505 !important; }

.media.list-group-item /* Most tables & lists */{
    border: 1px solid #050505 !important;
    background: #505050 !important;
    color: white !important; }
a.list-group-item:hover {
    background: #404040 !important; }

.list-group-item, .panel-body, /*Ships*/.character-list .media.list-group-item, 

/*ships/specific*/.media.list-group-item.p-sm, .list-group-item:last-child, 

/*Events*/.media.list-group-item.p-a-0, .media.list-group-item.p-0.b-t-0 {
    border-color: #050505 !important;
    background: #303030 !important;
	color: #CCC !important;}


					 /* ---------- Units Tab ---------- */
	/* Units > Characters*/
.effect-info {
    color: #CCC !important;
    border-color: #050505 !important;
    background: #303030 !important; }
.effect-summary, .effect-tag {
    background: #505050 !important; }

.ability-summary[aria-expanded="true"] {
    background-color: #505050 !important; }

.effect-condition-type-undefined .effect-condition-value, .effect-condition-type-add-persistent-

effect .effect-condition-value, .effect-condition-type-remove-persistent-effect .effect-

condition-value, .effect-condition-type-persistent-effect-add-instance .effect-condition-value, 

.effect-condition-type-persistent-effect-remove-instance .effect-condition-value, .effect-

condition-type-receive-pre-effect-eval .effect-condition-value, .effect-condition-type-receive-

post-effect-eval .effect-condition-value, .effect-condition-type-receive-pre-evasion-eval 

.effect-condition-value, .effect-condition-type-receive-post-evasion-eval .effect-condition-value, 

.effect-condition-type-pre-receive-damage .effect-condition-value, .effect-condition-type-post-

receive-damage .effect-condition-value {
    background: #505050 !important; }
.effect-summary-contextual-filter, .effect-summary-contextual-execute {
    border-left-color: orange !important;
    color: orange !important; }
.effect-summary-unresistable, .effect-summary-unmitigable {
    border-left-color: blue !important;
    color: blue !important; }
.effect-summary-no-health-steal, .effect-summary-no-counter-attack {
    border-left-color: red !important;
    color: red !important; }


	/* Units > Ships*/
.unit-stat-group-stat-primary .unit-stat-group-stat-label, .unit-stat-group-stat-equipment {
    color: #CCC !important; }

.ability-crew, .ability-mechanics, th, .table-condensed > tbody > tr > td {
    border-color: #050505 !important;
    color: #CCC !important;
    background: #404040 !important; }
.collapse.in {
    border-color: #050505 !important; }
a, .panel-title > a, .ability-mechanics a, .ability-crew a {
    color: #84c1fb !important; }

.breadcrumb > li {
    color: #CCC !important; }


	/* Abilities Page, Ships */
a.media-body {
    color: #CCC !important;
    background: #303030 !important; }
a.media-body:hover, div.list-group .list-group-item:hover {
    background: #404040 !important; }


	/* Units > Meta Report & Zeta Report*/ /* Thanks to Incrazone for the highcharts fix | 

userstyles.org/users/347895*/
.highcharts-background { fill: #202020 !important; } 
.highcharts-title { fill: #999 !important; } 
.highcharts-data-labels span { color: #CCC !important; }

tbody {
    background: #404040 !important; }
tbody td {
    border-color: #050505 !important; }

.pagination > li > a {
    border-color: #050505 !important;
    background: #505050 !important; }
.pagination > li > a:hover {
    background: #808080 !important; }


					/* ---------- Database Tab ---------- */
	/* Database > Gear */
a.gear-panel-raid {
    border-color: #050505 !important; 
    background: linear-gradient(45deg,#404040 95%,#ff9637 95%,#ee7100 100%) !important }
a.gear-panel-t12 {
    border-color: #050505 !important;
    background: linear-gradient(45deg,#404040 95%,#ffcc33 95%,#F0A814 100%) !important; }
a.gear-panel-t7 {
    border-color: #050505 !important;
    background: linear-gradient(45deg,#404040 95%,#a386ec 95%,#6746b9 100%) !important; }
a.gear-panel-t4 {
	border-color: #050505 !important;
    background: linear-gradient(45deg,#404040 95%,#7be2fb 95%,#06bce9 100%) !important; }
.gear-panel-t2 {
    border-color: #050505 !important;
    background: linear-gradient(45deg,#404040 95%,#06e94c 95%,#06e94c 100%) !important; }
.gear-panel-t1 {
    border-color: #050505 !important;
    background: #404040 !important; }

a.gear-panel-raid:hover, .gear-panel-t12:hover, .gear-panel-t7:hover, .gear-panel-t4:hover, 

.gear-panel-t2:hover, .gear-panel-t1:hover {
    background: #505050 !important; }
a.panel:hover {
    background-color: #505050 !important; }

.list-group-item-chars {
    border-color: #050505 !important;
    background: #303030 !important; }


	/* Database > Raids */
.nav-tabs > li.active > a {
    color: black !important;
    background: #909090 !important; }
.nav-tabs > li > a:hover {
    background: #505050 !important; }

.raid-row-text {
    color: white !important;
    background: #303030 !important; }


	/* Database > Shard Shipments */
.shipment-item {
    border-color: #050505 !important;
    background: #202020 !important; }


	/* Database > Events */
.loot-item-count {
    color: orange !important; }
.loot-item.loot-first-only .loot-item-count {
    color: black !important; }


					/* ---------- Guild Tab ---------- */
	/* Leaderboard > Unit Search */
.media-list-users .media-body-secondary {
    color: #CCC !important; }
.modal-dialog .modal-body-scroller strong {
    color: white !important; }


	/* Guild Activities */
.today-item .today .panel {
    color: white !important;
    background: #707070 !important; }
.col-md-6 .panel {
    background-color: #505050 !important; }


					/* ---------- Squads Tab ---------- */
	/* Squad List */
.media-body.squad-summary .m-0 {
    color: white !important; }


	/* Squad List > Specific */
.squad-builder .character-card, .m-b-sm {
    background: #303030 !important; }

.highcharts-legend-item text, .highcharts-axis-labels text {
    fill: white !important; }


	/* Squads > Squad Builder */
.modal-content h5 {
    color: white !important; }
.modal-content .custom-select, .modal-content .custom-select option {
    color: black !important;
    border: 1px solid #050505 !important;
    background-color: #707070 !important; }
.modal-content .custom-select:hover {
    background-color: #909090 !important; }

.modal-footer .btn-default-outline {
    border-color: #aa4a24 !important;
    color: white !important;
    background: #bf5329 !important; }


					/* ---------- User Tab ---------- */
	/* User Main */
.stat-item-title, .current-rank-title {
    color: #CCC !important; }

.chart-arena .chart-content {
    background: #202020 !important; }
.arena-rank-chart /* Text */ {
    fill: white !important; }

.chart-arena-info {
    color: white !important;
    background-color: #404040 !important; }
.chart-arena-team {
    background-color: #303030 !important; }
.chart-arena-scan-time {
    color: white !important; }

.content-container-aside h5 {
    color: white !important; }


	/* user/collection/specific */
.statmod-stat, .pc-statmods .statmod-full .statmod-stat-value {
    color: white !important; }
.pc-stat-primary .pc-stat-label, .pc-stat-modified .pc-stat-value {
    color: #CCC !important; }

.popup-statmod {
    background-color: #303030 !important; }
.popup-statmod .statmod-stats-heading {
    color: white !important; }


	/* user/mods */
#filterModal .mod-filter-table .mod-filter-label-text {
    color: white !important; }
.m-t-0 .media.list-group-item {
    background: #303030 !important; }



	/* /user/ships/specific */
.m-b-sm .media.list-group-item {
    background: #303030 !important; }

span.code {
    background: #404040 !important; }


    /* /user/squads */

.player-squad-list-squad .panel {
    background-color: #404040 !important; }
.player-squad-list-squad .panel h4 {
    color: white !important; }
.player-squad-list-squad .panel h4 small, .player-squad-list-squad .panel p {
    color: #CCC !important; }




	/* Gear Page:hover*/
.popover-content, .popover-title, .popover {
    color: #CCC !important;
    border-color: #050505 !important;
    background: #404040 !important }

	/* Raids, Latest Characters + My Ships, Characters */
.m-b-md h5, .collection-ship-name-link, .collection-char-name-link {
    color: #CCC !important; }
}



@-moz-document regexp("(http|https)://.*swgoh\.gg/(C|c)haracters.*"), regexp("(http|https)://.*swgoh\.gg/db/(G|g)ear.*"), regexp("(http|https)://.*swgoh\.gg/db/(S|s)hipments.*"), regexp("(http|https)://.*swgoh\.gg/db/(M|m)issions.*"), regexp("(http|https)://.*swgoh\.gg/(N|n)ews.*")
{

	/* Fixes for Character Gear Level page */
.list-group-item, .panel-body, div.list-group .list-group-item, .list-group-item .character, 

.media.list-group-item.p-a, .media.list-group-item .table {
    border-color: #050505 !important;
    background: #303030 !important;
	color: #CCC !important;}

/* character/specific/stats */
.media.list-group-item .table tr:hover {
    background: #404040 !important; }
}
