css:
@-moz-document url-prefix("https://www.deepl.com") {
/* ====================================
	2018 v 0.1
	Robert Kanzler
	robert@kanzler.email
   ==================================== */
/* ====================================
   VARIABLES
   ==================================== */

:root {
    --color-accent: rgb(18, 143, 231);
    --color-accent-light: rgb(33, 154, 233);
    --color-accent-alternate: rgb(137, 234, 80);
    --color-dark: rgb(80, 80, 80);
    --color-darker: rgb(60, 60, 60);
    --color-darkest: rgb(30, 30, 30);
    --color-light: rgb(210, 210, 210);
    --color-lighter: rgb(240, 240, 240);
    --color-lightest: rgb(250, 250, 250);
    --color-medium: rgb(120, 120, 120);
}
/* ====================================
   ELEMENTS
   ==================================== */

html {
    -webkit-font-smoothing: antialiased;
    -moz-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    -ms-font-smoothing: antialiased;
    -o-font-smoothing: antialiased;
}
body {
    background: var(--color-dark) !important;
    color: var(--color-light) !important;
}
a {
    color: var(--color-lighter) !important;
}
.dl_header {
    background: var(--color-darker) !important;
    color: var(--color-light) !important;
}
.dl_dropdown_dialog {
    background: var(--color-darker) !important;
    color: var(--color-light) !important;
}
.dl_dropdown_dialog.dl_dropdown_dialog--menu li:hover {
    background: var(--color-darkest) !important;
}
.lmt__language_select label strong,
div.lmt__language_container,
.lmt__language_select .lmt__language_select__opener path {
    color: var(--color-light) !important;
    stroke: var(--color-light) !important;
}
.lmt__language_select.lmt__language_select--open label {
    background: var(--color-darkest) !important;
}
.lmt__language_select.lmt__language_select--open ul {
    background: var(--color-darker) !important;
    color: var(--color-light) !important;
}
.lmt__language_select li:hover {
    background: var(--color-darkest) !important;
}


textarea {
     color: var(--color-lighter) !important;
}


#lmt_pro_ad_article,
#lmt_quotes_article {
    display: none !important;
}
#lmt_quotes_article ~ .dl_footer,
#lmt_quotes_article ~ .dl_footer > div {
    background: var(--color-darker) !important;
    background-color: var(--color-darker) !important;
    color: var(--color-light) !important;
}
#dl_cookie_footer,
#dl_cookie_footer>div,
.dl_footer .dl_footer__social {
    background: var(--color-darker) !important;
    color: var(--color-light) !important;
}
#dl_cookie_footer > div > a:hover {
    border: 2px solid var(--color-lighter) !important;
}
}
