css:
@-moz-document domain("codefights.com") {
.-bg-white {
    background-color: #212B37;
}
body {
    color: #fff;
    background-color: #212B37;
}
.card {
    background-color: #33485f;
}
.interview-practice-home--future-topic-card-inner,
.interview-practice-home--topic-card-inner {
    color: #fff;
}
.interview-practice-topic--task-card-inner {
    color: #fff;
}
.-grey-70 {
    color: #fff;
}
.tabs--tab {
    color: #fff;
}
.-grey-60 {
    color: #fff;
}
.-grey-80 {
    color: #fff;
}
.-blue-grey-70 {
    color: #fff;
}
.tabs.-view-ide {
    background-color: #212B37;
}
.tabs {
    position: relative;
    background: #212B37;
    color: #fff;
}
.tabs.-view-ide .tabs--tab {
    background: #212B37;
    color: #fff;
}
.tabs.-view-ide .tabs--tab.-active {
    background: #212B37;
}
.-bg-blue-grey-3 {
    background-color: #212B37;
}
.-bg-blue-grey-7 {
    background-color: #33485f;
}
.button.-view-flat {
    color: #999;
}
.markdown pre {
    background-color: transparent;
}
codefights-card {
    background: #5d6e82;
}
.chat-friends {
    background: #5d6e82;
}
.select-menu {
    background-color: #212B37;
}
.table--header {
    background-color: inherit;
}
.table--body-row.-stripes:nth-child(odd) {
    background-color: inherit;
}
.table--body-row.-stripes:nth-child(2n) {
    background-color: #33485f;
}
.select--wrapper {
    color: #fff;
}
.challenges .challenge-box {
    background-color: #93a1a1;
}
.challenges .challenge-box--bottom {
    background-color: #93a1a1;
}
.modal--container {
    background-color: #212B37;
}
.modal.-theme-dim .modal--header,
.modal.-theme-light .modal--header {
    background-color: #212B37;
}
.company-bots--card {
    color: #fff;
}
.modal--container {
    color: #fff;
}
.-blue-grey-80 {
    color: #fff;
}
.home-card--header {
    background: #5d6e82;
}
.home-card--footer {
    background-color: #5d6e82;
}
.info--label {
    color: #fff;
}
.hljs-section,
.hljs-title {
    color: #d84343;
}
.hljs-deletion,
.hljs-number,
.hljs-quote,
.hljs-selector-class,
.hljs-selector-id,
.hljs-string,
.hljs-template-tag,
.hljs-type {
    color: #d84343;
}
