.fight-overview--round_title {
    color: #fff;
}
codefights-slider .codefights-slider-buttons .codefights-slider-pager {
    background: rgba(183, 183, 183, 0.3);
}
.interview-practice-home--resume-header {
    background-color: #5d6e82;
}
.markdown a {
    color: #92bdf5;
}
a {
    color: #6cb0de;
}
.quick-challenge--description {
    color: #fff;
}
.-blue-grey-60 {
    color: #fff;
}
.quick-challenge {
    background: inherit;
}
.quick-challenge--description:after {
    background: linear-gradient(180deg, hsla(0, 1%, 19%, 0.4) 0, #212B37);
}
.markdown table tr {
    background-color: inherit;
}
.markdown table tr:nth-child(2n) {
    background-color: inherit;
}
.text-task-description--content {
    background: inherit;
}
}
