css:
@-moz-document domain("koreus.com") {
/*@import url('https://fonts.googleapis.com/css?family=Poppins:300,400,500');
/* The new fontawesome work with 3 imports, don't worries ! solid.css and regular.css is almost empty */
/*@import url('https://use.fontawesome.com/releases/v5.0.8/css/solid.css');
@import url('https://use.fontawesome.com/releases/v5.0.8/css/regular.css');
@import url('https://use.fontawesome.com/releases/v5.0.8/css/fontawesome.css');*/
body {
    background-color: #F0EFF7 !important;
    font-family: 'Poppins', sans-serif !important;
}
table {
    background-color: transparent;
}
body table:first-child {
    background-color: transparent !important;
}
a {
    font-family: 'Poppins', sans-serif;
    font-size: 15px;
    font-weight: 500;
}
input,
select {
    background-color: #fff;
    border: 1px solid #e4e7ea;
    border-radius: 0;
    box-shadow: none;
    color: #565656;
    max-width: 100%;
    padding: 7px 12px;
    transition: all 300ms linear 0s;
}
input[type="submit"],
input[type="button"] {
    cursor: pointer;
}
input[type="submit"]:hover,
input[type="button"]:hover {
    transition-duration: 0.3s;
    background: #524f81;
    color: #fff
}
img[src="//koreus.cdn.li/static/images/logo.jpg"] {
    height: 70px;
    box-sizing: border-box;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    padding-left: 406px;
    background: transparent url('data:image/png;base64,');
}
form[action="/search.php"] input[type="text"] {
    border: 2px solid #EAE7F4;
    height: 30px;
    border-radius: 3px;
    padding: 2px 10px;
}
form[action="/search.php"] input[type="submit"] {
    border: 0px solid #330066;
    height: 34px;
    border-radius: 3px;
    background: #AEA0D4;
    color: #330066;
    cursor: pointer;
    padding: 3px 10px;
}
td#leftcolumn,
td#rightcolumn {
    background: none;
    border: 0px;
    width: 250px;
}
td#rightcolumn {
    padding-right: 5px;
}
td#leftcolumn div.blockTitle,
td#rightcolumn div.blockTitle {
    font-family: 'Poppins', sans-serif;
    text-align: left;
    font-weight: 500;
    background-image: none;
    background-color: #EDA261;
    color: #fff;
    line-height: 30px;
    padding-left: 10px;
    font-size: 15px;
    text-transform: uppercase;
    border-radius: 3px 3px 0px 0px;
    margin-top: 15px;
}
td#leftcolumn div.blockContent,
td#rightcolumn div.blockContent {
    padding: 0;
    background: #fff;
}
/* QUI EST EN LIGNE */

td#leftcolumn div.blockContent a {
    font-weight: 300;
    font-size: 12px;
    font-family: 'Poppins', sans-serif;
}
/** MENU UTILISATEUR **/

td#leftcolumn div.blockContent td#usermenu,
td#rightcolumn div.blockContent td#usermenu {
    font-size: 0;
    padding: 0;
    border: 0px solid #ccc;
}
td#leftcolumn div.blockContent #usermenu a,
tdrightcolumn div.blockContent #usermenu a {
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    line-height: 120%;
    color: #330066;
    border-bottom: 0px solid rgba(0, 0, 0, .1);
    padding-left: 10px;
    border-right: 0;
    padding-bottom: 13px;
}
/** MENU PRINCIPAL **/

td#leftcolumn div.blockContent td#mainmenu,
td#rightcolumn div.blockContent td#mainmenu {
    font-size: 0;
    padding: 0;
    padding-left: 8px;
}
td#leftcolumn div.blockContent .menuMain,
td#rightcolumn div.blockContent .menuMain {
    line-height: 30px;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    color: #330066;
    border-top: 1px solid rgba(255, 255, 255, .5);
    border-bottom: 0;
    border-right: 0;
}
td#leftcolumn div.blockContent .menuTop,
td#rightcolumn div.blockContent .menuTop {
    line-height: 30px;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    color: #330066;
    border-top: 0px solid #fff;
    border-bottom: 0;
}
td#leftcolumn div.blockContent .menuSub,
td#rightcolumn div.blockContent .menuSub {
    line-height: 30px;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    color: #330066;
    border-top: 1px solid rgba(255, 255, 255, .5);
    margin-left: 10px;
    border-left: none;
    border-bottom: 0;
    border-right: 0;
}
/* COMMUNAUTE */

td#leftcolumn div.blockContent ul,
td#rightcolumn div.blockContent ul {
    padding: 10px;
}
/* COMMUNAUTE */

td#leftcolumn div.blockContent ul li,
td#rightcolumn div.blockContent ul li {
    list-style-type: none;
    line-height: 30px;
}
td#leftcolumn div.blockContent ul li a,
td#rightcolumn div.blockContent ul li a {
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
}
/* NOUVEAU MEMBRES */

td#rightcolumn div.blockContent table tr td img {
    display: none;
}
td#rightcolumn div.blockContent table tr td {
    text-align: left;
    line-height: 40px;
}
td#rightcolumn div.blockContent table tr td br {
    display: none;
}
/* CONTENU CENTRE */

td#centerCcolumn fieldset {
    border: 0;
    background: white;
    padding: 30px;
    padding-top: 20px;
    margin-bottom: 20px;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
}
td#centerCcolumn fieldset legend.blockTitle,
th[align="left"] .comTitle {
    font-weight: 400;
    font-family: 'Poppins', sans-serif;
    font-size: 20px;
    color: white !important;
    background-color: #EDA261;
    padding: 5px 25px;
}
td#centerCcolumn fieldset .blockContent {
    border: 0;
}
td#centerCcolumn fieldset .blockContent table {
    background: none;
    border-spacing: 0px;
    padding-bottom: 10px;
}
td#centerCcolumn fieldset .blockContent table tr td {
    font-family: 'Poppins', sans-serif;
    font-weight: 300;
    font-size: 14px;
}
td#centerCcolumn fieldset .blockContent table .itemHead {
    border: 0;
    background: none;
    font-family: 'Poppins', sans-serif;
}
td#centerCcolumn fieldset .blockContent table .itemHead h1 {
    font-weight: 400;
    font-family: 'Poppins', sans-serif;
    font-size: 14px;
}
td#centerCcolumn fieldset .blockContent table .even,
td#centerCcolumn fieldset .blockContent table .even .itemPoster {
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    background: none;
    font-size: 13px;
}
td#centerCcolumn fieldset .blockContent table img.thumb {
    border: 0;
    margin-left: 0px;
}
td#centerCcolumn fieldset .blockContent center {
    background: #F6F4FF;
    color: #330000;
    margin: -12px;
}
td#centerCcolumn fieldset .blockContent ul,
td#centerCcolumn fieldset .blockContent br[clear="all"] {
    display: none;
}
tr.even td {
    background-color: #F6F4FF;
}
tr.odd td {
    background-color: #DED9FF;
}
td#centerCcolumn fieldset .blockContent center h2 {
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    line-height: 40px;
}
td#centerCcolumn fieldset .blockContent table.outer {
    border: 0;
}
td#centerCcolumn fieldset .blockContent table.outer tr {
    line-height: 20px;
}
td#centerCcolumn fieldset .blockContent table.outer tr td img {
    vertical-align: middle;
    width: 0px;
}
td#centerCcolumn fieldset .blockContent table.outer tr td a {
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 14px;
}
td#centercolumn th {
    font-family: 'Poppins', sans-serif;
    font-weight: 300;
    font-size: 12px;
    background-color: white;
    color: #333;
    vertical-align: middle;
    border-bottom: solid 1px #333;
}
/**page**/
#content div div[style="float: right; margin: 0px;"],
#content > div > div[style="padding: 5px;float: right; text-align:right;"],
#content > div[style="float: right; text-align:right;"] {
    font-size: 10px;
    color: #F0EFF7;
    padding: 5px;
}
#content div div[style="float: right; margin: 0px;"] a,
#content div div[style="float: right; margin: 0px;"] b,
#content > div > div[style="padding: 5px;float: right; text-align:right;"] > a,
#content > div > div[style="padding: 5px;float: right; text-align:right;"] b,
#content > div[style="float: right; text-align:right;"] > a,
#content > div[style="float: right; text-align:right;"] b{
    padding: 10px 12px;
    line-height: 3.5;
    text-decoration: none;
    background-color: #fff;
    border: 1px solid #ddd;
    border-top-color: rgb(221, 221, 221);
    border-right-color: rgb(221, 221, 221);
    border-bottom-color: rgb(221, 221, 221);
    border-left-color: rgb(221, 221, 221);
    margin-left: -5px;
    color: #524F81;
    font-size: 12px;
    transition-duration: 0.3s;
    font-weight: initial;
}
#content div div[style="float: right; margin: 0px;"] a u,
#content > div > div[style="padding: 5px;float: right; text-align:right;"] > a u,
#content > div[style="float: right; text-align:right;"] > a u {
    text-decoration: none;
}
#content div div[style="float: right; margin: 0px;"] a:hover,
#content > div > div[style="padding: 5px;float: right; text-align:right;"] > a:hover,
#content > div[style="float: right; text-align:right;"] > a:hover {
    background-color: #e4e7ea;
    font-weight: initial !important;
    transition-duration: 0.3s;
}
#content div div[style="float: right; margin: 0px;"] b,
#content > div > div[style="padding: 5px;float: right; text-align:right;"] b,
#content > div[style="float: right; text-align:right;"] b {
    background-color: #EDA261;
    color: #fff;
}
#content div div[style="float: right; margin: 0px;"] b::first-letter{
    font-size:0px;
}
/** comments **/

.outer .odd .comUserRank,
.outer .odd .comUserStat,
.outer .odd .comUserStatus,
.outer .odd .comTitle,
.copyright {
    display: none !important;
}
.outer .odd {
    background-color: transparent;
}
.outer .odd .comUserImg {
    border-radius: 90px;
    height: 40px;
    width: 40px;
}
/* MENU LEFT */

#leftcolumn {
    margin-left: -256px;
    transition-duration: 0.3s;
    position: absolute;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    font-size: 13px;
    z-index: 2000;
}
#leftcolumn > div {
    opacity: 1;
    transition-duration: 0.3s;
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
    line-height: 120%;
    font-size: 13px;
    padding: 10px !important;
}
#leftcolumn > div > form {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
}
#leftcolumn:hover {
    margin-left: 0px;
    transition-duration: 0.3s;
}
#leftcolumn:hover > div {
    opacity: 1;
    transition-duration: 0.3s;
}
#leftcolumn > div.blockTitle {
    position: absolute;
    transform: rotate(90deg);
    width: 180px;
    margin-left: 175px;
    margin-top: 75px !important;
}
#leftcolumn > div:nth-child(7),
#leftcolumn > div:nth-child(8),
#leftcolumn > div:nth-child(9),
#leftcolumn > div:nth-child(10) {
    display: none !important
}
#leftcolumn > div.blockContent {
    padding: 20px !important;
    margin-bottom: 2px;
}
/* Center column */

td#centercolumn {
    justify-content: space-between;
    display: flex;
    flex-direction: column;
    align-items: center;
    min-width: 580px;
    width: 80vw;
    max-width: 1500px;
    margin: auto;
}

/******
search
***/

#centercolumn div form[action="/search.php"] {
    position: absolute;
    right: 30px;
    top: 15px;
}
#rightcolumn,
div[style="width:728px; height:90px;"] {
    display: none;
}
#content {
    position: relative;
}
/** TOUS LES ARTICLES OU 1 AVEC COMMENTAIRE **/

#centercolumn #content {
    padding: 0;
}
#centercolumn div form[action="article.php"],
#centercolumn div form[action="article.php"] table {
    width: auto;
    display: flex;
    justify-content: flex-end;
    min-width: 1256px;
    margin-bottom: -40px;
    background: transparent !important
}
#centercolumn div form[action="article.php"] .even {
    bottom: -30px !important;
}
#centercolumn .item {
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    border: 0;
    background: #fff;
    margin: 0;
    padding: 15px;
    padding-left: 230px;
    border-top: 4px solid #524F81;
    border-radius: 3px;
    position: relative;
    min-height: 130px;
    min-width: 1080px;
    max-width: 1080px;
}
@media screen and (max-width: 1500px) {
    #centercolumn .item {
        min-width: 580px !important;
        max-width: 580px !important;
        margin: 0 30px;
    }
    #centercolumn .itemFoot {
        min-width: 796px !important;
        max-width: 796px !important;
        margin: 0 30px;
    }
}
#centercolumn .item .itemInfo {
    text-align: left;
    padding-bottom: 15px;
    margin-left: 30px;
}
#centercolumn .item .itemInfo .itemPoster,
#centercolumn .item .itemInfo .itemPoster a,
#centercolumn .item .itemInfo .itemPostDate,
#centercolumn .item .itemInfo .itemPostDate a {
    background: none;
    font-family: 'Poppins', sans-serif;
    font-size: 11px;
}
#centercolumn .itemFoot {
    border: 0;
    background: rgba(174, 160, 212, .1);
    border-radius: 4px;
    padding: 5px 15px;
    min-width: 1295px;
    max-width: 1295px;
    margin-bottom: 10px;
}
#centercolumn .itemFoot .itemPermaLink {
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    color: #524F81;
    font-size: 0px;
}
#centercolumn .itemFoot .itemPermaLink a {
    font-family: 'Poppins', sans-serif;
    font-weight: 500;
    color: #524F81;
    font-size: 12px;
}
#centercolumn .itemFoot .itemPermaLink a:first-child {
    border-right: solid 1px #524F81;
    margin-right: 10px;
    padding-right: 10px;
}
#centercolumn .item p {
    display: none;
}
#centercolumn .item br {
    display: none;
}
#centercolumn .item p.itemText {
    display: block;
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 15px;
}
#centercolumn .item p.itemText a:first-child img {
    border: 0px solid #fff;
    position: absolute;
    top: 0px;
    left: -10px;
    height: 100%;
    max-height: 170px;
}
#centercolumn .item p.itemText a:not(:first-child) img {
    text-align: center;
    width: 75%;
    margin: 20px;
    display: block
}
#centercolumn .item p.itemText span {
    margin-left: -20px;
}
#centercolumn .item p.itemText span:before {
    content: "";
    display: inline-block;
    vertical-align: middle;
    margin-right: 5px;
    width: 0;
    height: 0;
    border-top: 5px solid transparent;
    border-right: 5px solid transparent;
    border-bottom: 5px solid transparent;
    border-left: 5px solid #3F51B5;
}
#centercolumn .item .itemHead {
    font-family: 'Poppins', sans-serif;
    border: 0;
    background: transparent;
}
#centercolumn .item lu li,
#centercolumn .item lu li a {
    list-style-type: none;
    font-size: 12px;
    font-weight: 300
}
#centercolumn .item lu li:before {
    content: '\f105';
    font-family: 'Font Awesome\ 5 Free';
    font-weight: 900;
    padding-right: 5px;
    font-size: 10px;
}
#centercolumn .item .itemHead h1.itemTitle a {
    font-family: 'Poppins', sans-serif;
    font-weight: 500;
    font-size: 17px;
}
#centercolumn .item .itemHead h1.itemTitle a:first-child {
    background: #EDA261;
    padding: 2px 5px;
    color: #fff;
    border-radius: 3px;
}
#centercolumn a[href="https://www.koreus.com/modules/news/images0.html"] {
    background: pink !important;
}
#centercolumn .item .itemInfo {
    background: transparent;
}
}

@-moz-document url-prefix("https://www.koreus.com/modules/newbb/") {
/**FORUM**/

.comText {
    word-wrap: break-word;
    word-break: break-all;
}
}

@-moz-document url-prefix("https://www.koreus.com/video/") {
/** VUE D'UNE VIDEO**/

.container {
    border: none !important;
}
.container h1.h2 {
    text-align: left;
}
.container h1.h2 span {
    font-weight: 300;
    font-family: 'Poppins', sans-serif;
}
.container .btn-group .btn-default {
    background: #ccc;
    border-radius: 3px 0px 0px 3px;
}
.container .btn-group.btn-group-justified .btn-group button {
    border-radius: 0px;
}
.container p {
    font-family: 'Poppins', sans-serif;
    font-weight: 400;
    font-size: 15px;
}
.container p a.btn.btn-default.btn-sm {
    font-family: 'Poppins', sans-serif;
    background: #190033;
