}

@-moz-document url-prefix("https://www.koreus.com/modules/news/"), url-prefix("https://www.koreus.com/modules/newbb/") {
#content {
    display: block !important;
}
/**
 PUB
**/

#centercolumn div[style="max-width:1200px; margin-left: auto; margin-right: auto;"],
#centercolumn div[style="width:768px;  height:210px;margin-left: auto; margin-right: auto;"],
#centercolumn div[style="text-align: center; padding: 3px; \nmargin:3px;"] {
    display: none !important;
}
/** 1 ARTICLE **/

#centercolumn div table.outer tr {
    position: relative;
}
#centercolumn h2 {
    margin-bottom: -75px;
    color: #ff6849;
    margin-left: 25px;
}
#centercolumn div table.outer {
    border-spacing: 0px;
    padding: 20px;
    margin: 10px 0;
    border: none;
    background: #FFF !important
}
#centercolumn div table.outer tr td.odd {
    background-color: transparent;
    position: relative;
    width: 0 !important;
}
@media screen and (max-width: 1400px) {
    #centercolumn div table.outer tr td.odd .comText {
        min-width: 580px !important;
        max-width: 580px !important;
        margin: 0 30px;
    }
    #centercolumn div form[action="article.php"] table,
    #centercolumn div form[action="article.php"] {
        min-width: 810px !important;
        max-width: 810px !important;
    }
}
#centercolumn div table.outer tr td.odd:last-child > div {
    position: relative;
}
#centercolumn div table.outer td.odd:last-child > div:nth-child(2),
#content table.outer th[align="right"] .comTitle {
    position: absolute;
    top: 57px;
    left: -18px;
    z-index: 200;
}
#centercolumn div table.outer td.odd:last-child > div:nth-child(2) span[style="font-size:20px"],
#content table.outer th[align="right"] .comTitle span[style="font-size:20px"] {
    font-size: 9px !important;
    font-weight: 400;
    background: white;
    position: absolute;
    top: 20px;
    border: solid 1px #CDC8F0;
    padding: 2px 0 !important;
    left: 7px;
    text-align: center;
    width: 20px !important;
}
#centercolumn div table.outer .arrow-up,
#centercolumn div table.outer .arrow-down,
#centercolumn div table.outer .arrow-up2,
#centercolumn div table.outer .arrow-down2 {
    background: none;
    position: relative;
    width: 14px;
    height: 14px;
    color: grey;
}
#centercolumn div table.outer .arrow-up:before,
#centercolumn div table.outer .arrow-down:before,
#centercolumn div table.outer .arrow-up2:before,
#centercolumn div table.outer .arrow-down2:before {
    font-family: 'Font Awesome\ 5 Free';
    font-size: 13px;
    position: absolute;
    top: 0;
    left: 0
}
#centercolumn div table.outer .arrow-up:before,
#centercolumn div table.outer .arrow-up2:before {
    content: "\f164";
    font-weight: 400
}
#centercolumn div table.outer .arrow-down:before,
#centercolumn div table.outer .arrow-down2:before {
    content: "\f165";
    font-weight: 400
}
#centercolumn div table.outer .arrow-up:hover:before,
#centercolumn div table.outer .arrow-up2:before {
    font-weight: 900
}
#centercolumn div table.outer .arrow-down:hover:before,
#centercolumn div table.outer .arrow-down2:before {
    font-weight: 900
}
#centercolumn div table.outer .arrow-up2:before {
    color: #524f81
}
#centercolumn div table.outer .arrow-down2:before {
    color: #EDA261
}
#centercolumn div table.outer tr td.even {
    position: absolute;
    bottom: 15px;
    right: 12px;
    background-color: transparent;
    padding: 0
}
#centercolumn div table.outer tr td.even a,
#content table.outer td.foot a[rel="nofollow"] {
    background-color: transparent;
}
#centercolumn div table.outer tr td.even a:first-child,
#content table.outer td.foot a[rel="nofollow"] {
    font-size: 0px;
    color: #ff6849
}
#centercolumn div table.outer tr td.even a:hover:first-child,
#content table.outer td.foot a[rel="nofollow"]:hover {
    color: red
}
#centercolumn div table.outer tr td.even a:before,
#content table.outer td.foot a[rel="nofollow"]:before {
    font-family: 'Font Awesome\ 5 Free';
    font-size: 14px;
    content: '\f024';
}
#centercolumn div table.outer tr td.even a:last-child:before{
    content: '\f3e5';
    font-weight: 900;
}
#centercolumn div table.outer tr td.even a img,
#content table.outer td.foot a[rel="nofollow"] img {
    display: none;
}
#centercolumn div table.outer tr td.odd:last-child {
    border-left: solid 1px #CDC8F0;
    border-top: solid 1px #CDC8F0;
}
#centercolumn div table.outer tr:nth-child(2) td.odd:last-child,
#centercolumn div table.outer tr:nth-child(3) td.odd:last-child {
    border-left: solid 1px #CDC8F0;
    border-top: none;
}
#centercolumn div table.outer .xoopsQuote {
    padding: 6px;
    font-style: italic;
    border: none;
    background-color: #F0EFF7;
    font-size: 11px;
    margin: 0px 20px;
}
#centercolumn div table.outer tr td.odd .comText {
