css:
@-moz-document url-prefix("https://github.com") {
.zhc-sidebar {
   display: none;
}

.zhc-pipeline:first-child {
   margin-left: 1rem !important;
}

.zhc-pipelines, .zhc-board__menu {
   width: 100vw;
}

.zhc-board__menu {
   margin-left: 0 !important;
}
.zhc-pipeline--add-placeholder {
  display: none;
}

.zhc-board .zhc-pipelines {
    padding-left: 0;
    margin-left: -10px;
}
}
