css:
@-moz-document domain("sportschau.de"), domain("tagesschau.de"), domain("wdr.de") {
@media screen and (max-width: 479px) and (min-width: 0px) {
    html,
    body {
        overflow-x: hidden;
    }
    .wrapper,
    .storywrapper {
	width: 100%;
        margin-right: 4px;
    }
    .section {
        width: calc(100% - 8px) !important;
    }
    .mod .box {
        width: calc(100% - 8px) !important;
    }
    .modParagraph .subtitle,
    p {
        clear: both;
    }
    .mod.modGallery .media {
        height: auto;
    }
    .inline {
        width: auto;
    }
    .modParagraph .infokasten .mediaCon,
    .inline .mod.modGallery .controls.sliding {
        width: 100%;
    }
}
}
