@-moz-document domain(mspfa.com)  {
    body {
        background-color: #222;
        color: #eee;
    }
    #main {
        background-color: #333;
    }
    #slide {
        background-color: #494949;
    }
    #slide .spoiler {
        border: none;
        background-color: #555;
        border-radius: 18px;
    }
    #dialog .major {
        background-color: #666;
    }
    .mspfalogo {
        background-color: #494949;
        border-right-color: #333;
    }
    center, center>a {
        background-color: #494949 !important;
        color: #aaa !important;
    }
    .story .smol {
        background-color: inherit;
        border: none;
    }
    #storyicon, #usericon, .cellicon, .story>img {
        background-color: #eee;
        border: 2px solid #333;
    }
    #info>span>.spoiler.open {
        background-color: #494949;
    }
    .page:visited {
        color: #AAA;
    }
    a {
        color: #1c9dec;
    }
    a:visited {
        color: #1a97e0;
    }
    table.container.alt, table.container.alt2 {
        border-color: #333;
        background-color: #494949;
    }
    #flashytitle {
        background-color: #333;
    }
    table.container > tbody > tr > td, table.container > tbody > tr > th {
        border-color: #333;
    }
    .container>tbody>tr>td, tbody#stories {
        background-color: #494949;
    }
    form#dialog {
        background-color: #494949;
        border: 1.5px solid #333;
    }
    input {
        background-color: #666;
        border: 2px solid #666;
        color: white;
    }
    input:active {
        background-color: #595959;
        border: 2px solid #595959;
        color: #9e9e9e;
    }
    .fav.major, .notify.major, .edit.major, select, textarea {
        background-color: #666;
        border: 1px solid #666;
        color: white;
    }
    .fav.major.unlit:active, .fav.major.lit:active, .notify.major.unlit:active, .notify.major.lit:active, input.edit.major:active {
        background-color: #555;
        border: 1px solid #555;
        color: #9b9b9b;
    }
    #editstory>table>tbody>tr>td>input, #showless, #showmore, #showall, #leaveeditors, #grantuserbtn, #feature, #newcomment>input, span>rate.notranslate>input, .container.alt>tbody>tr>td>input, #saveall, .container.alt.alt2>tbody>tr>th>input, .container.alt.alt2>tbody>tr>th>span>input, .rate.notranslate>input, #dialog>div>input, #markasread, #markasunread, #deletemsgs, #recipients>input, #recipients>span>input, #bccs>input, #saveprofile, #savesettings, #editsettings>table>tbody>tr>td>table>tbody>tr>td>.major, input.major {
        /*A lot of buttons :eyesbutfast:*/
        background-color: #666 !important;
        border: 2px solid #666 !important;
        text-shadow: none;
    }
    #editstory>table>tbody>tr>td>input:active, #showless:active, #showmore:active, #showall:active, #leaveeditors:active, #grantuserbtn:active, #feature:active, #newcomment>input:active, span>rate.notranslate>input:active, .container.alt>tbody>tr>td>input:active, #saveall:active, .container.alt.alt2>tbody>tr>th>input:active, .container.alt.alt2>tbody>tr>th>span>input:active, .rate.notranslate>input:active, #dialog>div>input:active, #markasread:active, #markasunread:active, #deletemsgs:active, #recipients>input:active, #recipients>span>input:active, #bccs>input:active, #saveprofile:active, #savesettings:active, #editsettings>table>tbody>tr>td>table>tbody>tr>td>.major:active, input.major:active {
        /*Pressed buttons (still a lot)*/
        background-color: #555 !important;
        border: 2px solid #555 !important;
        color: #9b9b9b;
    }
    #bbtoolbar>input {
        background-color: #666;
        border: 1px solid #666;
        color: #999;
    }
    td.major {
        color: #999;
    }
    #dialog>div.major {
        background-color: #333;
    }
    #messages>tr>td>.cellicon, .arrow.right.lit, .arrow.right, .arrow.left.lit, .arrow.left, #alist>.lit>td>.cellicon, #alist>.unlit>td>.cellicon, #userstories>a>.cellicon, #userachievements>a>.cellicon {
        background-color: transparent !important;
        border: none !important;
    }
    .arrow.right.lit:active, .arrow.right:active, .arrow.left.lit:active, .arrow.left:active {
        border: none !important;
    }
    #messages .spoiler {
        border: none;
        background-color: #555;
        border-radius: 18px;
    }
    span.major {
        text-shadow: none;
    }
    #content .spoiler.open span {
        transition: 0.5s;
    }
    #content .spoiler.open span:hover {
        background-color: #fff;
        border-radius: 2px;
    }
    /*--Scroll bar--*/
     body::-webkit-scrollbar {
        max-width: 17px;
    }
    body::-webkit-scrollbar-track {
        background-color: #333;
    }
    body::-webkit-scrollbar-thumb {
        background-color: #494949;
    }
    textarea::-webkit-scrollbar {
        max-width: 17px;
    }
    textarea::-webkit-scrollbar-track {
        background-color: #333;
    }
    textarea::-webkit-scrollbar-thumb {
        background-color: #494949;
    }
    textarea::-webkit-resizer {
        background-image: url("https://orig00.deviantart.net/fbe0/f/2018/093/c/b/drag_by_spaghettitron5000-dc7tjza.png");
        background-color: #494949;
        max-width: 14px;
        max-height: 14px;
    }
      /*--TWITTER NEWS--*/

    .timeline-Widget {
        background-color: #434343;
    }
    .timeline-Tweet {
        background-color: #494949;
    }
    .timeline-Tweet:hover {
        background-color: #444;
    }
    .js-openLink {
        background-color: #565656;
        border: 1px solid #333;
    }
    .js-openLink:hover {
        background-color: #494949;
        border: 1px solid #333;
    }
    .timeline-Header-title {
        color: #e9e9e9;
    }
    .timeline-LoadMore-prompt {
        background-color: #494949;
        border: 1px solid #333;
        color: #1c9dec;
    }
    .timeline-LoadMore-prompt:hover {
        background-color: #434343;
        border: 1px solid #333;
        color: #1a93dd;
    }
    .link.customisable {
        color: #1c9dec;
    }
    .timeline-Viewport::-webkit-scrollbar {
        max-width: 17px;
    }
    .timeline-Viewport::-webkit-scrollbar-track {
        background-color: #424242;
    }
    .timeline-Viewport::-webkit-scrollbar-thumb {
        background-color: #565656;
    }
}
@-moz-document url-prefix("https://mspfa.com/?s=16414") {
    /*--IF YOU COME ACROSS A FANVENTURE WITH MADCREATIVITY'S MSPA THEME, ADD THE ADVENTURE'S STARTING URL TO THE SPECIFICATIONS--*/

    #spcLatestPagesTopLine, #spcLatestPagesHolder, #spcLatestPagesMiddleBox, #spcAdsHolder, #spcAdsMiddleBox {
        background-color: #3e3e3e !important;
        color: #fff;
    }
    #spcNewsHolder {
        background-color: #494949 !important;
        color: #fff;
    }
    header nav, #spcMainHolder nav {
        background-color: #000;
    }
    .mspfalogo {
        background-color: #333 !important;
    }
    #spcMainHolder>nav>img {
        background-color: #494949 !important;
        border-top: 8px solid #494949;
    }
    #copyright {
        background-color: #494949 !important;
        color: #fff;
    }
}
