css:
@-moz-document url-prefix("https://mitpress.mit.edu/sites/default/files/sicp/full-text/book/") {
body {
    background-color: #0c141f;
    color: #6fc3df;
    font-family: terminus, serif, sans-serif;
}

h1, h2, h3, h4, h5, h6,
h1 a, h1 a:visited, h2 a, h2 a:visited,
h3 a, h3 a:visited, h6 a, h4 a:visited, 
h5 a, h5 a:visited, h4 a, h6 a:visited {
    color: #e6ffff;
    text-shadow: 0 0 3px #e6cccc;
}

a, a:visited {
    color: yellow;
}

.navigation {
    color: #df740c;
    font-style: normal;
}

em, i {
    color: #00ff00;
    text-shadow: 0 0 6px #00ff00;
}
strong, b {
    color: #cb15c9;
    text-shadow: 0 0 2px #cb15c9;
}
}
