/*Title/Subheadings Color*/
.blog-title a {
    color: #fff !important;
}
.main-menu a {
    color: #939393 !important;
}
.menucodenirvana {
    box-shadow: 1px 2px 9px #212121 !important;
}
.menucodenirvana {
    background: #212121 !important;
}
#site-title a, .custom-header #site-title {
    color: #fff !important;
    text-shadow: 2px 2px 0 #000, 5px 5px 0 rgb(0, 0, 0) !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #939393 !important;
}
.site-nav a {
    color: #939393 !important;
}
.Header h1 a {
    color: #fff !important;
}
.tabs-inner .widget li a {
    color: #939393 !important;
    border-top: 1px solid #212121 !important;
    border-bottom: 1px solid #212121 !important;
    border-left: 1px solid #212121 !important;
}
.tabs-inner .widget li:last-child a {
    border-right: 1px solid #212121 !important;
}
/*Background Color*/
body {
    background-color: #212121 !important;
}
.bg-white {
    background: #212121 !important;
}
body.custom-background {
    background-color: #212121 !important;
}
body * {
    background-color: #212121 !important;
    color: #939393 !important;
}
/*Content Background Color*/
.site {
    background: #212121 !important;
}
.post {
    background: #212121 !important;
}
.content-wrapper {
    background: #212121 !important;
    box-shadow: 0px 0px 15px #212121 !important;
}
/*Content Color*/
span {
    color: #939393 !important;
}
body {
    color: #939393 !important;
}
.post-title a {
    color: #939393 !important;
}
.post-body {
    color: #939393 !important;
}
html, body, div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, font, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td {
    background-color: #212121 !important;
}
.singular .entry-title {
    color: #939393 !important;
}
/*Comment Section Color Scheme*/
.comment-inner {
    background: #303030 !important;
}
.comment-header h4 a {
    color: #fff !important;
}
.comment-respond form label {
    color: #939393 !important;
}
.comment-respond input[type="text"], .comment-respond textarea {
    background: #303030 !important;
    color: #939393 !important;
}
.comment-respond input[type="submit"] {
    background: #303030 !important;
    color: #939393 !important;
}
#allHolder {
    background-color: #212121 !important;
    border: 1px solid #212121 !important;
    border-bottom: 1px solid #212121 !important;
}
}
