css:
@-moz-document regexp("^https?\\:\\/\\/.*") {
body,
html,
*[role=grid],
main,
contentinfo,
aside,
header {
    background-color: #000000 !important;
    background: #000000 !important;
    color: #ffffff !important;
}
body * {
    background-color: transparent !important;
    color: inherit !important;
    box-shadow: none !important;
}
body *[class] {
    box-shadow: none !important;
}
select {
    border-color: yellow !important;
    box-sizing: border-box;
}
a,
a[class],
*[class] a[class],
*[class] a[class]:focus {
    /* border-color: yellow !important; */
    box-sizing: border-box;
}
button,
button[class],
form[class] button[class]#search,
*[role="button"],
*[role="button"][class] {
    border-color: #FFFFFF!important;
    box-sizing: border-box;
}
input[type="search"]::placeholder,
input[type="text"]::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: #00FF00 !important;
    opacity: 1;
    /* Firefox */
}
input[type="search"]:-ms-input-placeholder,
input[type="text"]:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #00FF00 !important;
}
input[type="search"]::-ms-input-placeholder,
input[type="text"]::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #00FF00 !important;
}
input[type="search"],
input[type="text"] {
    border-color: #FFFFFF !important;
    box-sizing: border-box;
}
input[type="search"],
input[type="text"],
form[class] button[class]#search,
*[class ~="sticky"] {
    background-color: #000000 !important;
}
*[role="tab"] {
    border-top-color: yellow !important;
    border-right-color: yellow !important;
    border-bottom-color: yellow !important;
    border-left-color: yellow !important;
}
*[class] select:focus,
select:focus,
li[class] > a {
    outline-color: yellow !important;
}

@media screen and (min-width: 1px) {

    html body a,
    html body a[class],
    html body a:link,
    html body a:hover,
    html body a:visited,
    html body a div[class],
    html body a span[class],
    html body div[class] a[class],
    html body a > span[class],
    html body a:link p[class],
    html body div .c-uhfh > [class*="theme-"] {
        color: yellow !Important;
    }
}


abbr,
h1,
h1[class],
div[class] h1,
h2,
h2[class],
div[class] h2,
h3,
h3[class],
div[class] h3,
h4,
h4[class],
div[class] h4,
h5,
h5[class],
div[class] h5,
h6,
h6[class],
div[class] h6,
h1 span,
h1[class] span,
h2 span,
h2[class] span,
h3 span,
h3[class] span,
h4 span,
h4[class] span,
h5 span,
h5[class] span,
h6 span,
h6[class] span,
header,
li
main,
aside,
b,
dfn,
i,
em,
mark,
nav,
section,
section[class],
nav,
nav[class],
footer,
footer[class],
ol,
p,
p[class],
.pmg-cl-black,
.pmg-cl-000000,
pre,
body *[class] p[class],
label,
label[class],
q,
select,
select[class],
strong,
sub,
sup,
time,
ul{
    background-color: #000000 !important;
    background-image: none !important;
    color: #ffffff !Important;
    border-color: #ffffff !important;
}
div,
div[class] div[class],
div[class] {
    background-color: transparent !important;
    color: #ffffff !Important;
}
body *,
button,
button[class],
li,
header,
aside,
contentinfo,
div:first-child,
.c-uhfh > div:first-child,
.c-uhfh > div:second-child,
.c-uhfh,
.c-uhfh > [class*="theme-"] {
    background: #000000 !important;
    background-color: #000000 !important;
}
section[class*="pmg-"],
section[class*="ms-"] {
    background-color: transparent !important;
    color: inherit !Important;
}
a,
a[class],
a:link,
a:hover,
a:visited,
a div[class],
a span[class],
div[class] a[class],
a > span[class],
a:link p[class],
div .c-uhfh > [class*="theme-"] {
    color: yellow !important;
    background-color: #000000 !important;
}
button,
button[class],
form[class] button[class]#search {
    color: #FFFFFF !important;
    border-color: #FFFFFF !important;
    background-color: #000000 !important;
}
a:visited,
a[class]:visited,
div[class] a[class]:visited {
    color: lime !important;
    /* border-color: lime !important; */
}
a:hover {
    text-decoration: underline !important;
}
body a > span,
body a div,
body a > div > p,
body a.c-call-to-action.c-glyph::after,
body a p,
body a:link p,
body a:visited p,
body a:active p,
body a:hover p,
body a h1,
body a:link h1,
body a:visited h1,
body a:active h1,
body a:hover h1,
body a h2,
body a:link h2,
body a:visited h2,
body a:active h2,
body a:hover h2 body a h3,
body a:link h3,
body a:visited h3,
body a:active h3,
body a:hover h3,
body a h4,
body a:link h4,
body a:visited h4,
body a:active h4,
body a:hover h4,
body a h5,
body a:link h5,
body a:visited h5,
body a:active h5,
body a:hover h5,
body a h6,
body a:link h6,
body a:visited h6,
body a:active h6,
body a:hover h6 {
    color: yellow !important;
}
.c-uhf-menu.js-nav-menu > ul {
    background-color: #000000 !Important;
}
table {
    border-collapse: collapse;
}
th,
td {
    border-color: #ffffff !important;
    border-style: solid !important;
}
div[class] {
    border-color: #ffffff !important;
}
div[role="alert"],
div[role="dialog"] {
    background-color: #000000 !Important;
    border-color: #ffffff !important;
    border-style: solid !important;
}
*[class*="glyph"]::before {
    color: #aaaaaa !Important;
}
*[class*="glyph"]::after {
    color: #ffffff !Important;
}
/* specific site styles */
/* getmwf */
/*
    a.c-call-to-action.f-lightweight,
    button.c-call-to-action.f-lightweight,
    .c-feature .c-call-to-action:not(.f-primary):not(.f-secondary),
    .m-feature .c-call-to-action:not(.f-primary):not(.f-secondary),
    .c-content-placement .c-call-to-action,
    .m-content-placement-item .c-call-to-action {
        color: #0066cc !important;
    }
    */
.m-histogram > ul a > div > div,
.c-histogram > ul a > div > div {
    background-color: highlight !Important;
}
.c-uhfh .theme-light .c-uhf-menu > button + ul,
.c-uhfh div[class*="brand-"] .c-uhf-menu > button + ul {
    border-color: yellow !Important;
}
.m-code-example > pre {
    border-top: 1px solid rgba(255, 255, 255, 1);
    border-left: 1px solid rgba(255, 255, 255, 1);
    border-bottom: 1px solid rgba(255, 255, 255, 1);
}
.context-do h3::after,
.context-dont h3::after {
    background-color: transparent !important;
}
.c-uhfh > .f-transparent.theme-dark .c-uhf-menu > ul > li > ul,
.m-hero-item .c-paragraph,
.c-hero .c-paragraph {
    background-color: #000000 !important;
}
/* sharepoint */
.ms-cui-ribbon a img,
.ms-cui-menu a img,
.ms-cui-QAT a img,
.ms-cui-toolbar-toolbar a img,
.ms-srch-sb-searchImg,
#GlobalBreadCrumbNavPopout-anchor img,
.ms-promotedActionButton-icon,
.s4-clust,
#SearchBox .ms-srch-sb img,
.card-icon/*#infopediaApp .card-container .card .card-items-container .card-items-ul > li .card-item-icon */
{
    filter: brightness(2) contrast(1.5) !Important;
}
.o365cs-newALV3-on .o365cs-nav-appTile:not(.o365cs-nav-appTileDisabled):not(.o365cs-nav-appTileHovered):not(:focus):not(.o365cs-nav-appTileMenuShown) .o365cs-nav-appTileTitle {
    color: yellow !Important;
}
.c-checkbox input[type=checkbox] + span:before {
    /* in high contrast mode there are no box shadows */
    box-shadow: none !Important;
}
#SearchBox .ms-srch-sb {
    border-color: yellow !important;
    border-style: solid !important;
}
.o365cs-nav-header16 .o365cs-nav-button,
#infopediaApp #search-box select,
#infopediaApp #search-box input[type="search"] {
    border-width: .08vw;
}
#infopediaApp .learning-menu ul.menu-level1-ul li.menu-level1-li .menu-level2 ul.menu-level2-ul,
#infopediaApp .learning-menu ul.menu-level1-ul .menu-more-container .menu-level2 ul.menu-level2-ul,
#infopediaApp .learning-menu ul.menu-level1-ul li.menu-level1-li .menu-level2 ul.menu-level3-ul,
#infopediaApp .learning-menu ul.menu-level1-ul .menu-more-container .menu-level2 ul.menu-level3-ul {
    background-color: #000000 !Important;
    border-top-color: #ffffff !important;
    border-right-color: #ffffff !important;
    border-bottom-color: #ffffff !important;
    border-left-color: #ffffff !important;
}
}
