/* i really want this to be global */
body#respecDocument {
    max-width:960px;
    text-align:center;
    margin-left:auto;
    margin-right:auto;
}

body#respecDocument * {
    text-align:left;
}

body#respecDocument > section {
    margin-top: 60px;
}

body#respecDocument h3 {
    margin-top: 40px;
}

body#respecDocument section > table {
    width: 1200px;
    margin-left: -120px;
    font-size: 90%
}

body#respecDocument section>ul+b {
    margin-left: -120px;
}
