css:
@-moz-document domain("www.webnovel.com") {
/*------------> Banner <------------*/
.hom-banner {
    height: 0px;
    margin-bottom: 0px
}

/* Background */
button,
html,
input,
select,
textarea {
    background-color: #2A2A2A
}

/* Front Page */
.g_book .g_h4,
.g_book .g_h4 a,
.m-update ._tit {
    color: #E26A6A
}

/* All Novel Titles */
.g_h3 {
    color: #E26A6A
}

/* Reccomended Title (Big) */
.hom-rec-1st {
    background-color: #212121
}

/* Recommended Background(Big) */
.c_strong {
    color: #E26A6A
}

/* Latest Updates Title/Release */
.g_ells {
    color: #AAA
}

/* Recommended text (Big) */
a {
    color: #007399
}

/* "More" Link */
.hom-h .g_h1,
.g_h1 {
    color: #DAA520
}

/* Categories */
.hom-tags a {
    color: #AAA;
    background-color: #2C3E50
}

/* Suggested Tags */
.fs14 {
    color: #7098EF
}

/* Spirit Stone # */
.ell,
.hom-ann .g_h3,
.hom-ann .g_h3 a {
    color: #AAA
}

/* Announcement Text/Title */
.ser-ret h3 strong {
    color: #E26A6A
}

/* Search */
.hom-vote a {
    color: #000
}
.hom-bd::before {
    background: #2A2A2A
}
.g_mod_wrap {
    background: rgba(0, 0, 0, 0.56)
}
.g_drop_sel_dark,
.g_drop_bd a {
    background: #212531;
    color: #AAA
}
.m-update-nav._slide a:hover {
    color: #CCC
}

/* Translation / Original */
.m-update-nav ._on {
    color: #AAA
}

/* ^ */
.g_drop_hd {
    color: #AAA
}

/* Browse Dropdown */
.g_hd_link:hover,
.g_drop_hd:hover {
    color: #B6B6B6
}
.hom-tags a:hover {
    background: #243342
}

/* Suggested Tags */
.m-update-pane td {
    border-color: #555
}

/* Header, Footer */
.g_footer,
.g_footer a {
    background-color: #212531;
    color: #AAA
}
.g_header {
    background-color: #212531;
    border-bottom: #515151
}
.g_hd_link,
.g_drop_sel {
    color: #AAA
}

/* Header Links */
.g_lang p {
    background: #212531
}

/* Power Ranking */
.banner {
    background-image: none;
    background-color: #2A2A2A
}
.banner-title {
    color: #DAA520
}
.top20-item {
    background: #333333
}

/* Ranking Background */
.top20-item ._rank_num {
    color: #AAA
}

/* Ranking # */
.top3-item:first-child {
    background: #333333
}

/* Top 3 */
.top3-wrap li {
    background: #333333
}

/* ^ */
.top20-item .text h4 {
    color: #E26A6A
}

/* Novel Title */
.bt {
    color: #AAA;
    background: #2C3E50;
    border: none
}
.bt_ghost {
    color: #AAA;
    background: none;
    border: 1px solid #2C3E50
}
.top3-item ._power_stone strong,
.top20-item .text ._power_stone strong {
    color: #7098EF
}
.date-tab .date {
    background: #2C3E50
}

/* Weekly */
.book-type-nav._slide a {
    color: #AAA
}
.book-type-nav._slide a:hover {
    color: #CCC
}

/* Browse */
.c_strong {
    color: #AAA
}

/* Novel Synop' */
.g_tag,
.g_tags a {
    color: #008000
}

/* Genre */
.g_sub_hd {
    background-color: #2A2A2A
}
.g_drop_bd,
.g_drop_bd a {
    background-color: #2A2A2A;
    color: #AAA
}

/* Dropdown */
.g_sub_hd h2 {
    color: #DAA520
}
.g_sub_hd ._tab a:hover {
    color: #CCC
}
.g_sub_hd ._tab ._on {
    color: #AAA
}
.a._on {
    color: #DAA520
}
.g_sub_hd ._tab a {
    color: #666
}
.g_book_item h3 {
    color: #E26A6A
}
.g_sub_hd .m-intro {
    color: #AAA
}
.ser-form input {
    color: #AAA;
    border-color: #555
}
.g_drop_bd::after {
    background: #2A2A2A
}

/* Novel Page */
.det-hd {
    background-color: #2A2A2A;
    border-color: #555
}
.det-info h2 {
    color: #E26A6A
}
.g_msg_bd {
    color: #AAA
}

/* Novel Reviews */
.g_msg_bd ._readmore {
    background: transparent linear-gradient(to right, #2A2A2A, #333333 1em) repeat scroll 0% 0%
}

/* View More */
.power-vote-num strong,
.power-vote,
.power-vote-hd {
    color: #7098EF;
    border-color: #555
}

/* Spirit Stone # */
.g_h2 {
    color: #DAA520
}

/* Headings */
.rev-score-list strong {
    color: #AAA
}

/* Rating Text */
.g_star_num small {
    color: #DAA520
}

/* Rating Score */
.g_msg_hd h3 span,
.g_msg_hd h4 span,
.g_msg_hd h3 {
    color: #008000
}

/* Username */
a.ui-page:hover {
    color: #CCC
}

/* Next Page */
a.ui-page {
    color: #AAA
}
.ui-page + .ui-page {
    color: #AAA
}

/* ^ */
.g_msg_spo {
    background-color: #333333
}

/* Spoiler */
.g_mod_chat_hd {
    background: #2A2A2A
}

/* Replies */
.g_mod_chat_bd {
    background: #2A2A2A
}

/* Replies */
.g_mod_hd {
    background: #2A2A2A;
    color: #DAA520
}
.g_msg_quote a,
.g_msg_quote._block ._bd a {
    color: #008000
}

/* Quote Username */
.g_mod {
    background: #2A2A2A
}
.det-info ._link_row a {
    color: #AAA
}

/* Read On Device */
.mt20 {
    color: #AAA
}
.det-tab-nav a._on span {
    color: #AAA
}

/* About */
.ui-select-button {
    background-color: #333333
}
.ui-select-datalist-li,
.ui-select-datalist > .selected {
    background-color: #333333;
    color: #AAA
}
.det-tab-nav a span:hover {
    color: #CCC
}
.det-tab-nav a span {
    color: #888
}
.det-con-ol i {
    color: #6791A2
}

/* TOC # */
a.ell.lst-chapter.dib.vam {
    color: #DAA520
}

/* Latest chapter */
.g_msg_spo._retract:hover {
    background: #444
}
.rev-score ._hd {
    border-color: #555
}
.rev-score {
    border-color: #555
}
.rank-tag {
    background-color: rgba(249, 90, 53, 0.48)
}

/* Reading */
.cha-page-in {
    background: #2A2A2A;
    color: #DAA520
}

/* Background, Chapter */
.cha-content p {
    color: #AAA
}

/* Text */
.cha-content {
    color: #008000
}
.bt_ghost_white {
    color: #333333;
    background: #333333;
    border: 1px solid #555
}

/* Comments */
.bt-cha-link {
    color: #3B6D9F
}
.cha-hd-progress {
    color: #AAA
}

/* Progress */
.cha-page {
    border: 1px solid #515151;
    background: #282828
}
.cha-hr {
    color: #008000
}
.cha-page-ft {
    background: #2A2A2A
}
.cha-lst h4 {
    color: #E26A6A
}
.ui-select-button {
    color: #AAA;
    border: 2px solid #2C3E50
}
.cha-modal {
    border-color: #515151
}
.cha-content._lock::after {
    background: rgba(6, 6, 6, 0) linear-gradient(to bottom, rgba(65, 65, 65, 0.74), #2A2A2A) repeat scroll 0% 0%;
    height: 1em
}
.cha-hd-mn-text a {
    color: #AAA
}

/* Novel Title */
.g_chat_reply textarea {
    color: #AAA
}

/* Comment/Reply */
.g_chat_reply {
    border-color: #515151
}
.g_mod .g_msg_quote {
    border-color: #555
}
.tag-link {
    background: #333
}

/* Tags */
.g_int_block {
    color: #AAA
}

/* ^ */
.cha-lst h3 {
    color: #AAA
}
.cha-info h2 {
    color: #E26A6A
}

/* Vote */
.banner p {
    color: #AAA
}
.top1 {
    background: #333333
}
.queue li {
    background: #333333
}
.queue a {
    color: #E26A6A
}
.ml5 {
    color: #AAA
}
.tac {
    color: #7098EF
}
.queue .bt {
    color: #AAA;
    background: #2C3E50
}
.ui-select-datalist-li:hover {
    background: #444
}

/* Comments dropdown */
.ui-select-datalist {
    border-color: #666
}

/* ^ */
.ui-select-button:hover {
    color: #AAA;
    border-color: #666;
    background: #333
}

/* ^ */
 :not(:disabled) + .ui-select > .ui-select-button:active {
    background: #444
}

/* ^ */
.m-thou {
    background: #333
}

/* End Page Thoughts */
.g_msg + .g_msg {
    border-color: #515151
}

/* RSS */
.g_thumb_books h3,
.g_thumb_books h4 {
    color: #E26A6A
}
.m-h2 {
    color: #DAA520
}
.m-stuff h2 {
    color: #E26A6A
}
.sub-hd {
    background-color: #2A2A2A;
    border-bottom: 1px solid #515151
}

/* Login */

/* Profile */
.g_sub_hd {
    background: none;
    border-color: #555
}

/* Inbox */
.m-main-ft {
    background: #2A2A2A
}
.m-main {
    background: #2A2A2A
}
.f_sans {
    color: #AAA
}
.m-noti ._bd {
    color: #AAA
}

/* History */
.his-list li:hover {
    background: #333
}
.his-list li:before {
    background: #333
}

/* Original Stories */
.filter-wrap a {
    color: #AAA
}
.filter-wrap a:hover {
    color: #CCC
}
.filter-wrap ._on {
    color: #CCC
}

/* User Dropdown Thing */
.g_drop_sel._hober:hover .g_drop_item,
.g_drop_sel:focus .g_drop_item {
    background: #2A2A2A
}

/* Spirit Stone Page */
.stone-intro {
    background: #333333
}
.bill-list td,
.bill-list ._up {
    color: #AAA
}
.g_tab_nav a._on {
    color: #AAA
}
.g_tab_nav a {
    color: #666
}
.g_tab_nav a:hover {
    color: #DAA520
}
.stone-intro ._stone strong {
    color: #7098EF
}
.m-noti ._extra {
    color: #7098EF
}
.strong-card ._num {
    color: #7098EF
}
.mr16,
.g_confirm_hd,
.g_mod_confirm .g_confirm_hd + .g_mod_bd {
    color: #AAA
}

/* Buying */
.g_charge_list input:checked + ._wrap {
    background: #343434
}

/* Settings */
.form-base {
    color: #AAA
}
.fc_sel select {
    color: #AAA
}

/* Countries */
.form-base ._label {
    color: #AAA
}
.fc_int {
    color: #AAA;
    background: #333;
    border-color: #515151
}
.fc_int_count i {
    color: #AAA;
    background: #333
}
.cropper-face {
    background-color: #333
}
.fc_sel {
    background: #333;
    color: #AAA;
    border-color: #515151
}
.mod-profile .g_int {
    color: #AAA
}

/* Edit Username*/

/* Dropdown */
.g_drop_sel_dark .g_drop_bd,
.g_drop_sel_dark .g_drop_bd a {
    background: #2A2A2A;
    color: #AAA
}
.g_drop_sel_dark .g_drop_bd a:hover {
    background: #333;
    color: #AAA
}
.g_drop_sel_dark .g_drop_link:hover {
    background: #2A2A2A
}
.g_drop_link:hover {
    background: #333
}

/* Invite Friends */
.footer_auto,
.footer_auto body {
    background: #2A2A2A
}
.inv-hd h2,
.inv-hd p {
    color: #AAA
}
.inv-form input {
    color: #AAA;
    background: #333
}
.inv-hd p strong {
    color: #7098EF
}

/* Logged In */
.m-h1,
.m-sign input,
.m-hd {
    color: #AAA
}
.g_gift_list li {
    background: #333
}

/* Gift */

/* Other */
.g_app_form ._bt {
    background: #2A2A2A
}
}

@-moz-document url-prefix("https://passport.webnovel.com") {
/* Login Thing */
.m-main {
    background: #2A2A2A
}
.m-hd-bg h1,
.m-hd-bg {
    color: #AAA
}

/* Email Login */
.m-form legend {
    color: #AAA
}
.m-main-ft {
    background: #2A2A2A
}

/* Sign Up Email */
.m-input ._int,
.form-info {
    color: #AAA
}
.m-input-psw .i-eye {
    background: #2A2A2A
}
.m-cell {
    background: #2A2A2A
}
}
