css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("streamcloud.eu")
{
body {
    background: #000000!important;
}
#page, .jwplayer {
    width: 100vw!important;
    height: 56vw!important;
}
div#page .content .padding {
    padding: 0 0 0 0!important;
}
div#page .content, div#page .header {
    background: black!important;
}
#vmenubar, #header, #footer, div#page .bottom, div#page .top, div#page .header {
    display: none!important;
}
#mediaplayer_controlbar {
    max-width: 90vw!important;
    width: 80vw!important;
}
#mediaplayer_controlbar .jwtimeSliderProgress {
    background: url('data:image/png;base64,') center center / 1px 30px repeat-x!important;
}
#mediaplayer_controlbar .jwtimeSliderBufferCapRight {
    background: url('data:image/png;base64,') center center / 5px 30px no-repeat!important;
}
#mediaplayer_controlbar .jwtimeSliderProgressCapLeft {
    background: url('data:image/png;base64,') center center / 8px 30px no-repeat!important;
}
#mediaplayer_controlbar .jwtext {
    color: rgb(255, 50, 50)!important;
}
#mediaplayer_controlbar .jwplay button {
    background: url('data:image/png;base64,') center center / 24px 30px no-repeat!important;
}
#mediaplayer_controlbar .jwmute button {
    background: url('data:image/png;base64,') center center / 26px 30px no-repeat!important;
}
#mediaplayer_controlbar .jwfullscreen button {
    background: url('data:image/png;base64,') center center / 22px 30px no-repeat!important;
}
#mediaplayer_controlbar .jwtimeSliderThumb {
    background: url('data:image/png;base64,') center center / 16px 30px no-repeat!important;
}
#mediaplayer_display_button_play {
    background: url('data:image/png;base64,')!important;
}
#mediaplayer_display_button_buffer {
    background: url('data:image/png;base64,')!important;
}
}
