css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
/* i really want this to be global */
/*header */

.navbar-fixed-top {
    background-color: #fff !important;
}
.navbar .nav li a {
    color: #2b2b2b !important;
}
.navbar .nav li a:hover {
    color: #12d6c4 !important;
}
.navbar .nav li.divider-vertical {
    background-color: #222 !important;
}
.navbar-fixed-top {
    border-bottom-color: #000 !important;
}
.form-search .btn {
    background-color: transparent !important;
    background-image: url("http://theoldreader.com/assets/ico-search.png") !important;
    background-position: center center !important;
}
.dropdown .open {
    background-color: #222 !important;
}
.dropdown-menu > li > a:hover {
    color: #333 !important;
}
/* text input (SEARCH) */

textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background-color: #444 !important;
    color: #fff !important;
    border-bottom-color: #000 !important;
    border-left-color: #000 !important;
    border-top-color: #000 !important;
    border-right-color: #000 !important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.075) inset !important;
}
/* end text input (SEARCH) */
/* end header */
/* BOTH COLS */

body {
    background-color: white !important;
    color: #202020 !important;
    font-size: 15px !important;
    line-height: 1.75em !important;
}
.well {
    background-color: white !important;
    border: 1px solid #e0e0e0 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
}
/*p {line-height:22px !important; margin-top: 5px !important; margin-bottom: 20px !important;}*/

.btn {
    color: #000 !important;
    text-shadow: none !important;
    background-color: #f9f9f9 !important;
    background-image: none !important;
    box-shadow: none !important;
}
/* end BOTH COLS */
/* left col*/
/*.row-fluid .span3 {width: 15% !important;}*/
/* making content col. wider part 1/2*/

.reader .subscribe-button-container .bg {
    background-color: #fff !important;
}
/*subscribe button*/

.reader .subscribe-fixed-top .bg {
    background: none repeat scroll 0 0 #333
    /*the same as BODY bg color*/
    
    !important;
}
.subscribe .btn-primary {
    border: 1px solid #ebebeb !important;
}
/*end subscribe button*/
/*nav list*/

.nav-list li a {
    color: #232323 !important;
}
.nav-list li a:hover {
    color: #12d6c4 !important;
}
.nav-list li.social a:hover {
    color: #12d6c4 !important;
}
.nav-list li.nav-header a {
    color: #232323 !important;
}
.nav-list li.nav-header a:hover {
    color: #12d6c4 !important;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
    background-color: #fff !important;
    color: #555;
}

/*nav list end*/
/* end left col*/
/* right col */
/*.reader .body-fixed-top {left: 14% !important; width: 83% !important;}*/
/* making content col wider part 2/2 */

.loader {
    background-color: #333 !important;
}
.page-header {
    border-bottom: 1px solid #222 !important;
}
/* line below site title or category (folder) */
/*articles*/

.reader .floating {
    background: #fff !important;
}
/*must be same as BODY background color*/
h3 {
    font-size: 16px !important;
}

.post>h3 {
    margin-bottom: 0px;
    font-size: 20px !important;
}

.post h3 a {
    color: #10b9aa !important;
}
.label.label-transparent {
    color: #999 !important;
}
.post.unread {
    border-left: 2px solid #12d6c4 !important;
    padding-bottom: 10px !important;
    padding-left: 11px !important;
    padding-right: 10px !important;
    padding-top: 10px !important;
}
.post.unread .content {
    color: #1c1c1c !important;
}
.post .content {
    color: #2b2b2b !important;
}
.post {
    padding-bottom: 10px !important;
    padding-left: 20px !important;
    padding-right: 10px !important;
    padding-top: 10px !important;
    margin-bottom: 10px !important;
}
.post blockquote {
    background-color: #222 !important;
}
img {
    margin-bottom: 15px !important;
    margin-top: 5px !important;
}

.label.label-feed {
    background-color: #f9f9f9 !important;
    border: none !important;
    color: #2b2b2b !important;
    padding: 4px 14px !important;
}

/* end articles*/
/* end right col*/
/* SPECIFIC */

.home-page dl dd p a {
    color: #ccc !important;
}
legend {
    color: #ddd !important;
}
.dropdown-menu {
    background-color: #252525 !important;
}
/* end SPECIFIC */
/* alerts and heroes */

.alert {
    background-color: #26687f !important;
}
.alert.alert-info {
    color: #fff !important;
}
.hero-unit {
    background-color: #222 !important;
}
/* end alerts and heroes */
/* sponsorship */

.sponsorship {
    background-color: #222 !important;
    border-color: #444 !important;
}
.sponsorship a {
    color: #ddd !important;
}
/* end sponsorship */

.dropdown-menu>li>a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: normal;
    line-height: 1.42857143;
    color: #333;
    white-space: nowrap;
    background-color: #fff;
}

.dropdown-menu {
    background-color: #ffffff !important;
}

.reader .floating {
    background: #fff;
    padding: 0 !important;
}

.hero-unit {
    background-color: #fff !important;
}

textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #fff !important;
    color: #b5b5b5 !important;
    border-color: #969696 !important;
}

.dropdown-menu>li>a:focus {
    text-decoration: none;
    color: #fff;
    background-color: #fff !important;
}
.post blockquote {
    background-color: #ececec !important;
    padding: 8px 12px !important;
    margin: 10px 0 !important;
}
