@-moz-document domain("moodle-exia.cesi.fr"), domain("moodle-examens.cesi.fr") {
/**
COLORS :

DARK GREY : #303030;
LIGHT GREY : #424242

TOP BAR RED : #D9001A
LIGHT RED : #ea3838

**/

/** Reset **/
h1,
h2,
h3,
h4,
h5,
h6,
p,
i,
span,
p > a,
div > a {
    color: #fff!important;
}
a {
    color: rgb(255, 68, 68);
    color: rgba(255, 68, 68, 1);
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    -o-transition: all 0.2s ease-in-out;
    text-decoration: none;
}
a:hover {
    color: #fff;
    transition: 0.2s;
}
a:focus {
    color: rgb(255, 68, 68);
    color: rgba(255, 68, 68, 1);
    transition: 0.2s;
}
select {
    background: #424242;
    color: #fff;
}
select[disabled] {
    background: #343434;
}
div {
    color: #fff!important;
}
table,
td,
th {
    background: #343434!important;
    color: #fff;
    border: 1px solid #303030;
}
nav > a {
    color: #fff!important;
}
.box {
    background: #424242;
    color: #fff;
    box-shadow: 0 0 5px 0 #202020;
}



/** Inputs **/
input[type=text],
input[type=password] {
    background: #383838!important;
    border: 1px solid #626262!important;
    color: #fff;
}
input[type=submit] {
    background: #ea3838!important;
    border: 1px solid #ea3838!important;
    margin: 0 5px !important;
    font-weight: bold!important;
}
input[type=submit]:hover {
    background: #ffffff !important;
    border: 1px solid #ea3838 !important;
    color: #ea3838!important;
    transition: 0.5s!important;
}
button {
    background: #ea3838!important;
    padding: 5px 10px;
}



/* Body global*/
.wrapper {
    background-color: #303030;
    min-height: calc(100vh - 118px)!important;
}
.main-nav {
    background-color: #424242!important;
}



/** Breadcrumbs **/
ul > li > span > a {
    font-weight: 700!important;
}
.box.generalbox {
    background: #424242!important;
    border-bottom: 2px solid #383838;
    box-shadow: none;
}
#page #region-main .coursebox {
    background: #424242!important;
    box-shadow: none!important;
    border-bottom: none;
}
#page #region-main .coursebox > .info > .coursename a {
    color: #fff;
    text-shadow: 0 0 5px 0 #202020;
}
#page #region-main .paging > a {
    color: #fff;
}
.box.generalbox.sitetopic {
    display: none;
}
#frontpage-course-list > div > div {
    border-left: #ea3838!important;
}
#page .breadcrumb-nav ul.breadcrumb li:first-child:before {
    color: #fff;
}



/** Texte prosit **/
#page #region-main .course-content .section.main {
    box-shadow: none;
}
.course-content ul li.section.main {
    border-bottom: none;
}
.course-content li {
    background: #424242!important;
}
#page #region-main .course-content .section.main.current {
    border-left: 3px solid #ea3838;
}
.course-content > ul > li {
    border-bottom: 2px solid #626262;
}
#page #region-main .course-content .section.main {
    border-bottom: 2px solid #626262;
}
#page #region-main h3.sectionname > span > a:hover {
    color: #ea3838;
}
.course-content .section-summary {
    border: none;
}



/** Title prosit **/
.title > h2 {
    border-top: 2px solid #ea3838!important;
    margin-top: -2px !important;
}



/* Dashboard */
#page #region-main .block-myoverview .tab-content {
    background: #424242;
    box-shadow: 0 0 5px 0 #202020;
    border: none;
}
.well.well-small {
    background: #343434!important;
    border: 1px solid #626262!important;
}
.nav-tabs > li > a,
.nav-tabs > li.active > a {
    background: #303030!important;
    color: #fff!important;
    border: none!important;
    border-top: 2px solid #ea3434!important;
    margin: 0 5px 0 0!important;
    transition: 0.25s!important;
}
.nav-tabs > li > a:hover,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:active .nav-tabs > li.active > a:focus {
    background: #303030!important;
    border: none!important;
    border-top: 2px solid #fff!important;
    color: #ea3434!important;
    transition: 0.25s!important;
}
.btn.btn-default {
    background: #303030!important;
    color: #fff!important;
    border: 1px solid #626262!important;
    transition: 0.2s!important;
}
.btn.btn-default:hover {
    background: #424242!important;
    transition: 0.2s!important;
}
.btn.btn-default.active {
    color: #ea3434!important;
}



/** Examens**/
#page #region-main .loginbox {
    background: #424242;
    color: #fff;
    box-shadow: none;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}
.loginform {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-content: center;
}
.loginbox .loginform .form-label {
    float: inherit;
    width: auto;
    text-align: center;
    white-space: inherit;
}
.loginbox .loginform .form-input {
    float: inherit;
    width: auto;
    text-align: center;
}
.loginbox .loginsub {
    display: flex;
    flex-direction: column;
}
.potentialidp > a {
    background: #894b21!important;
    border: 1px solid #894b21!important;
    margin: 0 5px !important;
    font-weight: bold!important;
    transition: 0.2s!important;
}
.potentialidp:hover > a {
    background: #ffffff !important;
    border: 1px solid #894b21 !important;
    color: #894b21!important;
    transition: 0.2s!important;
}



/** Containers quiz examens **/
.region-main-inner {
    background-color: #424242!important;
    border: 2px solid #626262!important;
}
.que .formulation {
    background-color: #343434!important;
    border-color: #303030;
    color: #fff;
}
.formulation .incorrect,
.formulation .correct {
    background-color: #343434!important;
}
.que .outcome {
    background-color: #445143;
    border-color: #303030;
    color: #fff;
}
.que .feedback {
    margin: 0px;
}
.que .outcome.clearfix {
    border-color: #285828;
}



/** Drapeau questions examens **/
.que .info {
    background: #343434;
    color: #ffffff;
    border: 1px solid #303030;
}
#mod_quiz_navblock {
    border: 2px solid #626262!important;
    background-color: #424242!important;
}
#mod_quiz_navblock_title {
    color: #FFFFFF;
}



/** Relecture table examens **/
#page #region-main table {
    box-shadow: 0 0 5px 0 #343434;
}
table.quizreviewsummary td.cell,
th.cell {
    background: #343434!important;
    color: #fff;
}
#page #region-main table td,
#page #region-main table th {
    border: 1px solid #303030;
}



/** Navigation de test examens **/
.mod_quiz-next-nav {
    background-color: #343434!important;
    color: #B94A48;
    border: 2px solid #626262!important;
    padding: 3px 10px;
    font-weight: 700;
    text-align: center;
    transition: 0.2s!important;
}
.mod_quiz-next-nav:hover {
    background-color: #602423!important;
    border: 2px solid #343434!important;
    color: #B94A48!important;
    font-weight: 700;
    transition: 0.2s!important;
}
.submitbtns {
    margin-bottom: 20px;
}



/** Menu **/
.jsenabled .usermenu .moodle-actionmenu.show .menu {
    background: #424242;
    border: 1px solid #626262;
}
.jsenabled .usermenu .moodle-actionmenu.show .menu > li:hover,
.jsenabled .usermenu .moodle-actionmenu.show .menu > li > a:hover {
    background: #202020;
}
.jsenabled .usermenu .moodle-actionmenu > .menu .filler {
    background-color: #626262;
    border-bottom: 1px solid #626262;
}



/** Footers **/
#page-footer {
    border: none;
    margin-top: 0px;
}
#page .block-region .block {
    background-color: #424242;
    border: 2px solid #626262!important;
}



/** Login page **/
.loginpanel {
    display: flex;
    flex-direction: column;
    align-items: center;
}
.loginpanel > div {
    display: flex;
    margin: 0 0 5px 0;
}
.loginpanel > div > a {
    background: #ea3434;
    color: #fff;
    font-weight: bold;
    border: 2px solid #ea3434;
    padding: 5px 8px;
    margin: 0 0 0 -45px;
    transition: all 0.3s;
    min-width: 120px;
    text-align: center;
}
.loginpanel > div > a:hover {
    background: #fff;
    color: #ea3434!important;
    border: 2px solid #fff;
    transition: all 0.3s;
}



/** Bouton retour top **/
#topcontrol {
    background: #ea3434;
    bottom: 10px !important;
    right: 10px !important;
}
#topcontrol:hover {
    background: #ea2222;
}



/** Hide useless course weeks **/
.section.main.clearfix.hidden {
    display: none !important;
}



/** FORUM **/
.forumpost {
    background: #333!important;
    box-shadow: none!important;
    border-bottom: 2px solid #626262!important;
}



/** PLAGIAT REPORT **/
.plagiarismreport {
    background-color: #626262!important;
    border: 1px solid #ea3434;
    padding: 3px;
    height: 26px;
}



/** EDITOR **/
.felement.feditor > * {
    background: #333!important;
}
.editor_atto_toolbar {
    background: #333!important;
}
.atto_group > button {
    background: #404040!important;
    color: #fff!important;
}
.editor_atto_content_wrap {
    background: #383838!important;
}



/** Uploader **/
.fp-restrictions {
    background: transparent;
}
.filemanager-toolbar,
.fp-pathbar,
.fp-pathbar > span > a,
.fp-pathbar > span > a:hover,
.filemanager-container,
.editor_atto_notification .atto_info {
    background: #333;
    color: #fff;
}
#fgroup_id_buttonar {
    background: transparent;
}
.fp-btn-add,
.fp-btn-add > a,
.fp-viewbar,
.fp-viewbar > a,
.fp-btn-download,
.fp-btn-download > a,
div.editor_atto_toolbar div.atto_group {
    background: #404040!important;
    color: #fff!important;
    border: none!important;
}
.fp-viewbar > a.checked,
.fp-btn-add:hover,
.fp-btn-add:hover > a,
.fp-viewbar:hover,
.fp-viewbar > a:hover,
.fp-btn-download:hover,
.fp-btn-download > a:hover,
div.editor_atto_toolbar div.atto_group > a:hover {
    background: #626262!important;
}
.ftoggler > a {
    color: #ea3434!important;
    font-weight: 400;
}



/** Slider home page **/
.flexslider > .slides > li:first-child > img,
.flexslider > .slides > li:nth-child(1) > img:after {
    content: url(https://i.postimg.cc/KYCWQWP0/slide_1.jpg);
}
.flexslider > .slides > li:nth-child(2) > img,
.flexslider > .slides > li:nth-child(2) > img:after {
    content: url(https://i.postimg.cc/gjWTb6BR/slide_2.gif);
}
.flexslider > .slides > li:nth-child(3) > img,
.flexslider > .slides > li:nth-child(3) > img:after {
    content: url(https://i.postimg.cc/FHRnvvGP/slide_3.gif);
}
.flexslider > .slides > li:nth-child(4) > img,
.flexslider > .slides > li:nth-child(4) > img:after {
    content: url(https://i.postimg.cc/zBWcQkp8/slide_4.jpg);
}
pre {
    background: #333;
}
}

@-moz-document url-prefix("https://moodle-exia.cesi.fr/pluginfile.php/") {
html,
body {
    font-family: arial, sans-serif;
    padding: 0;
    margin: 0;
    color: #F2F2F2;
    background-color: #303030;
    font-size: 15px;
    width: 100%;
    height: 100%
