}
.infos {
    box-shadow: none;
}
.infoTitre {
    background-color: #ea3838;
    box-shadow: none;
}
.auteur,
.editeur,
.dateCrea,
.domaine,
.public,
.info {
    border: 1px solid #ea3838;
    box-shadow: none;
}
.ba_txt_tb td,
.ba_txt_tb th {
    border: 1px solid #ea3838;
}
.ba_txt_tb .ba_txt_tbtr_head th {
    background: #ea3838;
}
.prerequis,
.obj,
.activite {
    border-top: 3px solid #ea3838;
}
.activite {
    border-top-color: #ea3838;
}
.prerequis_ti,
.obj_ti,
.activite_ti {
    box-shadow: none;
    background: #ea3838;
}
.dureeAppr,
.modSpat,
.modTemp,
.modCollab,
.typeActivite {
    box-shadow: none;
    border: 1px solid #ea3838;
}
.modeTitre {
    font-size: 0.7em;
    color: #ea3838;
}
.actAppr {
    border-left: 3px solid #ea3838;
}
.celActAppr li {
    color: #ea3838;
}
a:visited,
.TTlabelTypeAct:visited {
    border-color: #ea3838;
}
td {
    padding: 10px 0 0 0;
}
.case_ti {
    display: inherit;
}
section.part:nth-of-type(2n+1),
section.context > .part_ti,
nav.menu ul li a,
nav.menu ul,
#outCat,
#outSec,
article.obj.noCbk {
    background: #434343;
    color: #FFF;
}
#outSec,
.prerequis,
.obj,
.activite {
    border: none;
}
.outCat_sel_yes .outCat_i {
    border-top: 3px solid #ea3838;
}
#outSec ul li .sel_yes {
    border-bottom: 6px solid #ea3838;
}
.default #header #title h1 {
    line-height: 55px;
    margin: 0 0 5px 0;
    border: none;
}
.case_ti {
    border: none;
}
.case_ti > span > strong {
    background: transparent;
}
.illus {
    display: none;
}
#title {
    display: flex;
    justify-content: center;
}
}

@-moz-document domain("moodle-exia.cesi.fr") {
/** Top bar moodle-exia **/
#page-header .top-bar {
    background: #D9001A;
}



/** Logo **/
#logo,
#logo:after {
    content: url('data:image/png;base64,');
}




/** Navigation **/
.main-nav .nav li.open a {
    background: #ea3838;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    -o-transition: all 0.2s ease-in-out;
}
.main-nav .nav li .dropdown-menu a {
    border-bottom-color: #ea4444;
}
.pagination > ul > li > a {
    background: #303030!important;
    color: #fff!important;
    border: 1px solid #626262;
}
.pagination > ul > li > a:hover {
    background: #626262!important;
    color: #fff!important;
    border: 1px solid #626262;
}
.pagination > ul > .page-item.active > a {
    color: #ea3434!important;
}
abbr {
    text-decoration: none;
}
}

@-moz-document domain("moodle-examens.cesi.fr") {
#logo,
#logo:after {
    content: url('data:image/png;base64,');
}



/** Navigation **/
.main-nav .nav li.open a {
    background: #894b21;
    -webkit-transition: all 0.2s ease-in-out;
    -moz-transition: all 0.2s ease-in-out;
    -o-transition: all 0.2s ease-in-out;
}
.main-nav .nav li .dropdown-menu a {
    border-bottom-color: #894b21;
}
select {
    background: #424242;
    color: #fff;
}
select[disabled] {
    background: #343434;
}
.title > h2 {
    border-top: 2px solid #894b21!important;
    margin-top: -2px !important;
}
a {
    color: #d17436;
}



/** Inputs **/
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background: #383838!important;
    border: 1px solid #626262!important;
}
input[type=submit] {
    background: #894b21!important;
    border: 1px solid #894b21!important;
    margin: 0 5px !important;
    font-weight: bold!important;
    transition: 0.2s!important;
}
input[type=submit]:hover {
    background: #ffffff !important;
    border: 1px solid #894b21 !important;
    color: #894b21!important;
    transition: 0.2s!important;
}
button {
    background: #d17436!important;
    padding: 5px 10px;
}
.box {
    box-shadow: none;
    padding: 0;
}
#mod_quiz_ flight_form > * {
    background: #444;
    color: #fff;
}
#mod_quiz_preflight_form > fieldset > legend {
    background: #444 !important;
    color: #fff !important;
    z-index: 10;
}
.felement.fgroup {
    padding-bottom: 10px;
}
.moodle-dialogue-hd.yui3-widget-hd {
    background: #333 !important;
    color: #eee;
}
.moodle-dialogue-bd.yui3-widget-bd {
    background: #333 !important;
    color: #eee;
}
.fitem.fitem_actionbuttons.fitem_fsubmit {
    display: flex;
    justify-content: center;
    background-color: #424242!important;
}
.felement.fsubmit {
    margin: 0!important;
}
.fheader {
    color: #894b21!important;
}
}
