css:
@-moz-document url-prefix("https://geekyapar.com") {
/* Created by Nihat Emre Yuksel - 30.03.2018 */

body {
    color: #ababab;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #ababab;
}
a {
    color: #676767;
}
#main {
    background-color: #191919;
}
#logo {
    -webkit-filter: brightness(100);
    /* Safari 6.0 - 9.0 */
    
    filter: brightness(100);
}
.navbar-inverse .navbar-inner {
    background: #313131;
    border-color: #313131;
}
.navbar .nav {
    background: #313131;
}
.navbar .nav > li {
    border-color: #ababab;
}
.navbar .nav > li:hover,
.navbar .nav > li:focus {
    background: #191919;
}
.navbar .navbar-search {
    border-color: #ababab !important;
    background: #313131;
}
.navbar .nav li[class*='current'] {
    background: #191919;
}
.nav-mask.active {
    opacity: 0.6;
}
.scrollbar > .scrollbar-inner {
    background: rgba(171, 171, 171, 0.4);
}
.handheld-header {
    background: #313131;
}
.author-info {
    background-color: #313131;
}
.author-name a {
    color: #d0d0d0 !important;
}
.author-description {
    color: #d0d0d0;
}
blockquote strong {
    color: #fff;
}
.stream-list .reading-now .title a {
    color: #fff;
}
}
